css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ponycity.fr") {

body {
    padding-top: 130px;
    background: url('http://38.media.tumblr.com/tumblr_luqb7i868x1qzbl1no1_500.gif')#1F292F;
    min-height: 150% !important;
    color: #777;
    font-family: 'Open Sans',sans-serif;
}
  
#topbar-first {
    background-color: #263238;
    top: 0;
    z-index: 1030;
    color: #C7C7C7;
}
   
#topbar-first .dropdown.account > a, #topbar-first .dropdown.account.open > a, #topbar-first .dropdown.account > a:hover, #topbar-first .dropdown.account.open > a:hover {
    background-color: #263238;
}
  
#topbar-first .btn-group > a {
    background-color: #263238;
}
    
.label-success {
    background-color: #708fa0;
}
 
    
.tag_input_field {
    background-color: transparent;
}
    
textarea.form-control {
    height: auto;
    color: #ededed;
}
    
ul.tag_input {
    background-color: #455A64;
    border: 1px solid #455A64;
    }
    
.userInput, .spaceInput {
    background-color: #6A7177;
}
   
    
.md-editor > textarea {
    background: #6A7177 !important;
        background-color: rgb(106, 113, 119);
        background-image: none;
        background-repeat: repeat;
        background-attachment: scroll;
        background-position: 0% 0%;
        background-clip: border-box;
        background-origin: padding-box;
        background-size: auto auto;
    border-top: 0px dashed #009688;
    border-bottom: 0px dashed #009688;
}
    
.md-editor {
    border: 1px solid #455A64;
}
    
.md-editor .md-footer, .md-editor > .md-header {
    background: #455A64;
}
.md-header {
    background: #455A64;
}
    
.btn-primary {
    background: #6A7177;
}
    
#topbar-second {
    top: 50px;
    background-color: #37474F;
    z-index: 1029;
    background-image: none;
    box-shadow: 0 1px 10px rgba(0,0,0,0.1);
    border-bottom: 1px solid #3A3F4B;
    animation: 1s ease 1s normal none 1 running none;
    box-shadow: 0px 3px 8px rgba(0, 0, 0, 0.3);
}    
  
body a, body a:hover, body a:focus, body a:active, body a.active {
    color: #7DC6BF;
}
    
#topbar-second .nav > li > a#space-menu {
    padding-right: 13px;
    border-right: 1px solid #4C6F72;
}
    
    
#topbar-second .nav > li > a:hover, #topbar-second .nav .open > a, #topbar-second .nav > li.active {
    border-bottom: 3px solid #4C6F72;
    background-color: #0E837A;
    color: #555;
    transition: background-color 0.3s ease-in-out;
}
    

.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    font-size: 14px;
    list-style: none;
    background-color: #1F292F;
    border: 1px solid #ccc;
    border: 1px solid rgba(0,0,0,0.15);
    border-radius: 4px;
    -webkit-box-shadow: 0 6px 12px rgba(0,0,0,0.175);
    box-shadow: 0 6px 12px rgba(0,0,0,0.175);
    background-clip: padding-box;
    transition-property: background-color, padding-left;
    transition-duration: 0.5s, 0.3s;
    transition-timing-function: linear, ease-out;
}  
    
.form-control {
    border: 2px solid #455a64;
    box-shadow: none;
}
    
.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.428571429;
    color: #555;
    vertical-align: middle;
    background-color: #455a64;
    background-image: none;
    border: 1px solid #455a64;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
    -webkit-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}

.panel {
    border: none;
    background-color: #1F292F;
    -webkit-box-shadow: 0 0 3px #dadada;
    -moz-box-shadow: 0 0 3px #dadada;
    border-radius: 4px;
    position: relative;
    box-shadow: 0px 3px 8px rgba(0, 0, 0, 0.3);
}
    
.panel-profile .panel-profile-header {
    position: relative;
    border: 3px solid #1F292F;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
} 
    
.modal .dropdown-menu li:hover, .panel .dropdown-menu li:hover, .nav-tabs .dropdown-menu li:hover, .modal .dropdown-menu li.selected, .panel .dropdown-menu li.selected, .nav-tabs .dropdown-menu li.selected {
    border-left: 3px solid #0A625B;
    background-color: #6A7177 !important;
}
    
.well {
    border: none;
    box-shadow: none;
    background-color: #37474F;
    margin-bottom: 1px;
}
    
.nav-tabs {
    border-bottom: 1px solid #1F292F;
}
    
hr {
    margin-top: 20px;
    margin-bottom: 20px;
    border: 0;
    border-top: 0px solid #1F292F;
}
    
.media .media-body h4.media-heading a {
    color: #2CA9DA;
}
    
.media .content a {
    color: #009688;
}
    
.panel .panel-heading {
    font-size: 16px;
    font-weight: 300;
    color: #FFFFFF;
    background-color: #0E837A;
    border: none;
    padding: 10px;
    border-radius: 4px;
}
    
a.list-group-item:hover, a.list-group-item.active, a.list-group-item.active:hover, a.list-group-item.active:focus {
    color: #ADADAD;
    background-color: #1F292F;
    border-left: 3px solid #263238 !important;
    transition: border-left 0.2s ease-in-out;
}
 
a.list-group-item {
    color: #BDBDBD;
}

.list-group-item {
    position: relative;
    display: block;
    padding: 10px 15px;
    margin-bottom: -1px;
    background-color: #1F292F;
    border: 1px solid #7DC6BF;
    border-left: 0px solid #fff !important;
}
    
.dropdown-menu .divider {
    height: 1px;
    margin: 9px 0;
    overflow: hidden;
    background-color: #263238;
}
    
.media-list li {
    border-bottom: 1px solid #1F292F;
    border-left: 3px solid #1F292F;
}
    
.btn-info {
    background: #2CA9DB;
    color: white !important;
}
  
.content span {
    word-wrap: break-word;
    color: #C5C6C7;
}
    
#topbar-second .nav>li>a:hover,#topbar-second .nav>li>a:active,#topbar-second .nav>li>a:focus {
	border-bottom:3px solid #6fdbe8;
	background-color:#263238;
	color:#C5C6C7;
	text-decoration:none;
}
  
#topbar-first .notifications .arrow::after {
    border-bottom-color: #1F292F;
}
    
#topbar-second .nav>li>ul>li>a:hover,#topbar-second .nav>li>ul>li>a.active {
	border-left:3px solid #6fdbe8;
	background-color:#263238;
}
   
#topbar-second .nav>li>a:hover,#topbar-second .nav .open>a,#topbar-second .nav>li.active {
	border-bottom:3px solid #6fdbe8;
	background-color:#263238;
}
    
.media-list .badge-space-type {
	background-color:#263238;
}
    
.media-list li:hover,.media-list li.selected {
	background-color:#263238;
    transition-timing-function: linear, ease-out;
}
    
.atwho-input.form-control {
    color: #ffffff;
}
    
.modal-content {
    background-color: #343D44;

}
    
.media-list li.new {
    background-color: #37474F;
}
    
.media .media-body {
    font-size: 13px;
    color: #b7b7b7;
}
    
input[type="text"], input[type="password"], input[type="select"] {
    color: #ffffff;
}
    
.media .media-body h4.media-heading {
    color: #a1a1a1;
}
    
.modal .dropdown-menu li a, .panel .dropdown-menu li a, .nav-tabs .dropdown-menu li a {
    color: #b7b7b7;
}
    
.modal .dropdown-menu, .panel .dropdown-menu, .nav-tabs .dropdown-menu {
    border: 1px solid #37474F;
}
 
.modal .dropdown-menu li, .panel .dropdown-menu li, .nav-tabs .dropdown-menu li {
    border-left: 3px solid #37474F;
}
    
.badge {
    background-color: #393D49;
}

    
.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    color: #FFFFFF;
    background-color: #263238;
    border: 1px solid #263238;
}    
    
    
.nav-tabs > li > a {
    border-radius: 4px 4px 4px 4px;
}
    
.nav > li > a:hover, .nav > li > a:focus {
    background-color: #37474F;
    transition: background-color 0.2s ease, color 0.2s ease;
}

    
.nav-tabs > li > a:hover {
    border-color: #37474F;
}
.nav > li > a:hover, .nav > li > a:focus {
    background-color: #37474F;
} 
    
.has-success .form-control, .has-success .form-control:focus {
    color: #E3F2FD;
}
    
select.form-control {
    color: #E3F2FD;
}
    
label.control-label {
    font-weight: normal;
}
label {
    color: #cccccc;
}
   
    
.form-control:focus {
	border:2px solid #708FA0;
	outline:0;
	box-shadow:none;
    transition : border 0.3s ease-in-out;
}
    

    
#topbar-second .nav > li > ul > li > a {
    border-left: 3px solid #263238;
    background-color: #1F292F;
    color: #B7B7B7;
}
    
.vanishIn {
	-webkit-animation-name:vanishIn;
	animation-name:vanishIn;
}
.vanishOut {
	-webkit-animation-name:vanishOut;
	animation-name:vanishOut;
} 
    
@-webkit-keyframes vanishIn {
	0% {
		opacity:0;
		-webkit-transform-origin:25% 25%;
		-webkit-transform:scale(1.5,1.5);
		-webkit-filter:blur(45px);
	}
	100% {
		opacity:1;
		-webkit-transform-origin:25% 25%;
		-webkit-transform:scale(1,1);
		-webkit-filter:blur(0px);
	}
}
@keyframes vanishIn {
	0% {
		opacity:0;
		transform-origin:25% 25%;
		transform:scale(1.5,1.5);
		-webkit-filter:blur(45px);
	}
	100% {
		opacity:1;
		transform-origin:25% 25%;
		transform:scale(1,1);
		-webkit-filter:blur(0px);
	}
}
@-webkit-keyframes vanishOut {
	0% {
		opacity:1;
		-webkit-transform-origin:25% 25%;
		-webkit-transform:scale(1,1);
		-webkit-filter:blur(0px);
	}
	100% {
		opacity:0;
		-webkit-transform-origin:25% 25%;
		-webkit-transform:scale(1.5,1.5);
		-webkit-filter:blur(10px);
	}
}
@keyframes vanishOut {
	0% {
		opacity:1;
		transform-origin:25% 25%;
		transform:scale(1,1);
		-webkit-filter:blur(0px);
	}
	100% {
		opacity:0;
		transform-origin:25% 25%;
		transform:scale(1.5,1.5);
		-webkit-filter:blur(10px);
	}
}
    
    .dropdown-menu {
        -webkit-animation-duration: 0.5s;
       -moz-animation-duration: 0.5s;
       -o-animation-duration: 0.5s;
       animation-duration: 0.5s;
       -webkit-animation-fill-mode: both;
       -moz-animation-fill-mode: both;
       -o-animation-fill-mode: both;
        animation-fill-mode: forwards;
       -webkit-animation-name: vanishIn;
       animation-name: vanishIn;
    }
    
#chatBottomBar {
    background: transparent;
}

#chatLineHolder {
    width: 73%;
    height: 500px;
    background: #1F292F;
    overflow-y: hidden;
    box-shadow: 0;
}

.chat {
    min-height: 33px;
    position: relative;
    background: #384248;
    border: 0px solid #384248;
    margin: 18px 5px 5px 18px;
    padding: 3px 5px 4px 37px;
    color: #FFFFFF;
    border-radius: 6px;
}

.chat .text {
    color: #f1f1f1;
}

.chat .time {
    color: #AFB3B6;
}

#chatLineHolder {
    box-shadow: 0px 0px 0px #999;
}

#chatUsers {
    background-color: #455A64;
    box-shadow: 0px 0px 0px #999;
    border-radius: 6px;
}

input#chatText {
    width: 100%;
    color: #ffffff;
}
}
