@namespace url(http://www.w3.org/1999/xhtml);

/***** киви qiwi.com ************************/

@-moz-document domain("qiwi.com")
 , domain("qiwi.ru") 
{

:root {
/* var(--or-bg) */ --or-bg: #F27A19 ; /* ор. F27A19 */
/* var(--lt-bg) */ --lt-bg: #FFFAF4 ; /* FFFAF4 */ 
/* var(--rod-bg) */ --rod-bg: #ffa834 ; /* родной ор. ffa834 */
/* var(--rodsin-bg) */ --rodsin-bg: #0023A0 ; /* родной синий 0023A0 */
/* var(--block-bg) */ --block-bg: rgba(240, 249, 255, 1) ; /* F9FbFf=F9FBFF */
/* var(--block-bg-2) */ --block-bg-2: rgba(230, 249, 255, .30) ; 
/* var(--links) */ --links: #2042A5 ; /* 2042A5 */
/* var(--links-hover) */ --links-hover: #E94204 ; /* E94204 bb0000 009900 */
/* var(--bord) */ --bord: var(--links) ; /* #3366FF */
/* var(--bord0) */ --bord0: #d7d7d7 ; /* серый d7d7d7 eee */
/* var(--fon) */ --fon: url("http://jpegshare.net/images/28/80/2880827d1f172f8e334b462d827f9724.png");
} 
 
HTML
, HTML > BODY 
, #container 
, #container SECTION
, div.qw-container > div
, SECTION.wrapper-head
, SECTION.wrapper-head > SECTION
, #body-wrapper
, #subNavWrapper
, HTML > BODY > FOOTER
{
 background: #fff fixed var(--fon) !important;
}

/* вход чеерез */
.oauth-provider-list > div > div span {
 color: #FFFFFF !important;
}

#body-wrapper{
 margin-top: -250px !important;
}
 
 
FORM[class="qiwi-payment-form payable"] > div ,
FORM[class="qiwi-payment-form payable"] > div > .qiwi-payment-provider {
 background: transparent !important;
 background-color: rgba(240, 240, 255, .3) !important; 
}
.payment_frm 
{
 background: transparent !important;
 background-color: rgba(240, 240, 255, .3) !important; 
 border: 3px solid var(--bord0) !important; 
}
.narrow.qiwicards ul[class^="list"] > li ,
FORM[class="qiwi-payment-form payable"] ,
.narrow .list_line .list_line-i ,
.page.narrow.qiwi-eggs-main {
 border: 3px solid var(--bord0) !important; 
}
.pageItem.wide .providerIconFrameWrap .providerIconFrame {
 border: 3px solid var(--bord0) !important; 
 position: relative !important;
 top: -3px !important;
 left: 12px !important;
}
.qiwi-payment-provider .qiwi-payment-provider-logo 
 {
 border: 3px solid var(--bord0) !important; 
 position: relative !important;
 top: 6px !important; /* -135px */
 left: 0px !important; /* 548px */
} 
 
/* поднимаем блок поиска */
#header .header-content-wide .auth.auth__authorized {
 padding-bottom: 0px !important;
 margin-bottom: -26px !important;
} 
/* лого */
#header .header-content > a {
 position: relative !important;
 top: -55px !important;
}
 
 
/* поиск */
#header .search-form {
} 
#header .search-form INPUT {
 font-weight: bold !important; 
 font-size: 1.1em !important; color: var(--or-bg) !important;
 border-color: var(--bord0) ; 
 height: 36px !important;
 margin-left: -0px !important;
 padding-left: 0px !important;
}
#header .search-form:hover > FORM > INPUT {
 border-color: var(--rod-bg) !important; /* яндекс FFCC33 */
}
#header .search-form INPUT:focus {
 border-color: var(--or-bg) !important; /* FF6600 */
}
#header .search-form INPUT:focus:hover {
 border-color: var(--or-bg) !important;
}
/* search BUTTON */ 
#header .search-form FORM BUTTON {
 margin-top: -6px !important;	
} 
/* подсказки */
[class="ui-autocomplete ui-menu ui-widget ui-widget-content ui-corner-all"] {
 border: 3px solid var(--or-bg) !important; 
 position: fixed !important; 
 z-index: 16201 !important; 
 margin-left: 0px !important;
 top: 130px !important; 
 max-height: 50vh !important;
 overflow-y: scroll !important; 
}
 
 
/* fix header */ 
#container {
 margin-top: 0px !important;
}
/* тело */ 
div#body-wrapper {
 margin-top: 120px !important;
 margin-bottom: 0px !important; 
}
/* верхняя полоса */
div .emergency-notification {
 position: fixed !important; 
 z-index: 16101 !important; 
 margin-top: -240px !important; 
}
div.infoblock {
 display: none !important;
} 
/* осн. блок заголовок */
SECTION.wrapper-head {
 position: fixed !important; 
 z-index: 6000 !important; 
 height: 130px !important; /* height */
 margin-top: -120px !important;
 margin-bottom: 0px !important; 
 border-bottom: 1px var(--bord) solid !important; /* var(--bord) var(--rod-bg) */
 box-shadow: 0px 0px 12px 0px #444 !important; 
 width: 100% !important; /* 99.5% */
}

/* fix левая колонка */ 
div#navigation {
 background: transparent !important; /* var(--fon) F3F2F0 */
}
#navigation > div.menu {
 position: fixed !important; 
 z-index: 5 !important; 
 margin-top: -2px !important; /* -28px */
 line-height: 0.85 !important; 
 border: 1px solid var(--bord) !important; /* 3366FF */
 padding-left: 6px !important;
 padding-right: 6px !important;
 background-color: var(--block-bg) !important; /* F9FbFf */
 margin-left: 3px !important;
 width: 212px !important;
}
#navigation > div.menu ul LI ,
#navigation > div.menu ul LI a ,
#navigation > div.menu ul LI label, 
#navigation > div.menu ul LI span ,
#navigation > div.menu ul LI input {
 font-size: 0.88rem !important; 
} 
#navigation > div.menu ul LI,
#navigation > div.menu ul LI span a ,
#navigation > div.menu ul LI span a span {
 padding-top: 0px !important;
 padding-bottom: 0px !important;
} 
#navigation > div.menu ul LI a {
 padding-top: 7px !important;
 padding-bottom: 7px !important;
} 
#navigation > div.menu ul LI a:hover {
 text-decoration: none !important; 
}
/* календарь - выбор даты */ 
#ui-datepicker-div {
 border: 2px solid var(--or-bg) !important; /* #BA610E */
 margin-top: -5px !important;	
 margin-left: 14px !important;
} 
 
 
 
/* верхняя полоса */
.emergency-notification .emergency-notification_cnt {
 margin-top: -1px !important;
 padding-top: 27px !important;
}
.emergency-notification BUTTON {
 color: #000 ; 
 border-width: 0px !important; 
 padding-bottom: 2px !important;
}
.emergency-notification BUTTON:hover {
 background: #FFF4CC !important;
}
.header-content-wide {
 margin-top: 0px !important;
 padding-top: 0px !important;
/* строка поиска */ 
 margin-bottom: -0px !important; 
}
/* лого */ 
.header-content #siteLogo {
 margin-top: -0px !important;
}

/* Оплатить Перевести Пополнить кошелек Банковские карты Вывести */
#navMenuWrap #navMenu {
 margin-top: -20px !important;
 padding-bottom: 0px !important;
}
/* .wrapper-head {
 margin-top: 0px !important;
 margin-bottom: 0px !important;
 padding-bottom: 0px !important;
} */
#nav li {
 padding-bottom: 6px !important;
 margin-bottom: 0px !important;
}
 
 

 
 
* {
 -moz-transition: none !important; 
/* font-weight: bold !important; */
 
}
.wrapper-head * ,
#body-wrapper *:not([class^="icon"]):not([class*="fa fa"]) 
{
 color: #000 ; 
 font-weight: normal !important; /* normal bold */
 font-family: Tahoma ;
 /* Arial Narrow Arial Black Tahoma Trebuchet MS Verdana 
 Courier New Franklin Gothic Medium Europe */
 /* Georgia Times New Roman Lucida Console Lucida Sans Unicode Monotype.com */
}
/* родной фонт для значков */
*[class^="icon"] ,
*[class^="fa fa"]
{
 font-family: FontAwesome !important; 
}
 
/* инфа о счете сверху */
.auth_balance {
/* оранж. полоска #f7991d */ 
 border-width: 2px !important; 
/* border-color: transparent !important; */
} 
/* весь прямоугольник */ 
.account_current {
}
.account_current:hover {
}
/* Счет Qiwi */ 
.account_current:hover .account_current_label ,
/* скока */
.account_current:hover .account_current_amount 
{
 color: #B64800 !important; 
} 
 
/* после нажатия */
.account_list {
 background: #fff !important; /* #FFFDFC FDF4E4 */
 border: solid #f9a01f !important; 
 border-width: 2px !important; 
 padding-top: 0px !important;
}
/* LI - элементы списка */
.account_list LI {
 margin-top: 2px !important; 
}
/* Счет Qiwi, RUB */
.account_list LI[class*="defaultAccount"] .account_current_label ,
/* текст про счет */
.account_list LI[class*="defaultAccount"] p.account_current_description {
 margin-left: -2px !important; 
}
/* скока бабок */
.account_list LI[class*="defaultAccount"] .account_current_amount {
 margin-right: 12px !important; 
 padding-left: 0px !important;
}
.account_list LI:hover {
/* background-image: -moz-linear-gradient( top, 
 #f7991d , 
 rgb(261, 230, 176) 10%, 
 #fff, 
 rgb(261, 230, 176) 90%, 
 #f7991d ) !important; 
 box-shadow: inset 0 0 15px 2px #f7991d !important; */
 background-color: var(--lt-bg) !important;
 box-shadow: 0 0 0px 2px var(--rod-bg) !important; 
/* = outline: 2px solid var(--rod-bg) !important; */
}
.account_list LI a {
 font-weight: bold !important; 
}
.account_list LI:hover a {
 color: #C07E1B!important; 
}
.account_list LI a:hover {
 color: #B64800 !important; 
 text-decoration: underline !important; 
}
 
 
 
 
/* история = Отчеты */
#body-wrapper DIV[class="base"] .reports .reportsLine.status_PROCESSED,
#body-wrapper DIV[class="base"] .reports .reportsLine.status_SUCCESS ,
#body-wrapper DIV[class="base"] .reports .reportsLine.status_ERROR {
 border: 1px solid transparent !important; 
}
#body-wrapper DIV[class="base"] .reports .reportsLine.status_PROCESSED.active,
#body-wrapper DIV[class="base"] .reports .reportsLine.status_SUCCESS.active {
 background: #0afbFF !important;
 border-color: var(--bord) !important; 
}
#body-wrapper DIV[class="base"] .reports .reportsLine.status_ERROR.active,
#body-wrapper DIV[class="base"] .reports .reportsLine.status_ERROR.active {
 background: #FFF9f9 !important;
 border-color: #CC3818 !important; 
} 
/* история = Отчеты Настройки */
#blockReports .settingsBlock .title , 
#blockReports .settingsBlock .toggle {
 margin-left: 0px !important; 
 padding-top: 0px !important; 
 margin-top: -0px !important; 
}
/* шестеренка */
#blockReports .settingsBlock I , 
#blockReports .settingsBlock I {
 margin-top: -3px !important; 
}
 
#blockReports .settingsBlock .toggle span.label , 
#blockReports .settingsBlock form .title span.label {
 font-weight: bold !important; 
}
#blockReports .settingsBlock .toggle:hover span.label ,
#blockReports .settingsBlock form .title:hover span.label {
 color: #a00 !important; 
}
/* выбор даты */
FORM[class="rangeSelectBlock"] ul.rangeSelect > li INPUT:hover ,
/* вып.списки */
.settings A[role="combobox"][aria-expanded="false"][aria-autocomplete="list"][aria-haspopup="true"][aria-disabled="false"]:hover {
 border-color: var(--rod-bg) !important; 
}
 
 
div.order,
div.reports {
 background-color: rgba(255, 255, 255, .80) !important; 
} 
/* Счета */ 
.order .ordersLine.status_CANCELED .ordersLine_cnt div > div > span , 
.order .ordersLine.status_CANCELED .ordersLine_cnt div > span ,
.order .ordersLine.status_CANCELED .ordersLine_cnt div > div ,
/* история = Отчеты */ 
/* ошибка */ 
.reports .reportsLine.status_ERROR div > div > span ,
.reports .reportsLine.status_ERROR div > span ,
.reports .reportsLine.status_ERROR div > div ,
.reports .reportsLine.status_CANCELED div > div > span ,
.reports .reportsLine.status_CANCELED div > span ,
.reports .reportsLine.status_CANCELED div > div 
{
 color: #900 !important; 
}
/* успех */ 
.reports .reportsLine.status_SUCCESS div > div > span , 
.reports .reportsLine.status_SUCCESS div > span ,
.reports .reportsLine.status_SUCCESS div > div ,
.order .ordersLine.status_PAID .ordersLine_cnt div > div > span , 
.order .ordersLine.status_PAID .ordersLine_cnt div > span ,
.order .ordersLine.status_PAID .ordersLine_cnt div > div 
{
 color: #060 !important; 
}
/* подсветка */
.reports .reportsLine.status_SUCCESS ,
.reports .reportsLine.status_ERROR ,
.order .ordersLine.status_CANCELED ,
.order .ordersLine.status_PAID {
 padding-left: 18px;
}
.reports .reportsLine.status_SUCCESS:hover ,
.reports .reportsLine.status_ERROR:hover ,
.order .ordersLine.status_CANCELED:hover ,
.order .ordersLine.status_PAID:hover {
 background-color: #F9FbFe !important; /* var(--lt-bg) #f9f9f9 */
 outline: 1px dotted #ABC0CC !important; /* #E0AC5F */
}

 

 
/* раздел Оплатить - Выставленные счета */
.orders .data UL LI .main-info:hover {
 background-color: var(--lt-bg) !important;
 box-shadow: 0 0 0px 2px var(--rod-bg) !important; 
}
/* Оплатить */
.orders .data UL LI .main-info:hover span.order-pay {
 color: #238507 !important;
} 
/* Отменить */
.orders .data UL LI .cancel-wrap:hover span.cancel-label {
 color: #BC2C0D !important;
}
 

/* список Оплатить -> Счета */
/* пс.кн. Оплатить */
.ordersLine_cnt .payOperation {
 padding: 2px 6px 2px 4px !important;
 margin: -15px 0px 0px 0px !important;
 border: 1px solid #aaa !important; 
 background-color: #ECFFE8 !important; 
 text-decoration: none !important; 
}
.ordersLine_cnt .payOperation:hover {
 border-color: #7BBA34 !important;
 background-color: #C0FD7B !important; 
}
/* пс.кн. Отменить */
.ordersLine_cnt .cancelOperation {
 padding: 2px 6px 2px 4px !important;
 margin: 3px 0px 0px 0px !important;
 border: 1px solid #aaa !important; 
 background-color: #FFF2F0 !important; 
 text-decoration: none !important; 
}
.ordersLine_cnt .cancelOperation:hover {
 border-color: #FD7B4A !important;
 background-color: #FDBCB3 !important; 
}
 

#body .double-column {
 margin-top: -242px !important; /* -242px !!осторожно!!! иначе линки смещаются */
}

/* селектор Избранные платежи Счета */
#body-wrapper #subNavWrapper #subNavMenu li a {
 font-weight: bold !important; 
 background-color: transparent !important; 
 border-bottom-width: 4px !important; 
}
#body-wrapper #subNavWrapper #subNavMenu li[class*="active"] a {
}
#body-wrapper #subNavWrapper #subNavMenu li a:hover {
/* background-image: linear-gradient(to bottom ,var(--rod-bg) , #E6972E, var(--rod-bg) ); */
 background-color: var(--or-bg) !important; /* F27A19 */
 color: #fff !important;
 border-color: transparent !important; 
/* outline: solid var(--rod-bg) 2px !important; */
}

 

/* линк Редактировать */
.edit-favorite-payments a.nolink span.subLink {
 margin: -6px -22px 16px 6px !important;
 color: #2859C6 !important;
} 
 
/* линки */
.wrapper-head a , 
#body-wrapper a ,
FOOTER.footer a ,
/* история - заголовок списка */ 
.reportsLine.noBorder div > div > span, 
.reportsLine.noBorder div > span ,
/* Счета - заголовок списка */ 
.order div[class="ordersLine"] .ordersLine_cnt div > span ,
.order div[class="ordersLine"] .ordersLine_cnt div > div > span, 
/* Избранные платежи */ 
.favs .data a .label,
/* Места пополнения */
#content .top-up .filters LI:not([class="active"]) SPAN ,
/* Комиссия */
#content .top-up .commissions LI:not([class="active"]) ,
/* список Все способы пополнения - заг. столбцов */
TABLE.methods tr.header SPAN 
{
 color: var(--links) !important; 
}


 
 
/* hover */ 
.wrapper-head a:hover , 
#body-wrapper a:hover,
FOOTER.footer a:hover, 
/* история - заголовок списка */ 
.reportsLine.noBorder div > div > span:hover, 
.reportsLine.noBorder div > span:hover ,
/* Счета - заголовок списка */ 
.order div[class="ordersLine"] .ordersLine_cnt div > span:hover ,
.order div[class="ordersLine"] .ordersLine_cnt div > div > span:hover, 
/* блок куда перевести бабки */ 
.list_grid-i:hover .list_grid-ttl , 
/* блок Вывести */ 
.providersList .category:hover .providerName ,
.popular LI:hover .text , 
.favorites-list UL LI:hover .name 
{
 color: var(--links-hover) !important; 
 opacity: 1 !important;
 text-shadow: none !important; 
 z-index: 30000 !important; 
}
 
/* Избранные платежи */
.favs .data a {
 outline: 1px solid var(--or-bg) !important; 
}
.favs .data a:hover .label {
 color: #fff !important; 
} 
.favs .data a:hover {
 background-color: var(--or-bg) !important; /* F27A19 */
} 

 
 
 
/* Пополнить */
/* Места пополнения */
#content .top-up .filters LI:not([class="active"]):hover SPAN ,
/* Комиссия */
#content .top-up .commissions LI:not([class="active"]):hover ,
/* список Все способы пополнения - заг. столбцов */
TABLE.methods tr.header SPAN:hover {
 color: var(--links-hover) !important; 
}
/* фильтр-поиск строка ввода */
#content .top-up form.search-methods {
 border: 2px solid var(--rod-bg) !important; 
 margin-bottom: 6px !important;
}
 
/* список Все способы пополнения */
TABLE.methods {
 background-color: transparent !important; /* var(--fon) transparent */
 background: var(--fon) !important; 
}
TABLE.methods tr {
 background-color: transparent !important; /* var(--fon) transparent */
 background: transparent !important; 
}
TABLE.methods td {
 background-color: transparent !important; /* transparent */
 background: #FFFFFF !important; 
}
TABLE.methods tr, 
TABLE.methods tr td {
 border-color: transparent !important; 
}
/* подсветка */ 
TABLE.methods tr:hover td {
 background-color: transparent !important; /* var(--fon) transparent */
 background: var(--lt-bg) !important; 
}

 
 
/* квадраты */ 
.list_grid a .list_grid-img ,
UL.providersList > LI .frame , 
.favorites-list UL LI .frame ,
.top-up-wrap.front UL LI ,
.popular LI .logo ,
ul.our-top-categories > li ,
.feature-mobile-apps ,
.feature-transfer.middle ,
li[class^="feature-"] ,
.last-messages > ul > li , 
.allcats > div.data > ul > li ,
div[class^="qw-content-col"] > a ,
div[class^="qw-content-col8"],
div.qw-block ,
.base div.order ,
#content div.staticPage ,
.base div.reports ,
.qw-content-wide , 
.payment-success > div[class^="page narrow"]
{
 outline: 2px solid var(--bord0) !important; /* d7d7d7 */
}
.list_grid a .list_grid-img ,
.providersList .category .frame {
 background-color: #fff !important;
 filter: none !important; 
}
/* квадраты hover */
/* блок куда перевести бабки */ 
.list_grid a:hover .list_grid-img, 
/* блок Вывести */ 
.providersList .category:hover .frame , 
/* список банков */
UL.providersList > LI:hover .frame , 
/* Пополнить кошелек */
.top-up-wrap.front UL LI:hover ,
.popular LI:hover .logo , 
.favorites-list UL LI:hover .logo ,
TABLE.methods tr.line:hover IMG ,
div[class^="qw-content-col"] > a:hover ,
a.qw-replenish-method-list-item:hover 
{
 filter: opacity(1) saturate(300%) brightness(100%) contrast(100%) !important; 
/* background-color: #fff !important; */
}
UL.providersList > LI:hover {
 opacity: 1 !important; 
}
.list_grid a:hover .list_grid-img ,
UL.providersList > LI:hover .frame , 
.favorites-list UL LI:hover .frame ,
.top-up-wrap.front UL LI:hover ,
.popular LI:hover .logo ,
a.qw-replenish-method-list-item:hover 
{
 outline: 2px solid var(--rod-bg) !important; 
}
/* Пополнить кошелек */
div[class^="qw-content-col"] > a:hover 
{
 outline-color: var(--rod-bg) !important; 
}
div[class^="qw-content-col"] > a:hover H4,
div[class^="qw-content-col"] > a:hover span.qw-link-box-centric-text {
 color: var(--links-hover) !important; 
}

 
 
/* номера страниц */
.switchSize a ,
.pagingLinks a 
{
 font-weight: bold !important; 
}
.switchSize li.active,
.pagingLinks li.active span
{
 background: #1B3789 !important;
 color: #FFFF00 !important; 
}
 
 
 
 
/* текст статуса Это вы */
div.types_list-i-my_stat {
 color: #fff !important; 
}
 
 
 
 
/* Log out */ 
.logout a { 
 color: #fff !important; 
 background: var(--or-bg) ; /* #aa0000 */
 text-shadow: none !important; 
 text-decoration: none !important; 
 padding-top: 2px !important;
 padding-bottom: 2px !important;
 padding-left: 5px !important;
 padding-right: 5px !important;
} 
.logout a:hover { 
 color: #fff !important; /* FFFF00 */
 background: #ee0000 !important;
} 
 

/* radio */
.qiwi-radio-menu-item-checkbox {
 padding: 0px !important;
 margin-top: 0px !important;
 font-size: 22px !important; 
 text-align: center !important; 
 vertical-align: middle !important; 
 line-height: 0.7 !important; 
} 
div[aria-checked="false"] .qiwi-radio-menu-item-checkbox {
 color: #FFFFFF !important; 
}
 
FOOTER.footer {
 padding-left: 270px !important; 
 color: #FFFF00 !important;
} 
FOOTER.footer * {
 color: #000 !important;
} 
 
} /* киви qiwi.com ********************************************/







/* Пополнить кошелек ***************** */
@-moz-document url-prefix("https://qiwi.com/replenish") ,
 url-prefix("https://w.qiwi.com/replenish") 
{
 
HTML > BODY {
 margin-top: 120px !important;
}
 
/* осн. блок заголовок */
SECTION.wrapper-head {
 margin-top: -120px !important;
 height: 130px !important; 
} 
 
.search-form {
 margin-top: -26px !important;	 
 
} 
 
#navMenuWrap {
} 
 
 
HTML > BODY *:not([class^="icon"]):not([class*="fa fa"]) 
{
/* font-weight: normal !important; */ /* normal bold */
 font-family: Tahoma ; 
