@-moz-document url-prefix("http://forum.codelyoko.fr/viewtopic.php"), url-prefix("https://forum.codelyoko.fr/viewtopic.php") {
body {
    background-color: #1f2840;
}

body table tbody td > table:nth-child(8) {
    max-width: 840px;
}
.row1, .row2 {
    background-color: #EEE !important;
}
.row2:not([style]) { /* Hacky thing to select forms */
    background-color: inherit !important;
}

.postbody, .postbody span, .postbody ~ span span, .row1 .postdetails, .row2 .postdetails {
    color: #000 !important;
}


.postbody, .postbody span, .postbody ~ span span {
    font-family: "Palatino Linotype", "Book Antiqua", Palatino, serif !important;
    font-size: 18px !important;
    line-height: 1.4 !important;
}

.quote, .spoiler_visible, .spoiler {
    background-color: #DFDFDF;
    border-color: #888;
    font-family: "Palatino Linotype", "Book Antiqua", Palatino, serif;
    font-size: 16px;
    line-height: 1.2;
    color: #000;
    box-shadow: 1px 2px 3px #00000029;
    border-radius: 2px;
}
.spoiler {
    height: 28px;
}
.spoiler input {
    height: 100%;
    border-color: #666;
    border-radius: 2px;
    background-color: #BBB;
    font-size: 12px !important;
    transition: all .1s;
}
.spoiler input:hover {
    background-color: #EEE;
}
.row1 .genmed, .row2 .genmed { /* Quotes header, sometimes...*/
    color: #000 !important;
    font-family: "Palatino Linotype", "Book Antiqua", Palatino, serif;
    font-size: 18px;
}
.postbody img { /* Avoid content overflow */
    max-width: 100%;
}
.row1 a, .row2 a, .row1 a.postlink, .row2 a.postlink {
    color: #268eea;
}
.row1 .postdetails img:first-child, .row2 .postdetails img:first-child { /* Default banners are white */
    -webkit-filter: invert(1);
    filter: invert(1);
}
}
