css:
@-moz-document regexp("https?://alicorn.elcenia.com/board/.*") {
html, body {
  color: #8C8C8C;
  background-color: #222;
}
ul.topiclist.topics li, ul.topiclist.forums li, .post.bg1, .post.bg2 {
  background-color: #54595D;
}
.post.bg1, .post.bg2 {
  background-color: #585858;
}
ul.topiclist.topics li.row:hover, ul.topiclist.forums li.row:hover {
  background-color: #6D6D6D;
}
.panel, .navbar {
  background-color: #666;
}
.reply-icon span {
  background-image: none;
}
.postprofile a:link, .postprofile a:visited, .postprofile dt.author a {
  color: #8ECCFF;
}
.postbody, .post .postbody .content, .post:target .postbody .content, .post .postprofile, .post .postprofile strong {
  color: #C5C5C5;
}
.post .postprofile strong {
  font-weight: bold;
}
.headerbar {
  background-image: none;
  background-color: #666;
}
.forumbg {
  background-image: none;
  background-color: #6F6F6F;
}
.forabg {
  background-image: none;
  background-color: #6F6F6F;
}
a:link {
  color: #ABABAB;
}
a:hover {
  color: #D2D2D2;
}
a:visited {
  color: #ABABAB;
}
.postlink:visited {
  color: #8E8E8E;
}
a.topictitle:hover, a.topictitle:active, a.forumtitle:hover, a.forumtitle:active {
  color: #D2D2D2;
}
#page-body h2 a {
  color: #ABABAB;
}
.pagination span a, .pagination span a:link, .pagination span a:visited {
  background-color: #6F6F6F;
  color: #B9B9B9;
}
.post:target .content, .content {
  color: #000;
}
.postprofile {
  color: #000000;
}
blockquote {
  background-color: #686e71;
  border-color: #B3B3A1;
	color: #d4d4d4;
}
blockquote blockquote {
  background-color: #767c7f;
	color: #dedede;
}
blockquote blockquote blockquote {
  background-color: #82898c;
	color: #e6e6e6;
}
span.corners-bottom, span.corners-bottom span, span.corners-top, span.corners-top span {
  background: none;
}
ul.topiclist li {
  color: #E2E2E2;
}
li.row {
  border-bottom-color: #4A4A4A;
}
div.postbody > div.content div[style]:not([class]) {
  background-color: #666666 !important;
  border: 1px solid #868686 !important;
}
div.postbody > div.content hr + div[style]:not([class]) {
	border-color: transparent !important;
}
div.content code {
  background-color: #464646 !important;
  color: #FFFFFF !important;
}
hr {
  border-color: #949494;
  border-top-color: #949494;
}
dl.details dd {
  color: #171A21;
}
h2 {
  color: #7D848E;
}
#cp-main .panel {
  background-color: #868686;
}
li.row:hover {
  background-color: #676767;
}
.bg1 {
  background-color: #747A7D;
}
.bg2 {
  background-color: #6E7173;
}
#cp-main .panel li.row {
  border-bottom-color: #888888;
  border-top-color: #5A5A5A;
}
.pmlist li.pm_replied_colour, .pm_replied_colour {
  border-color: #676767;
}
#tabs a {
  background-color: #828282;
  background-image: none;
}
#tabs a span {
  background-image: none;
  color: #3C3C3C;
}
#tabs a:hover span {
  color: #212121;
}
#tabs .activetab a {
  border-bottom-color: #5A5A5A;
}
#cp-main span.corners-top, #cp-menu span.corners-top, #cp-main span.corners-top span, #cp-menu span.corners-top span, #cp-main span.corners-bottom, #cp-menu span.corners-bottom, #cp-main span.corners-bottom span, #cp-menu span.corners-bottom span {
  background-image: none;
}
#navigation a {
  background-image: none;
  background-color: #929292;
}
#navigation a:hover {
  background-color: #A9A9A9;
  color: #292929;
}
#navigation #active-subsection a {
  background-color: #7B7B7B;
  color: #B7B7B7;
}
#navigation #active-subsection a:hover {
  color: #E6E6E6;
}
.cp-mini {
  background-color: #8E8E8E;
}
#cp-main .pm {
  background-color: #777777;
}
h3 {
  color: #B7B7B7;
}
dl.codebox {
  background-color: #383838;
  border-color: #757575;
}
dl.codebox dt {
  border-bottom-color: #757575;
}
.current {
  color: #A0A0A0 !important;
}
label {
  color: #45494E;
}
.inputbox, #search-box #keywords {
  background-color: #484848;
  border-color: #717171;
  color: #BFBFBF;
}
#search-box input {
  border-color: #717171;
}
.inputbox:focus {
  color: #D8D8D8;
}
.inputbox:hover {
  border-color: #BBBBBB;
}
#message-box textarea {
  color: #BFBFBF;
}
table.table1 tbody tr:hover, table.table1 tbody tr.hover {
  background-color: #616161;
}
table.table1 td {
  color: #D0D0D0;
}
.content {
  word-wrap: break-word;
}
}
