css:
@-moz-document domain("forums.spacebattles.com") {
/*Orange to blue: Make hue 140 -> 25; 137 -> 22; 143 -> 28; 141 -> 26*/

#header {
    background: rgb(214, 146, 33) url('https://forums.spacebattles.com/styles/flexile-dark/flexile/main-bar-gradient.png') repeat-x 0 0;
}

.navTabs .navTab.selected .tabLinks {
    background: #e18620 url('https://forums.spacebattles.com/styles/flexile-dark/flexile/navigation-tab.png') repeat-x top;
}

.navTabs .navTab.selected .navLink {
    background: #e18620 url('https://forums.spacebattles.com/styles/flexile-dark/flexile/navigation-tab.png') repeat-x top;
}

.navTabs .navTab.selected.PopupOpen .navLink {
    color: rgb(99, 72, 27);
    background: #e4ae6b url('https://forums.spacebattles.com/styles/flexile-dark/flexile/user-bar-hover.png') repeat-x top;
}

.navTabs .navTab.PopupClosed:hover {
    background-color: rgb(214, 146, 33);
}

.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    border: 1px solid #e4ae6b;
}

.heading, .xenForm .formHeader {
    background-color: #e18620;
    border-bottom: 1px solid rgb(214, 146, 33);
}

.nodeList .categoryStrip {
    color: rgb(255, 243, 217);
    background: rgb(173, 126, 39) url('https://forums.spacebattles.com/styles/flexile-dark/flexile-dark/category-bar-gradient.png') repeat-x top;
    border: 1px solid rgb(206, 153, 53);
}

.nodeList .categoryStrip .nodeTitle {
    color: rgb(255, 243, 217);
}

.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background: rgb(214, 146, 33) url('https://forums.spacebattles.com/styles/flexile-dark/flexile-dark/node-sprite.png') no-repeat -44px 0;
}

.node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
    background: #e18620 url('https://forums.spacebattles.com/styles/flexile-dark/flexile-dark/node-sprite.png') no-repeat 0 0;
}

.sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading {
    color: #e4ae6b;
}

.tagCloud .tagCloudTag1 {
    color: #e18620;
}

.tagCloud .tagCloudTag7 {
    color: rgb(99, 72, 27);
}

.tagList .tag {
    color: rgb(214, 146, 33);
}

.discussionList .sectionHeaders {
    color: rgb(255, 243, 217);
    background: rgb(173, 126, 39) url('https://forums.spacebattles.com/styles/flexile-dark/flexile-dark/category-bar-gradient.png') repeat-x top;
    border: 1px solid rgb(206, 153, 53);
}

.discussionList .sectionHeaders a {
    color: rgb(255, 243, 217);
}

.discussionList .sectionHeaders dd a[href]:hover {
    background-color: rgb(146, 98, 10);
}

a.callToAction span {
    background: #e18620 url('https://forums.spacebattles.com/styles/flexile-dark/xenforo/gradients/form-button-white-25px.png') repeat-x center -8px;
}

a.callToAction:hover span {
    background-color: #e4ae6b;
}

.sectionFooter {
    color: #e18620;
}

body .itemPageNav a {
    color: rgb(248, 222, 163);
    background: rgb(146, 98, 10) url('https://forums.spacebattles.com/styles/flexile-dark/xenforo/gradients/category-23px-light.png') repeat-x;
    border: 1px solid rgb(173, 126, 39);
}

body .itemPageNav a:hover {
    color: rgb(248, 222, 163);
    background-color: rgb(173, 126, 39);
    border-color: rgb(206, 153, 53);
}

.Popup .PopupControl:hover, .Popup.PopupContainerControl:hover {
    color: rgb(214, 146, 33);
}

.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
    background: #e4ae6b url('https://forums.spacebattles.com/styles/flexile-dark/flexile/user-bar-hover.png') repeat-x top;
}

.Menu {
    border: 1px solid #e4ae6b;
    border-top: 5px solid #e4ae6b;
}

.message .newIndicator {
    color: rgb(214, 146, 33);
    background: #e4ae6b url('https://forums.spacebattles.com/styles/flexile-dark/xenforo/gradients/form-button-white-25px.png') repeat-x top;
    border: 1px solid #e4ae6b;
}
.message .newIndicator span {
    background-color: #e4ae6b;
}

.secondaryContent a {
    color: rgb(214, 146, 33);
}
.primaryContent a {
    color: rgb(214, 146, 33);
}

.threadmarker .label strong {
    color: rgb(230, 184, 100);
}

.threadmarker_nav_bottom .previous a:hover, .threadmarker_nav_bottom .index a:hover, .threadmarker_nav_bottom .next a:hover, .threadmarker_nav_top .previous a:hover, .threadmarker_nav_top .index a:hover, .threadmarker_nav_top .next a:hover {
    color: rgb(248, 222, 163);
    background-color: rgb(173, 126, 39);
    border-color: rgb(206, 153, 53);
}

a:link, a:visited {
    color: rgb(214, 146, 33);
}

.ugc a:hover, .ugc a:focus {
    color: rgb(255, 243, 217);
    background: rgb(146, 98, 10) url('https://forums.spacebattles.com/styles/flexile-dark/xenforo/gradients/category-23px-light.png') repeat-x top;
}

.thread_view .threadAlerts dt {
    color: #e4ae6b;
}

.thread_view .threadAlerts dd {
    color: rgb(99, 72, 27);
}

.blockLinksList a:active, .blockLinksList li.kbSelect a:active, .blockLinksList a.selected, .blockLinksList li.kbSelect a.selected, .blockLinksList label:active, .blockLinksList li.kbSelect label:active, .blockLinksList label.selected, .blockLinksList li.kbSelect label.selected {
    color: rgb(255, 243, 217);
    background-color: rgb(173, 126, 39);
}

.xenOverlay .section, .xenOverlay .sectionMain {
    border: 20px solid #452703;
    border-color: rgb(69, 39, 3);
    border-color: rgba(69, 39, 3, 0.5);
}

.blockLinksList a, .blockLinksList label {
    color: rgb(214, 146, 33);
}

.statusEditorCounter {
    color: orange;
}

.subHeading {
    color: rgb(255, 243, 217);
    background: rgb(173, 126, 39) url('https://forums.spacebattles.com/styles/flexile-dark/flexile-dark/category-bar-gradient.png') repeat-x top;
    border: 1px solid rgb(206, 153, 53);
}

.textWithCount.subHeading .count {
    border: 1px solid rgb(173, 126, 39);
    background-color: rgb(146, 98, 10);
    color: rgb(255, 243, 217);
}

.navigationSideBar a:hover {
    color: #e18620;
}

.button {
    color: rgb(255, 243, 217);
    background: rgb(146, 98, 10) url('https://forums.spacebattles.com/styles/flexile-dark/xenforo/gradients/form-button-white-25px.png') repeat-x top;
    border: 1px solid rgb(173, 126, 39);
}

.button:hover, .button[href]:hover, .buttonProxy:hover .button {
    color: rgb(248, 222, 163);
    background-color: rgb(173, 126, 39);
}

.button:active, .button[href]:active, .button.ToggleButton.checked, .buttonProxy:active .button {
    border-color: rgb(213, 215, 200);
    border-top-color:#bcbdb3;
    background-color:rgb(163, 164, 153);
}

.button:focus {
    border-color: #e4ae6b;
}

.PageNav a {
    color: rgb(248, 222, 163);
    background-color: rgb(146, 98, 10);
    border: 1px solid rgb(173, 126, 39);
}

.PageNav a.currentPage {
    color: rgb(99, 72, 27);
    background-color: rgb(32, 31, 30);
    border-color: #e4ae6b;
}

.PageNav a:hover, .PageNav a:focus {
    color: rgb(248, 222, 163);
    background-color: rgb(173, 126, 39);
    border-color: rgb(206, 153, 53);
}

.button.primary {
    background-color: rgb(206, 153, 53);
}

#uaThreadViewContainer h3, #uaThreadReadContainer h3 {
    color: #e4ae6b;
}

#uaThreadViewContainer h3 .footnote, #uaThreadReadContainer h3 .footnote {
    color: #e4ae6b;
}
}
