css:
@-moz-document domain("pokefarm.com") {
/***   STYLE   ***/

a,
a:active,
a:visited {
    text-decoration: none;
    color: #494949;
}
.userlink0:link,
.userlink0:visited,
.userlink1:link,
.userlink2:link,
.userlink1:visited,
.userlink2:visited,
.userlink3:link,
.userlink3:visited,
.userlink4:link,
.userlink4:visited,
.userlink5:link,
.userlink5:visited,
.userlink6:link,
.userlink6:visited,
.userlink8:link,
.userlink8:visited,
.userlink10:link,
.userlink10:visited {
    color: #F18686;
}
body {
    background: #fff;
    color: #494949;
    font-size: 12px;
    line-height: 1.7;
    padding-bottom: 20px;
    border-color: #494949 !important;
}
hr {
    display: none;
}
#core[style*=width] {
    box-shadow: none;
}
#content {
    background: #fff;
    color: #494949;
    font-size: 12px;
    line-height: 1.7;
    border: 1px solid #494949;
    border-top: none;
    padding-bottom: 10px;
    padding-top: 20px;
}
#aboutbox>h3>.menu>.menu_icon,
#aboutbox>h3>.menu>.menu_icon:before,
#aboutbox>h3>.menu>.menu_icon:after,
.forumpost>.metadata>.menu>.menu_icon,
.forumpost>.metadata>.menu>.menu_icon:before,
.forumpost>.metadata>.menu>.menu_icon:after {
    border-color: #494949;
}
#aboutbox,
#aboutmepreview {
  background: none;
}
.tooltip_content {
    color: #494949;
    font-size: 10px;
    background: #fff;
    border-color: #494949;
}
.tooltip_content a {
    color: #494949;
}
.loading {
    border-color: #494949;
}
.loading:before {
    border-color: #494949 transparent transparent #494949;
}
.loading:after {
    border-color: transparent #494949 #494949 transparent;
}
.panel {
    background: none;;
    color: #494949;
    border: 1px solid #494949;
    line-height: 1.7;
}
.forumpost>.bbcode .panel,
.bbcode .panel {
    border-color: #494949;
    width: 98%;
    margin: auto;
    background: #FFF;
    color: #494949;
    text-align: justify;
}
.panel>h3:first-child {
    border-radius: 10px 10px 0 0;
}
.panel>h3,
.panel>h3 a.small {
    font-size: 12px;
    background: none;
    color: #494949;
    border-bottom: none;
}
.panel>h3 a,
.panel.accordion>h3>a {
    color: #494949;
}
.tabbed_interface>ul>li.tab-active {
    border-right-color: #494949;
    background-color: #494949;
    color: #FFF;
}
.tabbed_interface>ul>li {
    border-left: 1px solid #494949;
    border-bottom: 1px solid #494949;
    border-right: 1px solid #494949;
    background-color: #FFF;
    color: #494949;
}
.tabbed_interface>div {
    background-color: #FFF;
    color: #494949;
    border: 1px solid #494949;
}
.tabbed_interface>ul>li:first-child {
    border: 1px solid#494949;
}
.tabbed_interface>ul>li:last-child {
    border-bottom: 1px solid #494949;
}
.mq25 .tabbed_interface>ul,
.tabbed_interface.horizontal>ul {
    border: #494949;
}
.mq25 .tabbed_interface>ul>li.tab-active,
.tabbed_interface.horizontal>ul>li.tab-active {
    border: none;
}
#thisispfq,
#thisispfq a {
    background: #494949;
    border-color: #494949;
    color: #FFF;
}
.dialog>div>div>div>h3 {
    color: #494949;
    background: #fff;
}
div.dialog {
    background-color: rgba(073,073,073,0.75) !important;
}
.dialog>div>div>div {
    height: 50%;
    overflow: scroll;
    border-color: #494949;
    background-color: #FFF;
}
.dialog>div>div>div>h3,
div.bbcode_editor {
    background-color: #FFF;
    border-color: #494949;
    color: #494949;
}
.dialog>div>div>div>h3 a,
.dialog>div>div>div.accordion>h3>a {
    color: #494949;
}

/***   HEADER   ***/

div#announcements {
    display: block;
    background: #fff;
    margin-bottom: 0;
    color: #494949;
    border: 1px solid #494949;
}
#announcements>ul>li {
    font-size: 10px;
    text-transform: uppercase;
}
#announcements>ul>li>a {
    color: #494949;
    border: 1px solid #494949;
}
#announcements>ul>li>a>img {
    display: none;
}
#announcements>ul>li>a.new:after {
    background-color: #F18686;
    border: none;
    box-shadow: none;
    animation: none;
}
.newstag {
    background: #494949;
    border: none;
    color: #FFF;
}
#header {
    background: #fff;
    font-size: 12px;
    line-height: normal;
    border: none;
    padding-bottom: 7px;
    border-bottom: none;
}
#navigation {
    background: #fff;
    border: 1px solid #494949;
    margin-bottom: -1px;
}
#navigation>#navbtns>li>a>img,
#navigation>#navbookmark>li>a>img {
    display: none;
}
#navigation>#navbtns>li>a,
#navigation>#navbookmark>li>a {
    display: block;
    line-height: 1.7;
    font-size: 14px;
    font-weight: bold;
    color: #494949;
    text-decoration: none;
    background-color: #fff;
    border-radius: 0;
}
#navigation>#navbtns>li,
#navigation>#navbookmark>li {
    height: auto;
}
#header>#social>#notifs>a[data-count]:after,
#header>#social>#notifs>div {
    background: #fff;
    color: #494949;
    border: 1px solid #494949;
}
#header>#counters>#counterlist {
    font-size: 10px;
}
#header>#social>#notifs>div:before,
#header>#social>#notifs>div:after {
    display: none;
}
#header>#counters>#dailybonus a,
#header>#counters>#dailybonus {
    color: #FFF !important;
    line-height: 1.5;
    font-size: 10px;
}
#header>#counters {
    border-color: #494949;
    background: #494949;
    color: #fff;
    height: 93px;
}
#header>#counters a {
    color: #FFF;
}
#header>#counters>#counterlist .tooltip_content img {
    display: none;
}
#header>#counters>#counterlist .tooltip_content a {
    color: #494949 !important;
}

/***   PARTY   ***/

#trainercard {
    background: none;
    color: #494949;
    height: 170px;
}
#trainercard:before,
#trainercard>div.tc-header,
#trainercard>div.tc-name {
    background: none;
    color: #494949;
}
#trainercard>div.tc-sprite>.layer,
#trainercard>div.tc-sprite,
#trainercard>div.tc-starter,
.tc-header,
#trainercard>div.tc-ballicon,
#trainercard>div.tc-badges {
    display: none;
}
#trainercard>div.tc-status {
    top: 80px;
    border-radius: 8px;
    background: none;
    border: 1px solid #494949;
    color: #494949;
}
#trainercard>div.tc-joined {
    background: none;
    border: none;
    margin-left: -120px;
}
#trainercard>div.tc-rank {
    background: none;
    border: none;
    margin-top: -20px;
    margin-left: -120px;
}
#trainercard_form #trainercard_preview {
    border:1px solid #494949;
    background: #fff;
    color: #494949;
}
#field_berries>div {
    background: #494949;
    color: #FFF;
    border-color: #494949;
}
#field_field>div.field>.fieldmon.cormyr {
    animation: none;
    background: rgba(255,255,255,0.75);
}
#field_field>div.field>.fieldmon.goaway {
    background: none;
    animation: none;
}
.party>div>.pkmn:before,
.party>div>.taste,
.party>div>.name img,
.party>div>.extra>.happy,
.party>div>.extra>.nature,
.party>div>.extra img,
.party>div>.action>.berrybuttons>.tooltip_content {
    display: none !important;
}
.party>div>.extra>.menu>.menu_icon,
.party>div>.extra>.menu>.menu_icon:before,
.party>div>.extra>.menu>.menu_icon:after {
    float: right;
    border-color: #494949;
}
.party>div>.menu>label,
.party>div>.menu {
    background: #fff;
    border-color: #494949;
    color: #494949;
}
#aboutbox>div>.menu>label:hover,
#aboutbox>div>.menu>a:hover {
    color: #FFF;
    background: #494949;
}
#aboutbox>div>.menu>label,
#aboutbox>div>.menu>a,
#aboutbox>div>.menu {
    background: #FFF;
    border-color: #494949;
    color: #494949;
}

.party>div>.menu>label:hover {
    background: #494949;
    color: #FFF;
}
#partypage #partybox #profilelinks {
    background: #fff;
    color: #494949;
}
#partybox {
    border-right: none !important;
}
.party>div>.action {
    margin-top: -20px;
    margin-left: 10px;
}
.party>div {
    background: #fff;
    border: none;
    color: #494949;
}
#partypage #tasklistbox #tasklist>li,
#partypage #partybox #profilelinks {
  border-color: #494949;
}
#partypage #friendsbox {
    width: 200px;
}
#partypage #friendsbox #friendslist>li.online_separator {
    font-size: 100%;
    margin-left: 5px;
    text-transform: uppercase;
}
#partypage #friendsbox #friendslist>li {
    margin-left: 20px;
}
.fieldslink,
.party>div {
    border-color: #494949;
    background: #fff;
    color: #494949 !important;
}
.expbar {
    border: none;
    background: #494949;
    color: #fff;
}
.expbar>div {
    background: #F18686 !important;
}
.party.wide {
    width: 320px;
    padding-right: 20px;
}
.mq4 #partypage #partybox #profilelinks+.party.wide {
    width: 640px;
}
.party>div>.action>.berrybuttons[data-up='sour']>[data-berry='aspear'],
.party>div>.action>.berrybuttons[data-up='spicy']>[data-berry='cheri'],
.party>div>.action>.berrybuttons[data-up='dry']>[data-berry='chesto'],
.party>div>.action>.berrybuttons[data-up='sweet']>[data-berry='pecha'],
.party>div>.action>.berrybuttons[data-up='bitter']>[data-berry='rawst'] {
    background: #494949;
}
.party>div>.action>.berrybuttons[data-down='sour']>[data-berry='aspear'],
.party>div>.action>.berrybuttons[data-down='spicy']>[data-berry='cheri'],
.party>div>.action>.berrybuttons[data-down='dry']>[data-berry='chesto'],
.party>div>.action>.berrybuttons[data-down='sweet']>[data-berry='pecha'],
.party>div>.action>.berrybuttons[data-down='bitter']>[data-berry='rawst'] {
    background: none;
}
.party>div>.action>a {
    border-color: #494949;
    background: #fff;
    color: #494949;
}

/***  FARM  ***/

.inventory>div>ul>li>span,
.mq25 .tabbed_interface>ul>li,
.tabbed_interface.horizontal>ul>li {
    border-color: #494949;
}
#notepad_filelist>li>a,
#notepad_filelist>li>span {
    border-color: #494949;
    background: #fff;
    color: #494949;
}
#notepad_filelist>li:hover>a,
#notepad_filelist>li:hover>span,
#notepad_filelist>li>span:hover {
    border-color: #494949;
    background: #494949;
    color: #fff;
}

/***   FORUMS   ***/

.svgicon {
    fill: #494949;
}
.forumpost {
    margin-bottom: 5px;
    color: #494949;
    background: #fff;
    border-color: #494949;
    margin-top: 5px;
}
.forumpost>.metadata,
#fpost_target+.forumpost>.metadata,
#fpost_target+.forumpost>.metadata {
    font-size: 9px;
    background: #fff;
    color: #494949;
    border-color: #494949;
}
.threadmeta>div>.metacmd>.menu>.menu_icon,
.threadmeta>div>.metacmd>.menu>.menu_icon:before,
.threadmeta>div>.metacmd>.menu>.menu_icon:after,
.threadmeta>div>.metacmd>.menu+.threadcommands:before {
    border-color: #494949;
    background: #FFF;
    color: #494949;
}
.threadmeta>div>.metacmd>.menu+.threadcommands>a,
.threadmeta>div>.metacmd>.menu+.threadcommands>label,
.threadmeta>div>.metacmd>.menu+.threadcommands,
.forumpost>.metadata>.menu+.commands,
.forumpost>.metadata>.menu+.commands>a,
.forumpost>.metadata>.menu+.commands>label {
    border-color: #494949;
    background: #FFF;
    color: #494949;
}
.threadmeta>div>.metacmd>.menu+.threadcommands>a:hover,
.threadmeta>div>.metacmd>.menu+.threadcommands>label:hover,
.forumpost>.metadata>.menu+.commands>a:hover,
.forumpost>.metadata>.menu+.commands>label:hover {
    background: #494949;
    color: #FFF;
}
.forumpost>.user>.tooltip_content {
    background: #fff;
    display: none;
    border-right: 1px solid #494949;
}
.forumpost>.user>.tooltip_content hr {
    display: none;
}
.forumpost>.user>.tooltip_content img {
    opacity: 0;
}
.forumpost>.user>.tooltip_content .avatar {
    opacity: 1;
    border-radius: 100%;
    margin: auto auto;
}
.forumpost>.user {
    height: 100%;
    background: #fff;
    border: none;
    font-size: 14px;
}
.pagelist>a {
    color: #494949;
    background: #fff;
    border-color: #494949;
}
.pagelist>a.active {
    color: #fff;
    background: #494949;
    border-color: #494949;
}
.forumpost>.bbcode {
    border-left: 1px solid #494949;
    margin-left: 168px;
}
.forumlist>h3 {
    color: #fff;
    background: #494949;
    border-color: #494949;
}
.forumthreads>ul>li>a,
.forumthreads>ul>li,
.forumlist>ul>li,
.forumlist>ul>li>a,
.forumlist>ul>li>span:last-child,
.forumthreads>ul>li>span:last-child {
    color: #494949;
    background: none;
    border-color: #494949;
}
.forumthreads>ul>li:hover,
.forumlist>ul>li:hover {
    background: none;
}
.forumpost>.signature {
    display: none;
}
.forumthreads>ul>li:hover {
    background: none;
}
.forumthreads>ul>li.new {
    background: none;
    border-color: #F18686;
}
.forumthreads>ul>li>a>span.unread:before {
    border-bottom-color: #F18686;
}
.forumthreads>ul>li>a>span.unread:after {
    border-top-color: #F18686;
}
.forumthreads>ul>li.new>a,
.forumthreads>ul>li.new,
.forumlist>ul>li.new,
.forumlist>ul>li.new>a,
.forumlist>ul>li.new>span:last-child,
.forumthreads>ul>li.new>span:last-child {
    border-color: #F18686;
    border-left-color: #F18686;
}
/***   FIELDS   ***/

#field_party {
    border: none;
    background-color: #FFF;
}
.plateform:before {
    background: none;
    border: 1px solid #494949;
    border-radius: 10px;
    height: 100%;
}
#field_field,
#field_field a {
    background: #494949;
    border-color: #494949;
    color: #FFF
}
#field_field, #field_field a {
    background: none;
    color: #494949;
}
#field_party>div>div.tooltip_content:before {
    border-color: transparent transparent #494949;
}
.tooltip_content .fieldmontip>div.expbar {
    background: #494949;
    border: none;
}
.fieldmontip img {
    display: none;
}
#field_field>.menu {
    background: #fff;
    border-color: #494949;
    color: #494949;
}
#field_field>.menu>label:hover {
    background: #494949;
    color: #FFF;
}
#field_field>.menu>label {
    color: #494949;
}
#field_field>div.fielddata>.menu>.menu_icon,
#field_field>div.fielddata>.menu>.menu_icon:before,
#field_field>div.fielddata>.menu>.menu_icon:after {
    background: #494949 !important;
    border-color: #494949;
}
.fieldmontip>div.pkmn:before {
    display: none;
}

/***   SHELTER, FORGE, SCOUR   ***/

#shelterpage #shelter,
#shelterpage #shelter #shelterarea,
#shelterpage #shelter #sheltercommands,
#shelterpage #shelter #shelterarea>.tooltip_content,
#shelterpage #shelter #shelterarea>.tooltip_content .buttons:after {
    background: #FFF;
    color: #494949;
    border-color: #494949;
}
.tooltip_item>.name>.image {
    background-color: #FFF;
}
#newmission {
    background: #494949;
    border: none;
}
.badgelist>li,
#scoretable>div.total,
#scoretable {
  border-color: #494949;
}
#scoretable>div:hover {
    background-color: #494949;
    color: #FFF
}

/***   SUMMARY PAGE   ***/

#portrait,
#summary_col2,
#summary_advertisement {
    display: none;
}

/***   POKÃ‰DEX   ***/

#dexlinks {
    background: #494949;
    color: #FFF;
    border-color: #494949;
}
#dexlinks a {
    color: #FFF;
}
#regionlist>.panel .flag>img {
    display: none;
}
#regionlist>.panel .regionprogress {
    background: #FFF;
}

/***   MESSAGES   ***/

#pmconvolist>div.head>span {
    background: #494949;
    color: #FFF;
}
#pmconvolist>div>a,
#pmconvolist>div>span {
    background: none;
    border-color: #494949;
    color: #494949;
}
#pmconvolist>div:hover>a,
#pmconvolist>div:hover>span {
    background-color: #494949;
    border-color: #494949;
    color: #FFF;
}
#pmconvolist>div>a>span.replied:before {
    border-right-color: #F18686;
}
#pmconvolist>div>a>span.replied:after {
    border-color: #F18686;
}
#pmconvolist>div>a>span.unread:before {
    border-bottom-color: #F18686;
}
#pmconvolist>div>a>span.unread:after {
    border-top-color: #F18686;
}
/***   ONLINE   ***/

#useronlinelist>div.head>a,
#useronlinelist>div.head>span,
#useronlinelist>div.body>div>a,
#useronlinelist>div.body>div>span {
    background-color: #FFF;
    border: 1px solid #494949;
    color: #494949;
}
#useronlinelist>div.body>div:hover>a,
#useronlinelist>div.body>div:hover>span {
    background-color: #494949;
    border-color: #494949;
    color: #FFF;
}

}
