css:
@-moz-document domain("gta5-mods.com") {
.header {
    background: rgba(186, 32, 32, 0.85);
}
.header a.header-link.active,
.header a.header-link:hover {
    background-color: #900C06;
}
a {
    color: #BA2020;
    text-decoration: none;
}
.header .list-dropdown-menu li a:hover {
    background-color: #9F0A0A;
    color: #fff;
}
.header .notification-dropdown-menu a {
    color: #BA4520 !important;
}
.header .search-dropdown-menu .search-button {
    background: #BA2020;
}
.label-border {
    background: #BA2020;
}
.btn-primary {
    color: #fff;
    background-color: #BA2020;
    border-color: #A4291C;
}
#navigation li a:hover .label-border,
#navigation li.active .label-border {
    background: #BA2720;
}
.btn.btn-o-primary {
    border-color: #BA2020;
    color: #FF0000;
}
.btn.btn-o-info {
    border-color: #DEA45B;
    color: #CA6D09;
}
.user-panel .username {
    color: #BA3720;
    font-size: 18px;
    line-height: 18px;
    vertical-align: middle;
}
.btn-primary:hover {
    color: #fff;
    background-color: #8E1919;
    border-color: #701313;
}
.btn.btn-o-primary:hover {
    border-color: #BA2020;
    color: #BA2020;
}
.btn.btn-o-info:focus,
.btn.btn-o-info:active,
.btn.btn-o-info:hover {
    border-color: #D6DE5B;
    color: #DED55B;
}
a:hover,
a:focus {
    color: #791515;
    text-decoration: underline;
}
a.thumbnail:hover,
a.thumbnail:focus,
a.thumbnail.active {
    border-color: #BA2020;
}
#comments .media .media-details a:hover {
    color: #BA2020;
}
.form-control:focus {
    border-color: #BA2020;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(186, 32, 32, 0.6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(186, 32, 32, 0.6);
}
.star-rating .fa,
span.comment-rating .fa,
span.comment-average-rating .fa {
    color: red;
}
#profile .user-stats .stat-icon {
    display: block;
    font-size: 36px;
    text-align: center;
    color: #FF0000;
}
.header .notification-dropdown-menu a.view-all:hover {
    text-decoration: none;
    background: #BA2020;
    color: #fff !important;
}
#settings .nav-pills>li.active a {
    border-color: #BA2020;
    background: #BA2020 !important;
}
#settings .nav-pills>li a:hover,
#settings .nav-pills>li a:focus {
    border-color: #BA2020;
    background: none;
}
#settings .avatar-list li a:hover {
    border-color: #BA2020;
}
#comments .media .media-heading .pinned-message {
    font-weight: normal;
    font-style: italic;
    color: #BA2020;
}
#comments .media.pinned .panel,
#comments .media.active .panel {
    border-color: #BA2020;
}
.btn.btn-o-primary.active,
.btn.btn-o-primary.active:hover,
.btn.btn-o-primary.active:focus {
    border-color: #D82525;
    background-color: #D82525;
    color: #fff;
}
.btn.btn-o-primary:focus,
.btn.btn-o-primary:active,
.btn.btn-o-primary:hover {
    border-color: #BA2020;
    color: #BA2020;
}
.btn.btn-o-info:focus,
.btn.btn-o-info:active,
.btn.btn-o-info:hover {
    border-color: #DEAA5B;
    color: #DE925B;
}
.btn.btn-o-info.active,
.btn.btn-o-info.active:hover,
.btn.btn-o-info.active:focus {
    border-color: #E4AF79;
    background-color: #E4AF79;
    color: #fff;
}
.pagination>.active>a,
.pagination>.active>a:hover,
.pagination>.active>a:focus,
.pagination>.active>span,
.pagination>.active>span:hover,
.pagination>.active>span:focus {
    background-color: #BA2020;
    border-color: #BA2E20;
    cursor: default;
}
.pagination>li>a,
.pagination>li>span {
    color: #BA2020;
}
.pagination>li>a:hover,
.pagination>li>a:focus,
.pagination>li>span:hover,
.pagination>li>span:focus {
    z-index: 2;
    color: #791515;
    background-color: #eeeeee;
    border-color: #ddd;
}
.header .search-dropdown-menu .search-background input {
    background: none;
    border: 0;
    outline: none;
    width: 94%;
    height: 40px;
    font-size: 16px;
    color: #8E1919;
}
.label-primary {
    background-color: #BA2020;
}
.btn-primary:active,
.btn-primary.active,
.open>.btn-primary.dropdown-toggle {
    color: #fff;
    background-color: #8E1919;
    border-color: #701313;
}
.user-panel .user-social a:hover {
    color: #BA2020;
}
a {
    color: #BA2020;
    text-decoration: none;
}
#featured ul li a:hover {
    border-color: #BA2020;
}
#featured .main:hover {
    border-color: #BA2020;
}
}
