@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.messenger.com") {
  @keyframes FadeIn {
    0% {opacity: 0;}
    100% {}
  }
  
  body {
    overflow: hidden !important;
  }
  
  ._4eby {
    position: fixed;
    left: calc(50% - 200px);
    top: calc(50% - 200px);
    width: 400px;
    height: 400px;
    border-radius: 0px !important;
    animation-name: FadeIn;
    animation-duration: 1s;
  }
  
  ._58al:focus {
    color: black !important;
  }
  
  ._s15 {
    font-size: 0px !important;
    z-index: 0 !important;
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: URL("/*[[imageurl1]]*/") !important;
    background-attachment: fixed;
    background-size: auto 125%;
    background-position: center center;
    background-repeat: no-repeat !important;
    opacity: 1 !important;
    transform: scale(1.1);
  }
  
  ._s15 * {
    visibility: hidden !important;
  }
  
  ._4sp8 {
    top: 0px !important;
    margin-top: 0px !important;
    height: 100% !important;
  }
  
  ._48e9 li {
    height: 40px !important;
    overflow: hidden !important;
    background-color: transparent !important;
  }
  
  ._48e9 li div a div div div div {
    transform: scale(0.5);
  }
  
  ._4ld_ {
    visibility: hidden !important;
  }
  
  ._5f0v {
    background-color: rgba(255,255,255,0.3);
  }
  
  ._4t9y {
    background-color: rgba(255,255,255,0.3);
  }
  
  ._1htf span {
    color: black !important;
  }
  
  ._3058 {
    background-color: rgba(255,255,255,0.5);
    margin-left: 10px;
  }
  
  ._hh7 {
    background-color: transparent !important;
    color: black !important;
    border-color: rgba(0,0,0,0.25);
    border-width: 1px;
    border-style: solid;
    display: block;
  }
  
  ._hh7::before {
    background-image: URL("/*[[imageurl1]]*/") !important;
    background-size: auto 137%;
    background-position: center center;
    background-attachment: fixed;
    background-repeat: no-repeat !important;
    width: 100%;
    height: 100%;
    left: 0px;
    top: 0px;
    content: "";
    position: absolute;
    filter: blur(2px) brightness(500%) contrast(20%);
    border-radius: inherit;
    z-index: 1 !important;
  }
  
  ._3oh- a {
    color: blue !important;
  }
  
  ._hh7 span {
    z-index: 9999 !important;
    color: black !important;
    display: block;
    position: relative !important;
  }
  
  ._3m31 {
    border-radius: 50%;
    transition: border-radius 0.25s, opacity 0.5s, transform 0.25s, filter 0.25s;
    opacity: 0.5;
    filter: blur(1px);
    transform: scale(0.75);
  }
  
  ._3m31:hover {
    opacity: 1;
    border-radius: 0px;
    transform: scale(2);
    z-index: 10000;
    border-radius: 0px;
    filter: blur(0px);
    position: sticky !important;
  }
  
  ._15gf {
    background-color: transparent;
  }
  
  ._4u-c {
    padding-left: 1px !important;
  }
  
  ._5742 {
    background-color: rgba(255,255,255,0.5);
    margin-bottom: 1px;
    margin-left: 1px;
  }
  
  div[role*="banner"] {
    background-color: rgba(255,255,255,0.5);
    z-index: 1 !important;
    margin-bottom: 1px !important;
    position: relative !important;
  }
  
  ._30yy {
    filter: brightness(130%);
  }
  
  ._4rv3 {
    background-color: rgba(255,255,255, 0.3);
    margin-top: 1px;
    margin-left: 1px;
  }
  
  i[alt*="Camera"] {
    background-color: transparent !important;
  }
  
  ._4q61 {
    background-color: transparent !important;
  }
  ._2y8y {
    background-color: rgb(255,255,255);
    margin-left: 1px;
  }
  
  ._1ut7 {
    background-color: rgba(255,255,255,0.3);
    margin-left: 1px;
  }
  
  ._5l37 {
    background-color: transparent !important;
  }
  
  ._4ldz {
    margin-left: 10px;
  }
  
  ._11_d {
    background-color: transparent !important;
  }
  
  ._58al {
    margin: 0px !important;
    width: 110% !important;
    background-color: rgba(255,255,255,0.1) !important;
    color: white !important;
  }
  
  ._5iwm {
    padding: 0px !important;
    width: 100% !important;
  }
  
  ._1ht2 {
    background-color: rgba(255,255,255,0.5) !important;
  }
}
