css:
@-moz-document domain("mpcforum.pl") {
/* online */
.ipsBadge_green { background: #33ff00; }
.ipsBadge_purple { background: #993299; }
.ipsBadge_grey { background: #999999; }
.ipsBadge_lightgrey { background: #DBDBDB; }
.ipsBadge_orange { background: #FFB732; }
.ipsBadge_red { background: #FF3232; }
.ipsBadge.has_icon.ipsBadge_green { background-color: #33ff00; }
.ipsBadge_lightgrey { background-color: #33ff00; }

/* strony */
.pagination .pages li.active {
background: #CC201F url('http://www.mpcforum.pl/public/style_images/def342/highlight.png') repeat-x 0 0;
color: #fff;
padding: 1px 8px;
text-shadow: rgba(0,0,0,0.2) 0px -1px 0px;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
border-radius: 5px;
cursor: default;
}

/* tlo strony */
#ipboard_body {  
background-image: url(http://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-1946.png);
/* efekt paralax: */
min-height: 100%;
background-size: cover;
background-attachment: fixed;
background-repeat: no-repeat;
background-position: center center;
}

#content {
opacity: 0.95
}

#footer_utilities {
opacity: 0.95
}

/* obsługa zbanowanych */
td.col_f_post > ul
li {
color: black;
}

div.post_username a:link,hover,active,visited{
color: white;
font-weight: bold;
}
div.snowCanvas
{
     display:none
}
.post_username{
float: left;
min-width: 177px;
font-weight: normal;
color: black;
}


/* nawigacja */
#secondary_navigation a{
color: #D8941A;
line-height: 37px;
height: 37px;
}
#secondary_navigation a:hover{ color: white; }
#secondary_navigation {
     background-image: url(http://wallpapercave.com/wp/exR7dL9.png); border-radius: 5px
}

/* kolor tekstu */
a {
color: #D8941A;
}
a:hover { color: #34D491; }

#index_stats a{ color: silver; }
#index_stats a:hover{ color: white; }

/* reputacja */
.maintitle {
background: #34D491;
color: white;
text-shadow: rgba(0,0,0,0.25) 0px -1px 0px;
padding: 12px;
font-size: 12px;
font-weight: 300;
-moz-border-radius: 6px;
-webkit-border-radius: 6px;
border-radius: 6px;
overflow: hidden;
margin-bottom: 4px;
-webkit-box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
-moz-box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
     text-align: center
}
.reputation.positive, .members li.positive {
background: #34D491 url('http://www.mpcforum.pl/public/style_images/def342/highlight_faint.png') repeat-x 0 0; border-radius: 180px
}
       
.reputation.negative, .members li.negative {
background: #34D491 url('http://www.mpcforum.pl/public/style_images/def342/highlight_faint.png') repeat-x 0 0; border-radius: 180px
}
       
.reputation.positive, .reputation.negative {
color: #fff;
}
       
.reputation.zero {
background: #505050 url('http://www.mpcforum.pl/public/style_images/def342/highlight_faint.png') repeat-x 0 0; border-radius: 180px;
color: #fff;
}

/* wyszukiwarka */
#search .submit_input {
background: #34D491 url('http://www.mpcforum.pl/public/style_images/def342/search_icon.png') no-repeat 10%;
text-indent: -3000em;
padding: 0; border: 0;
display: block;
width: 26px;
height: 26px;
position: absolute;
right: 0; top: 0; bottom: 0;
-moz-border-radius: 0 5px 5px 0;
-webkit-border-top-right-radius: 5px;
-webkit-border-bottom-right-radius: 5px;
border-radius: 0 5px 5px 0;
-webkit-box-shadow: inset 0px 1px 0 rgba(255,255,255,0.2);
-moz-box-shadow: inset 0px 1px 0 rgba(255,255,255,0.2);
box-shadow: inset 0px 1px 0 rgba(255,255,255,0.2);
}
 
#search .submit_input:hover{ background-color: #34D491; }
		
/* buttony */
.topic_buttons li a, .topic_buttons li span, .ipsButton {
background: #CC201F;
-moz-border-radius: 5px;
-webkit-border-radius: 5px;
border-radius: 5px;
color: #fff;
text-shadow: 0 -1px 0 rgba(0,0,0,0.2);
font-size: 12px;
font-weight: bold;
line-height: 34px;
height: 34px;
padding: 0 20px;
text-align: center;
min-width: 70px;
display: inline-block;
cursor: pointer;
-webkit-box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
-moz-box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
box-shadow: inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
}
.topic_buttons li a:hover, .ipsButton:hover { background-color: #FF2827; color: #fff; }
		
/* posty */
ul.post_controls a {  
color: #fff;
text-shadow: rgba(0,0,0,0.2) 0px -1px 0px;
background: #CC201F;
-webkit-border-radius: 4px;
-moz-border-radius: 4px;
border-radius: 4px;
height: 26px;
line-height: 26px;
padding: 0 10px;
text-decoration: none;
margin-left: 4px;
display: block;
}

ul.post_controls a:hover { background-color: #FF2827; color: #fff; }

/* lajkacz */
.ipsLikeButton.ipsLikeButton_enabled {
background: #34D491;
}

/* dodawanie postów */
.input_submit {
background: #34D491 url('http://www.mpcforum.pl/public/style_images/def342/highlight_faint.png') repeat-x 0 0;
border-color: #3E3E40;
color: #fff;
text-shadow: rgba(0,0,0,0.4) 0px -1px 0px;
-moz-box-shadow: rgba(0,0,0,0.15) 0px 1px 3px, inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
-webkit-box-shadow: rgba(0,0,0,0.15) 0px 1px 3px, inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
box-shadow: rgba(0,0,0,0.15) 0px 1px 3px, inset rgba(255,255,255,0.1) 0px 0px 0px 1px;
-moz-border-radius: 4px;
-webkit-border-radius: 4px;
border-radius: 4px;
}
 
.input_submit:hover {
background-color: #34D491;
color: #fff;
}

/* popup */
.message {
background: #34D491 url('http://www.mpcforum.pl/public/style_images/def342/highlight_faint.png') repeat-x 0 -1px;
padding: 10px;
border: 1px solid #FF2827;
text-shadow: rgba(0,0,0,0.2) 0px -1px 0px;
line-height: 1.6;
font-size: 12px;
-webkit-box-shadow: rgba(0,0,0,0.1) 0px 1px 3px;
-moz-box-shadow: rgba(0,0,0,0.1) 0px 1px 3px;
box-shadow: rgba(0,0,0,0.1) 0px 1px 3px;
}
.desc
{
     color: #B9B9B9
}
.ipsType_small
{
     font-size: 10px
}
table.ipb_table tr td
{
     background-image: url(http://wallpapercave.com/wp/exR7dL9.png); border-radius: 5px;
}
#top_posters li{
     background-image: url(http://wallpapercave.com/wp/exR7dL9.png); border-radius: 5px;
}
.ipsSideBlock {
     background-image: url(http://wallpapercave.com/wp/exR7dL9.png); border-radius: 5px;
}
.ipsList_withminiphoto li {
     background-image: url(http://wallpapercave.com/wp/exR7dL9.png); border-radius: 5px; text-align: center
}
.message.unspecific{
     background-image: url(http://wallpapercave.com/wp/exR7dL9.png); border-radius: 5px;
                                                       text-align: center
}
.ipsBox{
     border-radius: 5px;
}
  #board_statistics{
    display:none;
  }
  .statistics{
    display:none;
  }
#branding
{
     opacity: 0.5;
}
img {border-radius: 60px}
}
