css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com"),domain("www.youtube.com") {
*[dir=rtl],
html[dir=rtl] > body,
html, a, li, h1{font-family: "Droid Arabic Naskh", sans-serif !important;}
.yt-lockup-meta-info>li {font-weight: bold !important; font-size: 16px !important; color: #333 !important;}
.yt-lockup-meta-info>li:last-child {font-size: 11px !important; color: #999 !important;}
#watch7-sidebar-contents  {margin-top: 400px !Important;}
#watch7-sidebar-discussion, #watch-sidebar-spacer, #watch7-sidebar, #head  {display: none !Important;}
#placeholder-player, #player-api {width: 100% !important; max-width: 640px !important; margin: auto !important; height: 400px !important; margin-left: 160px !important;}
#yt-masthead-container { padding-top: 1px !Important; padding-bottom: 1px !Important;}
.appbar-hidden #masthead-positioner-height-offset {height: 38px !Important;}
#watch7-main  {margin-top: 15px !Important;}
#player-playlist .watch-playlist { margin-top: 420px !Important; left: 540px !Important; margin-left: 100px !Important;}
#watch7-sidebar-ads, #ad_creative_1, .ad-div, .pyv-afc-ads-container, .ad-interstitial-showing .video-ads, .ad-showing .video-ads { display:none!important; visibility:hidden!important; } 
.yt-thumb-clip img  {width: 180px !Important;}
.yt-thumb-72  {width: 180px !Important;}
.pl-video-title-link, .comment-text-content {font-size: 13px !Important; text-align: right !Important;}
.playlist-videos-list {top: 1px !Important;}
.playlist-header, .player-unavailable, .ytp-error {display: none !Important;}
#gh-banner, #channel-subheader, #pl-header {display: none !Important;}
.content-alignment  {width: 1000px !Important;}
.yt-uix-tile-link {margin-right: 2px !Important;}
.yt-shelf-grid-item {margin-right: 2px !Important; width: 240px !Important;}
.yt-lockup-thumbnail {float: right !Important; margin-right: 1px !Important; margin-left: 5px !Important;}
.yt-lockup-content {float: right !Important; text-align: right !Important;}
.yt-lockup-description {max-width: 700px !Important; text-align: right !Important;}
.yt-lockup-title {text-align: right !Important; font-weight: bold !important; max-width: 700px !important; margin-bottom: 10px !important;}
.branded-page-v2-secondary-col, .yt-lockup-action-menu, .shelf-title-table {display: none !Important;}
.yt-uix-tile {cursor: default !Important;}
.yt-ui-ellipsis-2 {max-height: 4em !Important;}
.yt-lockup-title.contains-action-menu {margin-right: 2px !Important;}
.yt-lockup-meta {max-width: 240px !Important;}
}
