css:
@-moz-document domain("digg.com") {
*[dir=rtl], div[dir=rtl], span[dir=rtl],
 .feeditem-headline, .detail-body {font-family: "Droid Arabic Naskh", sans-serif !important;
font-size:18px !important; direction: rtl !Important;}
.col-left {width: 18% !Important;}
.col-right {margin-left: 200px !Important;}
.story-actions, .story-actions-container, #dr-nav-saved, #dr-nav-diggs, #dr-nav-digg-deeper {display: none !Important;}
.story-metadata {width: 40px !Important; padding-right: 2px !Important;}
.feeditem-read, .feeditem-headline {float: right !Important; }
.feeditem-feedtitle {float: left !Important; width: 80px !Important;}
.story-data {padding-right: 2px !Important; padding-left: 2px !Important;}
.story-detail-content  {width: 720px !Important;}
.detail-body img {max-width: none !Important;}
}
