css:
@-moz-document domain(trello.com) {

.window a {
    color: #D0D0D0;
}

.list,
div.window {
    background: #292929;
    color: white;
}

.list {
    background: #333;
}

.list-card {
    background-color: #191919;
    border-bottom-color: #000;
}

.list-card.active-card {
    background-color: #282828;
    border-bottom-color: #242424;
}

.list-card a {
    color: #CCC;
}

.list-card:hover a,
.list-card.active-card a {
    color: #eee;
}

.list-card-operation {
    background-color: #000 !important;
    color: #FFF !important;
}

.list-card.is-stickered .list-card-details {
    background-color: rgba(25, 25, 25, 0.7);
}

.list-card.active-card.is-stickered .list-card-details {
    background-color: rgba(40, 40, 40, 0.7);
}

.window .button-link {
    background: #191919;
    border-color: #000;
    color: #969696;
}

.window .button-link:hover {
    color: #fff;
}

.window .button-link:hover {
    background: #023F73;
    border-color: #001F31;
}

.window hr {
    background: #464646;
    color: #464646;
    margin: 15pt 0;
}

.window .phenom hr {
	background: #737373;
	color: #737373;
}

.u-fancy-scrollbar::-webkit-scrollbar-track-piece {
    background: #252525;
}

.window .org-export-list-item,
.window .phenom {
    border-bottom: 1px solid rgba(0, 0, 0, 0.21);
}

.window .action-comment {
    background-color: rgba(255, 255, 255, 0.13);
}

.window blockquote {
    border-left-color: #888;
    color: #BFBFBF;
}

.checklist-progress-bar {
    background: #2F4550;
}

.checklist-progress-bar-current {
    background: #00ABFF;
}

.checklist-item:hover {
    background: rgba(0,0,0,0.15);
}

.checklist-item.placeholder {
    background: rgba(0, 0, 0, 0.4);
}

.checklist-item.ui-sortable-helper {
    background: #131313;
}

.checklist-item-checkbox {
    -webkit-filter: invert(100%);
}

.attachment-thumbnail-preview,
.attachment-image-preview {
    border-color: #000;
}

.attachment-thumbnail-details-title {
    color: #dadada;
}

.attachment-thumbnail:hover .attachment-thumbnail-details {
    background-color: #424242;
}

.attachment-thumbnail:hover .attachment-thumbnail-preview {
    border-color: #000;
}

.attachment-image-preview:hover {
    border-color: #3A3A3A;
}

.embed {
    background-color: #1D1D1D;
}

.dark-hover:hover .icon-lg,
.dark-hover:hover .icon-sm,
.icon-lg.dark,
.icon-lg.dark-hover:hover,
.icon-sm.dark,
.icon-sm.dark-hover:hover {
    color: white;
}

.open-card-composer:hover {
    background-color: #232323;
    color: #DCDCDC;
}

textarea.list-card-composer-textarea,
textarea.list-card-composer-textarea:focus,
.window-main-col .edit textarea.field,
.editing .edit .field,
.editing .edit .field:active {
    color: #ccc;
}

.helper {
    background-color: rgba(0, 0, 0, 0.34);
}

div.window .quiet a:hover {
    color: #FBFBFB;
}

div.window .quiet-button:hover {
    background: rgba(255, 255, 255, 0.12);
    color: #ECECEC;
}

.window .atMention.me {
    color: #79CEFF;
}

.window .known-service-link {
    background: #313131;
}

.known-service-icon {
    margin-bottom: 2px;
}

.description-content-fade-button {
    background: linear-gradient(to bottom, rgba(41, 41, 41, 0), rgba(41, 41, 41, 0) 370px, #292929 400px);
}

.description-content-fade-button:hover {
    background: linear-gradient(to bottom, rgba(41, 41, 41, 0), rgba(41, 41, 41, 0) 385px, #292929 415px);
}

.description-content-fade-button-text {
    background: #292929 !important;
}

.description-content-fade-button:hover .description-content-fade-button-text {
    color: #ccc;
}

.list-wrapper.mod-add {
    background-color: #333;
}

.list-wrapper.mod-add .list-name-input,
.list-wrapper textarea.field.single-line {
    background: #191919;
    border-color: #191919;
}

.dark-background-hover:hover .icon-lg, 
.dark-background-hover:hover .icon-sm, 
.icon-lg.dark-background-hover:hover, 
.icon-sm.dark-background-hover:hover,
.checklist-add-controls-option:hover,
.window .edit-controls .option:hover,
.list-header-extras-menu:hover,
.list-header-menu-icon:hover {
	background: rgba(255, 255, 255, 0.12);
    color: white;
}

.window-cover {
	background-color: #383838;
}

.window blockquote {
    color: #E2E2E2;
}

.u-fancy-scrollbar::-webkit-scrollbar-track-piece {
    background: rgba(0, 0, 0, 0.1);
}

.u-fancy-scrollbar::-webkit-scrollbar-thumb:horizontal,
.u-fancy-scrollbar::-webkit-scrollbar-thumb:vertical {
    background: rgba(0, 0, 0, 0.35);
}

.window-archive-banner {
    background-color: #525044;
}

.window-archive-banner-text {
    color: #B9B9B9;
}

.window code {
    background-color: #272727;
    border: 1px solid #2D2D2D;
}

.window .description-content code {
    background-color: #171717;
    border: 1px solid #171717;
}

.window pre {
	background-color: #404040;
    border-color: #565656;
    color: #FFF;
}

.window .phenom-desc pre {
    background-color: #353535;
/*     background: #272727; */
    background: #303030;
}

.phenom.mod-highlighted {
	background: #3F464A;
}

.window pre code,
.window .description-content pre code {
    background: none;
    border: none;
    color: #FFF;
}

.window pre::-webkit-scrollbar {
	height: 9px;
	width: 9px;
}
.window pre::-webkit-scrollbar-track-piece {
    background: rgba(255,255,255, 0.2);
}
.window pre::-webkit-scrollbar-thumb {
    background: rgba(0, 0, 0, 0.7);
    border-radius: 3px;
}

.window .card-detail-item-add-button {
    background: rgba(11, 11, 11, 0.55);
}

.window .card-detail-item-add-button:hover {
    background: rgba(11, 11, 11, 0.35);
}

.window .button.disabled,
.window .button.disabled:active,
.window .button.disabled:hover,
.window button.disabled,
.window button.disabled:active,
.window button.disabled:hover,
.window input[type=button].disabled,
.window input[type=button].disabled:active,
.window input[type=button].disabled:hover,
.window input[type=submit].disabled,
.window input[type=submit].disabled:active,
.window input[type=submit].disabled:hover,
.window input[type=submit]:disabled,
.window input[type=submit]:disabled:hover {
    background: #424242;
    color: #949494;
}

.window .phenom.unread {
	background: #494E50;
}

.card-detail-badge {
	background-color: #464646;
}
.card-detail-badge.is-clickable:hover {
	background: #545454;
	color: #D2D2D2;
}

.window .u-bottom .show-more:hover {
	background: #1B1B1B;
}

.badge.is-voted {
	background-color: #444;
	color: #AFAFAF;
}

.card-detail-badge.is-voted {
	background-color: #5F5F5F;
}

textarea.mod-list-name,
textarea.mod-card-back-title {
	color: #FFF;
}

textarea.mod-card-back-title:disabled, 
textarea.mod-list-name:disabled {
	color: #E6E6E6;
	-webkit-text-fill-color: #E6E6E6;
}

textarea.mod-card-back-title.is-editing, 
textarea.mod-card-back-title:focus,
textarea.mod-list-name.is-editing,
textarea.mod-list-name:focus {
	background-color: rgba(255, 255, 255, 0.15);
}
textarea.mod-list-name.is-editing,
textarea.mod-list-name:focus {
	background-color: rgba(255, 255, 255, 0.2);
}

}
