@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.neopets.com/){


/*
  * Theme-Specific Style Sheet
  *
  * Theme: 20
  * 	Neopets Default
  * 	Black, yellow, and all around Neopets goodness
*/


/********************************************************************/
/* HTML Tag Styles **************************************************/
/********************************************************************/

	BODY {
		background-color: #CFCFCF !important;
	}

	A:Link, A:Visited {
		color: #2E72C0 !important;
	}

	A:Hover {
		color: #FFB300 !important;
	}

/********************************************************************/
/* Foundation IDs ***************************************************/
/********************************************************************/

	#main {
		border-left: 2px solid #747474 !important;
		border-right: 2px solid #747474 !important;
	}

	#header {
		background-color: #150810 !important;
		background-image: url("http://images.neopets.com/themes/020_ppl_3c22d/header_bg.png") !important;
	}

	#footer {
		background-color: #7DB365 !important;
		background-image: url("http://images.neopets.com/themes/020_ppl_3c22d/footer_bg.png") !important;
	}


/********************************************************************/
/* Header Stuff *****************************************************/
/********************************************************************/

/* Navigation.  Duh.  */
	ul.dropdown {
		border: 1px solid #64C702 !important;
		background-color: #333333 !important;
	}

	LI:hover UL.dropdown A, LI.over UL.dropdown A {
		color: #FFFFFF !important;
	}

	LI:hover UL.dropdown A:Hover, LI.over UL.dropdown A:hover {
		color: #FFFF00 !important;
	}

/* Event Icons */
	.eventIcon, .user {
		color: #000000 !important;
	}

	.eventIcon A:Link, .eventIcon A:Hover, .eventIcon A:Visited,
	.user A:Link, .user A:Hover, .user A:Visited {
		color: #2E72C0 !important;
	}

/* Clock */
	#nst {
		color: #FFFFFF !important;
	}


/********************************************************************/
/* Sidebar Classes **************************************************/
/********************************************************************/

	.sidebarModule {
		border: 2px solid #E4E4E4 !important;
	}

	.sidebarTable {
		border: 2px solid #C9C9C9 !important;
	}

	.sidebarHeader {
		background-color: #004364 !important;
		border-bottom: 2px solid #C9C9C9 !important;
		color: #ffffff !important;
	}

	.sidebarHeader A:Link, .sidebarHeader A:Hover, .sidebarHeader A:Visited{
		color: #feee30 !important;
	}


/********************************************************************/
/* Content Classes **************************************************/
/********************************************************************/

/* We need a few styles to make a module work */
	.contentModuleHeader {
		background-color: #000000 !important;
		color: #FFFFFF !important;
	}

	.contentModuleHeader A:Link, .contentModuleHeader A:Hover, .contentModuleHeader A:Visited {
		color: #C5C5C5 !important;
	}

	.moreLink {
		border: 1px solid #8F8F8F !important;
	}

	.contentModuleHeaderAlt {
		background-color: #005b88 !important;
		color: #FFFFFF !important;
	}

	.contentModuleHeaderAlt A:Link, .contentModuleHeaderAlt A:Hover, .contentModuleHeaderAlt A:Visited {
		color: #B3CACE !important;
	}

	.moreLinkAlt {
		border: 1px solid #629199 !important;
	}


/********************************************************************/
/* Footer Classes ***************************************************/
/********************************************************************/

	.footerForm {
		color: #000000 !important;
	}

	.copyright, .copyright A:Link, .copyright A:Hover, .copyright A:Visited {
		color: #000000 !important;
	}


/********************************************************************/
/* Misc Classes of Doom *********************************************/
/********************************************************************/

/* Ad banners.  No comment */
	#ban {
		background-color: #747474 !important;
		background-image: url("http://images.neopets.com/themes/020_ppl_3c22d/banner_bg.png") !important;
	}

	.marqueeSlot, .marqueeSlot A:Link, .marqueeSlot A:Hover, .marqueeSlot A:Visited {
		background-color: #005b88 !important;
		color: #FFFFFF !important;
	}

	.marqueeSlotOn, .marqueeSlotOn A:Link, .marqueeSlotOn A:Hover, .marqueeSlotOn A:Visited {
		background-color: #01354f !important;
		color: #FFFFFF !important;
	}
}
