@-moz-document regexp("https?:\\/\\/(?!www.)pastebin.com\\/(?:(?!signup|login|message|alerts|settings|trends|api|tools|faq|search|password|pro|change_avatar)\\w)+\\b") {
/* ↓----------------------[ background color ]----------------------↓ */

#content_frame {background-color: #E1E1E1;}
body {background-color: #F7F7F7;}

/* ↑----------------------[ background color ]----------------------↑ */
/* ↓----------------------[ main ]----------------------↓ */

#code_buttons > span.go_right > a:nth-child(1) {
    margin-left: 0pt!IMPORTANT;}
.go_right {float: left;}
#code_buttons {
    text-align: right;
    padding: 6px 8px 1px;}
#code_frame2 {
    border-top: 1px solid #DDD!IMPORTANT;
    border-bottom: 1px solid #DDD!IMPORTANT;
    border: 0; border-radius: 0;}
a.buttonsm {
    font-size: 15pt!IMPORTANT;
    margin-left: 20pt!IMPORTANT;
    margin-top: 5px!IMPORTANT;}
.buttonsm {
    background-color: transparent;
    border-bottom: 0;
    border-radius: 0;
    height: 26px;}
.buttonsm:hover {
    color: black;
    height: 26px;}
.buttonsm:after {
    height: 9px;
    width: 0;
    content: '';
    display: block;
    border-bottom: 1px solid grey!IMPORTANT;
	-webkit-transition: 0.4s ease-in, 0.2s ease-out;
       -moz-transition: 0.4s ease-in, 0.2s ease-out;
        -ms-transition: 0.4s ease-in, 0.2s ease-out;
    	 -o-transition: 0.4s ease-in, 0.2s ease-out;
    		transition: 0.4s ease-in, 0.2s ease-out;}
.buttonsm:hover:after {width: 100%;}

/* ↑----------------------[ main ]----------------------↑ */
/* ↓----------------------[ author's info ]----------------------↓ */

.paste_box_icon, .form_avatar {
    border: 0!IMPORTANT;}
div.paste_box_frame {
    padding-left: 7px!IMPORTANT;
    margin-top: -6px!IMPORTANT;}
.paste_box_info {
    padding-left: 12px!IMPORTANT;
    padding-top: 4px!IMPORTANT;}
.i_pm {
    width: 20px!IMPORTANT;
    padding-top: 0!IMPORTANT;}
img[src="/i/t.gif"] {
    margin-top: 2px!IMPORTANT;
    padding-left: 9px!IMPORTANT;}

/* ↑----------------------[ author's info ]----------------------↑ */
/* ↓----------------------[ header title ]----------------------↓ */

#header {right: 7px!IMPORTANT;}
#header, #header_top {
    margin-top: 1px!IMPORTANT;
    background: transparent;
    box-shadow: none;
    color: black;}

/* ↑----------------------[ header title ]----------------------↑ */
/* ↓----------------------[ removal segment ]----------------------↓ */
	/* ----↓ option for author's info ↓---- */

/*[[raw-code]]*/.banner_728, #steadfast, iframe[id="tfasyncframe_3"], iframe[id="tfasyncframe_2"], .banner_728, #steadfast, div.content_title.no_border, textarea.paste_code, #notice, #content_right, #abrpm, #b_facebook2, #b_twitter2, #footer_links, div.h_1024, #mid_footer, #menu, #titlebar, br, #notice, #header_members, div#content_right , #header_new_paste, #header_search, #header_links, .form_text, .tools_left {display: none!IMPORTANT;}

	/* ----↑ option for author's info ↑---- */
/* ↑----------------------[ removal segment ]----------------------↑ */
/* ↓----------------------[ return to top ]----------------------↓ */

.cd-top {width: 60px!IMPORTANT;}

/* ↑----------------------[ return to top ]----------------------↑ */
/* ↓----------------------[ body ]----------------------↓ */

* {
    width: auto!IMPORTANT;
    margin: auto!IMPORTANT;}

/* ↑----------------------[ body ]----------------------↑ */
}
