css:
@-moz-document domain("exalted.guildlaunch.com") {
.primaryContentAreaContainerDiv{background-image:none;}
#lbw{background-image:none;background-color:transparent;}
#rbw{background-image:none;background-color:transparent;}

#site_body
{
background-image:url(http://i.imgur.com/K5I0sv9.jpg);
background-attachment:fixed;background-repeat:repeat-x;background-color:#000000; background-position:top;
}
/*text & link colors */
#site_body {
color:#FFE2AE;
min-width: 1160px;
}

body a {
color:#FEB02A;
}

body a:hover {
color:#FFC969;
}



/*header*/
#headerFrame {
background-image:url(http://i.imgur.com/29tG5BW.png);   
}

.adminBarContainerDiv {
border-radius: 50px;
background-color: rgba(0,0,0,0.07);
}

#info_panels {
background-color: rgba(0,0,0,0.05);
border-radius: 50px;   
}

a.adminBar {
outline: none;
}

a.adminBar:hover {
background-color: transparent;
border-left: 1px solid transparent;
border-right: 1px solid transparent;
outline: none;
}

/*positioning logo*/
.headerBody {
padding-top: 0px;
margin-left: 460px;
height: 200px;
width: 450px;
background: url(http://i.imgur.com/BECgbFf.png) no-repeat;
}

img.guildLogo {
display: none;
}

/*navbar*/
#primaryNavContainer {
padding-bottom: 20px;
background-image: none;    
}

#primaryNavTable {
border-top: 0;
border-radius: 50px;
background-image: none;
background-color: rgba(0,0,0,0.07);
}

.guildNav a {
color:#FEB02A;
}

#primaryNavList div.primaryNavPlayer {
padding-bottom: 8px;
}

.primaryNavPlayer {
background-color: rgba(0,0,0,0.1);
}

/*all collums*/
td#leftColumn, td#mainBody, td#rightColumn {
padding-top: 0px;
}

/*center collum*/
td#mainBody {
background-image: none;
}

#mainBodyShim {
background-color: rgba(0,0,0,0);
}

.widgetBodyTopCenter {
background-color: rgba(141,107,47,95);
}

.widgetBodyTopRight .contentTitle {
background-color: rgba(219,168,81,95);
}

.widgetBodyTopRight {
background-color: rgba(219,168,81,95);
background-image: none;
}

.poll_result_line {
background-color:#DC8E06;
}

a.maintitle {
color:#FFF8EB; 
}

a.maintitle:hover {
color:#FFD996;
}

/*side collums*/
.widgetMenuTopCenter {
background-color: rgba(141,107,47,95);
}

.widgetMenuTopRight {
background-color: rgba(219,168,81,95);
}

.widgetMenuTopLeft .contentTitle {
background-color: rgba(219,168,81,95);
}

#leftColumn, #rightColumn {
background-image: none;
}

/*remove news*/
.widg_108 {
display:none;   
}

#message_ifr {
background-image: none;
}
}
