css:
/* change URL prefix below */
@-moz-document url-prefix("http://support.example.com/scp/") {
/* @import url(https://fonts.googleapis.com/css?family=Open+Sans);
@import url(https://fonts.googleapis.com/css?family=Quicksand:400,700); */

body {
 	background: #e6e6e6; 
    font-family: 'Open Sans', sans-serif !important;
    background-image: url('http://u.cubeupload.com/andrewjcole_ga/ver10.png');
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: right bottom; 
}

#header, #pjax-container, #content, #sub_nav, #nav {
 	border: none !important;   
}

#container {
	-webkit-box-shadow: 0px 0px 138px -5px rgba(56,56,56,0.71);
	-moz-box-shadow: 0px 0px 138px -5px rgba(56,56,56,0.71);
	box-shadow: 0px 0px 138px -5px rgba(56,56,56,0.71);
    padding: 20px;
    background: white !important;
    margin-top: 60px;
    margin-bottom: 60px;
}

#header {
    box-shadow: none !important;
    background: white !important;
}

#nav li.active {
 	box-shadow: none !important;   
    border: none !important;
}

#nav {
 	background: #ED564B !important;
    color: white;
    -webkit-box-shadow: inset 0px 0px 138px -22px rgba(56,56,56,0.71);
	-moz-box-shadow: inset 0px 0px 138px -22px rgba(56,56,56,0.71);
	box-shadow: inset 0px 0px 138px -22px rgba(56,56,56,0.71);
}

#nav .inactive a {
 	color: white !important;   
    font-weight: normal;
}

#nav .inactive a:hover {
 	color: black !important;   
}

#nav .active a {
 	color: black !important;   
    font-weight: normal;
}

#header p {
 	border: none !important;
    padding: 20px !important;
    background: transparent !important;
}

#nav .inactive li a {
 	color: black !important;   
}

#nav .inactive li a:hover {
 	color: white !important;   
    background-color: #FAB4AF !important;
}

#nav ul {
 	border: none !important;   
    padding: 0 !important;
    border-radius: 0px !important;
    -webkit-box-shadow: 0px 0px 138px -22px rgba(56,56,56,0.71);
	-moz-box-shadow: 0px 0px 138px -22px rgba(56,56,56,0.71);
	box-shadow: 0px 0px 138px -22px rgba(56,56,56,0.71);
}

#nav .inactive a, #nav .active a {
 	font-family: 'Quicksand', sans-serif !important;   
    font-weight: 700;
    font-size: 12px;
}

table.note, table.message, table.response, #ticket_thread th {
    border-top-left-radius: 1em;
    border-top-right-radius: 1em;
    border: none;
}

#ticket_thread > .response th {
 	background: #ff6666 !important;  
    font-weight: normal; 
}

#ticket_thread > .note th {
 	background: #d9d9d9 !important;  
    font-weight: normal; 
}

#ticket_thread > .message th {
 	background: #ffa366 !important;  
    font-weight: normal; 
    color: white !important;
}

table.thread-entry th div :not(.title) {
 	font-weight: normal; 
    font-style: italic;
}

#ticket_thread table.message, #ticket_thread table.response, #ticket_thread table.note {
 	border: none !important;   
}

.thread-body {
 	/* background: #f2f2f2; */
    -webkit-box-shadow: inset 0px 0px 138px -22px rgba(56,56,56,0.10);
	-moz-box-shadow: inset 0px 0px 138px -22px rgba(56,56,56,0.10);
	box-shadow: inset 0px 0px 138px -22px rgba(56,56,56,0.10);
}

.faded {
 	color: black;   
}

.message th div span, .response th div span {
 	color: white !important;   
}

#ticket_thread table th, #ticket_notes table th {
 	border-bottom: none !important;   
}

ul.tabs {
 	margin-bottom: 0 !important; 
}

#response_options {
    -webkit-border-radius: 1em !important;
    -moz-border-radius: 1em !important;
    border-radius: 1em !important;   
}

ul.tabs {
 	padding-left: 1em !important;   
	border-top-left-radius: 1em;
    border-top-right-radius: 1em;
    border-bottom: none !important;
    padding-top: 10px;
    background: #63788E !important;
}

#response_options > form {
 	border: none !important;   
}

.tabs li a {
    border-top-left-radius: 0.5em;
    border-top-right-radius: 0.5em;
    font-weight: normal !important;
}

.tabs li a.active {
    border: none !important;
}

.btn_sm {
 	border: 0 !important;
    padding: 0.6em;
    border-radius: 3px;
    cursor: pointer;
}



}
