css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.basilmarket.com/") {

/* ========== Common Code ========== */

/* Font Colour */
body.day *, body.night *
	{
		color: #FFFFFF !important;
		border-color: #FFFFFF !important;
	}
input, select, select *, textarea
	{
		color: #000000 !important;
	}

/* ---------- Background ---------- */

/* Body */
.night, .day
	{
		margin-bottom: 0px !important;
	}
/* Background - Day */
.day
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Background-ENT-Day.png") bottom center fixed no-repeat #45A1D9 !important;
	}
/* Background - Night */
.night
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Background-ENT-Night.png") bottom center fixed no-repeat #02407F !important;
	}

/* ---------- Banner ---------- */

/* Banner Area */
.innerWrapday, .innerWrapnight
	{
		width: 840px !important;
	}
/* Banner - Day */
.innerWrapday
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Banner-Day.png") top no-repeat !important;
	}
/* Banner - Night */
.innerWrapnight
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Banner-Night.png") top no-repeat !important;
	}
/* Logo + Character */
.innerWrapPP1, .innerWrapPP2
	{
		width: 840px !important;
	}
/* Logo */
.innerWrapPP1
	{
		background: transparent !important;
	}
/* Character */
.innerWrapPP2
	{
		background-position: 700px -6px !important;
	}

/* ---------- Body Wrap ---------- */

/* Body Wrap */
.bwnight, .bwday
	{
		width: 840px !important;
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Body-Wrap.png") top no-repeat !important;
	}

/* ---------- Menu ---------- */

/* Menu */
.menuWrap
	{
		width: 840px !important;
		height: 75px !important;
		padding: 0px !important;
	}
/* Menu - Background */
.innerMenuWrap
	{
		background:url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Menu-Background.png") no-repeat !important;
		background-position: 20px 30px !important;
		width: 800px !important;
		padding-left: 20px !important;
		padding-top: 35px !important;
	}
/* Menu - Head Menu */
#nicemenu li span.over
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/SubMenu-Background.png") #1C75C1 !important;
		border-left: 1px solid #398ECD !important;
		border-right: 1px solid #0C4498 !important;
		border-top: 1px solid #398ECD !important;
		border-bottom: 0 !important;
	}
/* Menu - Sub Menu */
#nicemenu div.sub_menu
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/SubMenu-Background.png") #1C75C1 !important;
		border-top: solid 1px #398ECD !important;
		border-left: solid 1px #398ECD !important;
		border-right: solid 1px #0C4498 !important;
		border-bottom: solid 1px #0C4498 !important;
		opacity: 0.9 !important;
	}
/* Sub Menu Hover - Background */
#nicemenu div.sub_menu a:hover
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/SubMenu-Hover-Background.png") no-repeat !important;
	}
/* Menu - Item Line */
#nicemenu a.item_line
	{
		border-top: solid 1px #449CD5 !important;
	}

/* Favourites - Icon */
.thickbox
	{
		margin-right: 60px !important;
	}
/* Favourites - Window */
#TB_ajaxContent form div
	{
		color: #000000 !important;
	}


/* ---------- Search Bar ---------- */

/* Search Bar */
#bodyWrap > div.misc1.mb10
	{
		background: #648EFD !important;
		width: 790px !important;
		margin-left: 12px !important;
	}

/* ---------- Inner Body ---------- */

/* Inner Body - Background */
#bodyWrap
	{
		width: 825px !important;
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Inner-Background.png") center repeat-y !important;
		background-position: 2px !important;
	}

/* ---------- Copyright Area ---------- */

/* Copyright Box */
.copyright
	{
		background: #648EFD !important;
		width: 780px !important;
		margin-left: 12px !important;
	}


/* ========== Other Common Code ========== */

/* Section Header */
.bbTop, .topicBar
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Section-Header.png") top no-repeat !important;
	}
.topicBar.small
	{
		width: 780px !important;
		margin-left: 12px !important;
	}

/* Button - Background */
.btn
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Button.png") no-repeat !important;
	}

/* Top Messages */
.topMsg, .misc1.small.normal.center, #bodyWrap > div.sep
	{
		width: 790px !important;
		margin-left: 12px !important;
	}

/* I like it - Button */
.likeit:hover
	{
		color: #358014 !important;
	}

/* Other Backgrounds */
.sideColumn
	{
		background: #87A8FD !important; /* Inner Body Blue */
	}
div.np table, .normal, .toolBox, .sep.normal, div.lh, .lstat div, .lstat2 div, .lstat3 .lstatn, .lstat4 div, .dropItem, .thumbItem, .refItem, .refFieldset, .buttons a:hover, .misc5, .ps2, .sideModule, .sideModule .csep, .middleColumn .np, .middleColumn .np .sep, td[width="35%"] .sep, .listScreen, div[style="padding: 10px; line-height: 1.4em;"], td[style="background: rgb(248, 247, 242) none repeat scroll 0% 0%; width: 200px; vertical-align: top; margin-bottom: 10px; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;"], td[style="background: rgb(248, 247, 242) none repeat scroll 0% 0%; width: 300px; vertical-align: top; margin-bottom: 10px; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;"], td[style="padding:0;text-align:center;margin-top:0px;margin-bottom:10px;"], .gachapon, td[style="width:300px;vertical-align:top;margin-bottom:10px;background:#F8F7F2;"], td[style="width:200px;vertical-align:top;margin-bottom:10px;background:#F8F7F2;"], .screen, .picm, .search_result, td[width="35%"] div.lh, .tmenuTabInactive, tr.small td
	{
		background: #648EFD !important; /* Light Blue */
	}
div.sep, .error3, .oldMsg, .lstat .lstatn, .lstat2 .lstatn, .lstat3 .lstatn, .lstat4 .lstatn, .thumbItemHeader, .dropTitle, .csep, .error2, .misc1 td, .hiLite1 td, .hil, .hil td, .rules, td.misc3, .gachaponBox, .misc3 td, div.sep.normal, .picma, .search_result u, .tmenuTabActive, .error1 > div
	{
		background: #2777FE !important; /* Dark Blue */
	}
.title3.sep, .monsterTitle, .monsterDropTitle, .referenceAreaType, .button, .hiLite1 div, .misc2, .mid, .misc4 td, .flHeader, body[style="background: rgb(245, 248, 244) url(img/back.jpg) repeat-x scroll 0% 0%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;"], body[style="background:url(img/back.jpg) repeat-x #F5F8F4;"], .mb5
	{
		background: #0055DD !important; /* Darker Blue */
	}
tr.errorField2 td, .leftModule .csep, #auction_help, #newsletter_help, #profil3t, #profil3gt, #help_scrap, #help_regular, #fbhelp, #pic_guidelines, .stickyrow td, .errorField td, .topMsg, .error2.small
	{
		background: #B91F00 !important; /* Red */
	}
.misc1.sep
	{
		background: #6FB33C !important; /* Light Green */
	}
div[style="border: 1px outset rgb(221, 221, 204); background: rgb(221, 221, 204) none repeat scroll 0% 0%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;"], div[style="border:1px outset #ddddcc;background:#ddddcc;"], .likeit
	{
		background: #358014 !important; /* Dark Green */
	}
.middleColumn .np table tr td.sep, .likeit:hover
	{
		background: #FFFFFF !important; /* White */
	}

.stamp2, .sideColumn, td.misc3, .ps5, td[width="35%"] div.lh
	{
		border: 0 !important;
	}
td.middleColumn .np .sep
	{
		border-bottom-color: #FFFFFF !important;
	}
.small.sep.normal, .lstatl
	{
		border-bottom: 1px solid !important;
	}
.title3.sep
	{
		border-top: 1px solid !important;
	}

td[width="35%"] .left
	{
		margin-left: 0px !important;
		width: 269px !important;
	}
.left.mr10
	{
		margin: 0px !important;
	}

.np.mt10 div.lh
	{
		height: 100px !important;
	}

div[style="padding: 10px; line-height: 1.4em;"] div.topicBar.title2
	{
		background-position: -10px 0px !important;
		margin-left: -10px !important;
		margin-right: -10px !important;
	}

#bodyWrap > div.sep a table
	{
		width: 800px !important;
		margin-left: 12px !important;
	}

.mainColumn .topMsg
	{
		margin: 0px !important;
		margin-bottom: 15px !important;
	}

.mainColumn .np .topicBar
	{
		margin-left: 0px !important;
	}
.mainColumn .np .sep.small, #bodyWrap > .topicBar.title2, #bodyWrap > .bbTop
	{
		width: 790px !important;
	}
.mainColumn > table
	{
		width: 800px !important;
	}
.mainColumn .np td[width="35%"] .sep.small, .mainColumn .np td[width="35%"] .small.misc1
	{
		width: 269px !important;
	}
.mainColumn .np td[width="25%"] .sep.small
	{
		width: inherit !important;
	}

#bodyWrap > .topicBar.title2, #bodyWrap > .bbTop
	{
		margin-left: 7px !important;
	}


/* ========== Home Page ========== */

/* Inner Body - Table */
#bodyWrap > table
	{
		width: 800px !important;
		margin-left: 12px !important;
	}
/* Side Module - Header */
.left
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Side-Header.png") right no-repeat !important;
		width: 100% !important;
		margin-top: -5px !important;
		margin-left: -5px !important;
	}
/* Side Module - Columns */
td.leftColumn, td.rightColumn
	{
		background: #87A8FD !important;
	}
/* Side Module - Modules */
.leftModule, .rightModule
	{
		background: #B91F00 !important;
		border-bottom: 1px solid !important;
		margin-bottom: 10px !important;
	}
.small.sep.normal, .normal.lh, div.misc1.small
	{
		background: #B91F00 !important;
		border: 0 !important;
		border-bottom: 1px solid !important;
	}
/* Side Module - Thumbnails */
a.sideThumb
	{
		background: #840B00 !important;
	}
/* Middle Module - Header */
.right
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Middle-Header.png") right no-repeat !important;
	}
/* Middle Module - Stickies */
table.mb10.normal tr.small.errorField2 td.normal
	{
		background: #296E0E !important;
	}
/* Middle Module - Categories */
table.mb10.normal tr.small td.normal.misc1
	{
		background: #499022 !important;
	}
/* Middle Module - Topics */
table.mb10.normal tr.small td.normal
	{
		background: #63A937 !important;
	}
/* Separator */
td.sep
	{
		background: #FFFFFF !important;
	}


/* ========== Forum Pages ========== */

/* ---------- Threads ---------- */

/* Post - Background */
.postn
	{
		background: #358014 !important;
		border-bottom: 1px solid !important;
	}
/* Post - Border */
div.lh
	{
		border: 5px solid #358014 !important;
	}
/* Post - Gradient */
.postSubn, div.lh
	{
		background:url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Christmas%20Theme/Post-Background_v2.png") top repeat-x #358014 !important;
	}
/* Post - Author */
.postAuthorAn strong
	{
		color: #1C4B0B !important;
	}
/* Post - Author Info */
span.normal, .rf.small.normal, .postib.normal
	{
		background: transparent !important;
	}
/* Post - Headers */
.msgHeader, .postSub
	{
		background: #6FB33C !important;
	}
/* Page Number Area */
.misc1
	{
		background: #2777FE !important;
	}
/* New Post - Boxes */
.postBox
	{
		color: #000000 !important;
	}


/* ========== Price Check Page ========== */

/* Price Range - Low */
.priceL
	{
		color: #B91F00 !important;
	}
/* Price Range - High */
.priceH
	{
		color: #89CA4E !important;
	}


}
