css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eksisozluk.com") {
	
#main {
    margin-left: 255px !important;
	}

#index-section {
	width: 255px !important;
	}
	
.tabs {
	margin-bottom: 0 !important;
	}
	
html.no-touch #partial-index {
	padding-right: 15px !important;
	}

html.no-touch .topic-list li > a:hover {
	background: none !important;
	text-decoration: underline !important;
	}
	
.topic-list li:first-child {
	border-top: none !important;
	}
	
.topic-list li {
	border-top: 1px solid #B0BEC5 !important;
	}

.stats.topic-list.partial {
    list-style: none !important;
	}
	
.stats.partial {
    margin-left: 0 !important;
	}

#content-body {
	width: 960px;
	margin-left: 15px !important;
	margin-right: 15px !important;
	}

#aside {
	display: none !important;
	}
	
#entry-item-list > li {
	border-bottom: 1px solid #B0BEC5 !important;
	padding: 15px 0 !important;
	}
	
#entry-item-list {
	border-top: 1px solid #B0BEC5 !important;
	}
	
#entry-item-list .content {
	margin-bottom: 15px !important;
	}

#entry-item-list footer {
    margin-top: 15px !important;
	}

.more-data {
    margin-bottom: 15px !important;
	}

#profile-cards > li > h3, #profile-cards > li h4 {
	border: none !important;
	font-size: 22px !important;
	line-height: 1.2 !important;
	}

#profile-cards > li > ul {
	margin-bottom: 30px !important;
	padding: 6px 0 0 0 !important;
	text-indent: 0 !important;
	}
	
#profile-cards > li > ul li:before {
	content: none !important;
	}

#profile-cards > li > ul li {
	border-top: 1px solid #B0BEC5 !important;
	padding: 3px 0 !important;
	}
	
#site-footer {
    margin-left:15px !important;
	}

}
