css:
@-moz-document domain(eksisozluk.com) {
.not-mobile .eksico {
display:none !important;
}
#google_image_div, #div-gpt-ad-1451403752361-0 {
display:none !important;
}
body {
    font-family: Verdana,sans-serif !important;
    font-size: 13px;
    font-weight: normal;
    color: #e0e0e0;
    line-height: 1.618;
}
#sub-navigation>ul>li>a {
    margin: 0 20px 0;
    color: #D89630;
    display: inline-block;
    border-bottom: 6px solid transparent;
}
.loggedin #sub-navigation>ul>li {
    display: inline !important;
    margin: 0;
}
  h1, h2, h3, h4, h5, h6 {
    font-family: inherit;
    font-weight: bold;
    color: #D89630;
    line-height: 1.618;
                    }
#search-form button[type=submit] {
    height: 29px;
    line-height: 29px;
    border: 0 none;
    background-color: #F8C513;
    color: #fff;
    border-radius: 0 5px 5px 0;
    padding: 0 8px;
}
a {
    color: #D89630;
    text-decoration: none;
    cursor: pointer;
    -ms-touch-action: manipulation;
    touch-action: manipulation;
}
#logo a {
    display: block;
    margin: 0;
    padding: 0;
    background-image: url('http://i.hizliresim.com/v5EGam.png');
    height: 60px;
    line-height: 60px;
    background-position: 10px 50%;
    background-size: 129px 40px;
    background-repeat: no-repeat;
    text-indent: -119988px;
}
#entry-list a {
    color: #D89630;
}
#top-navigation>ul .new-update, #sub-navigation>ul .new-update {
    font-weight: bold;
    color: #fff !important;
    background: #009600;
    border-radius: 4px;
}
#top-navigation>ul .new-update .eksico, #sub-navigation>ul .new-update .eksico {
    fill: #fff!important;
}
button.primary, input[type=submit].primary, input[type=button].primary {
    border: 1px solid #5b8e31 !important;
    background-color: #009600 !important;
    color: #e0e0e0;
}
.modal
{
background-color: #222 !important;
color: #bbb !important;
}
#topic-channel-info #topic-channel-options>li>.name.active {
    background-color: #009600 !important;
    border-color: #494949;
    color: #fff;
}
html.no-touch a.more-data:hover {
    text-decoration: none;
    background-color: #1f1f1f;
    color: #009600 !important;
}
#sub-navigation>ul>li>a:hover {
    margin: 0 20px 0;
    color: #009600 ;
    display: inline-block;
    border-bottom: 6px solid transparent;
}
.confirmbox {
    display: none;
    padding: 10px;
    background-color: #222 !important;
    border: 1px solid #444 !important;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    color: #fff;
    position: absolute;
    z-index: 1000;
    margin-top: -1px;
}
#threads li article p {
    word-wrap: break-word;
    color: #bbb !important;
}
#user-notifications ul li{
background-color:#222 !important;
border:1px solid #494949;-webkit-border-radius:3px;-moz-border-radius:3px;border-radius:3px;color:#ddd;padding:10px 25px 10px 10px;position:relative
}
.btn-group>.btn:hover, .btn-group>.btn:focus, .btn-group>.btn:active, .btn-group>.btn.active {
    z-index: 2;
    border: 1px solid #5b8e31;
    background-color: #009600;
    color: #ddd;
}
.loggedin #sub-navigation>ul>li {
    flex-basis: 0;
    flex-grow:0 !important;
    flex-shrink: 1;
    display: inline !important;
}
#entry-list .content {
    margin-bottom: 5px;
    overflow: hidden;
    font-family: verdana !important;
    max-height: 255.88px;
    font-size: 13px !important;
}
}
