@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("accounts.google.com") {
  
  /*
  * First login window
  */
  
  /* Header */
  .google-header-bar {
    background-color: #111111;
  }
  
  /* Remaining Bkgd */
  .content {
    background-color: #111111;
  }
  /* Central Box */
  .main-content .no-name {
    background-color: #222222;
  }
  
  .slide-in {
    color: #bcbcbc;
  }
  
  .signin-card #email-display {
    color: #bcbcbc;
  }
  
  /** For Re-sign in */
  .card {
    background-color: #222222;
    color: #bcbcbc;
  }
  
  .signin-card #reauthEmail {
    color: #bcbcbc;
  }
  
  /* Footer */  
  .card-mask {
    background-color: #333333;
    background-image: none;
  }
  
  p.tagline {
    color: #999999;
  }
  
  .wrapper {
    background-color: #111111;
  }
  
  
  /*
  * For Previous login accounts window
  */
  
  /* Remaining Bkgd */
  .s2h6df {
    background-color: #111111;
  }
  
  /* Middle Div */
  .FH0XAb {
    background-color: #111111;
  }
  
  /* Choose an account */
  .pYERqe { 
    color: #999999;
  }
  
  /* Email address box */
  .tnNfDe { 
    background-color: #222222;
  }
  
  /* Email address text */
  .gu0k2c { 
    color: #bcbcbc;
  }
  
  /* Sign up window */
  .signuponepage .signup-box {
    background-color: #222222;
    color: #bcbcbc;
  }
}
