css:
@-moz-document domain("google.com"), 
    domain("google.co.in")
{
/* 
 * Name:    Google Account Info Dark
 * Author:  Abhishek Chakraborty
 */
 
    /* Account Top-Right pop-up pane */
    .gb_ga { /* Top sub-pane */
        background-color: #303030;
        color: #aaaaaa;
    }
    
    .gb_ab {
        border-bottom-color: #303030;
    }
    
    .gb_pb { /* 'Add Account', 'Sign Out' pane */
        background-color: #333333;
    }
    
    .gb_Ca { /* 'Add Account', 'Sign Out' buttons */
        background-color: #444444;
        color: #cccccc;
    }
    
    /* Top-Right message */
    .gb_Jd .gb_ga {
        background-color: #303030;
        color: #aaaaaa;
    }
    
    /* Google Products dropdown */
    .gb_ja {
        background-color: #303030;
    }
    
    /* To Do: replace with appropriate selector to display different color for previously visited products */
    .gb_4 { 
        color: #999999;
    }
    
    .gb_ka {
        background-color: #404040;
    }
}
