css:
@-moz-document domain("eksisozluk.com") {
body, body.light-theme.theme-disabled>header {
    background-color: #cccccc !important;
    color: #000 !important;
}
#sub-navigation>ul>li>a {
    margin: 3px 0 0 !important;
    color: #fff !important;
    display: block !important;
    /* border-bottom: 6px solid transparent; */
}
#sub-navigation>ul {
    height: 0 !important;
	margin-top:5px !important;
}
.loggedin #sub-navigation>ul>li {
    display: table-cell !important;
    width: 1% !important;
    font: 8pt Arial,sans-serif !important;
    text-align: center !important;
    white-space: nowrap !important;
    cursor: default !important;
    background-color: #566484 !important;
    color: white !important;
	border-left: 4px solid #c4c4c4 !important;
}
.loggedin #sub-navigation>ul>li:hover{
	background-color: #667494 !important;
}    

#logo a {
    background-image: url('http://i.hizliresim.com/rMQnRP.png') !important;
    background-size: 153px 26px !important;
}
#search-form #search-textbox {
    border: 1px solid #566484 !important;
	border-radius: 5px 0 0 5px !important;
}
#search-form button[type=submit] {
    border: 1px solid #566484 !important;
}

input[type=text], input[type=email], input[type=search], input[type=url], input[type=datetime], input[type=date], input[type=time], input[type=password], select, textarea {
    padding: 0 !important;
    border: 1px solid #b0bec5 !important;
    -webkit-border-radius: 0 !important;
    -moz-border-radius: 0 !important;
    border-radius: 0 !important;
}
.vertical>div>label, .vertical>fieldset>div>label {
   margin-bottom: 0 !important;
}

#top-bar>#advanced-search-form {
	background-color: #cccccc !important;
    border: 1px solid #676666 !important;
}
#top-navigation>ul .new-update, #sub-navigation>ul .new-update {
    color: #000000!important;
    background-color: #80C14B!important;
	border-bottom-left-radius: 5px !important;
	border-bottom-right-radius: 5px !important;
}
body.light-theme.theme-disabled #top-navigation>ul>li>a:hover{
	background-color: #D4D4D4 !important;
	border-bottom-left-radius: 5px !important;
	border-bottom-right-radius: 5px !important;
}
.tabs {
    margin-bottom: 0 !important;
}

#index-section {
    width: 250px !important;
}
.tabs>li.active {
    border: none !important;
}
 #index-section {
    top: 100px !important;
}
.topic-list.partial>li>a {
    padding: 3px 25px 3px 0px !important;
	border-bottom: 1px solid #B9B9B9 !important;
}
.topic-list li>a small {
    color: #6B6B6B !important;
	top: 0px !important;
}
.topic-list li>a:hover {
    text-decoration: underline !important;
    background-color: #c4c4c4 !important;
}
#search-form button[type=submit] {
    background-color: #566484 !important;
}
#sub-navigation>ul>li>a {
    border-bottom: none !important;
}
#top-navigation>ul .new-update .eksico, #sub-navigation>ul .new-update .eksico {
    fill: #333 !important;
}
#content-body {
    width: 90% !important;
	top: -20px !important;
	position: relative !important;
}
#entry-list a {
    color: #001166 !important;
}
.pager>a {
  text-align:center !important;
  white-space:nowrap !important;
  cursor: default !important;
  background-color: #566484 !important;
  color: white !important;
  }
.pager>a:hover {
	background-color: #667494 !important;
}

#entry-list footer .rate-options {
    padding: 0 0 !important;
}
#entry-list>li {
	border-bottom: 1px solid #B9B9B9 !important;
}
button{
  text-align:center !important;
  white-space:nowrap !important;
  cursor: default !important;
  background-color: #566484 !important;
  color: white !important;
}
button:hover{
	background-color: #667494 !important;
}
#threads li article:hover {
    background-color: #c4c4c4 !important;
}
.dropdown .dropdown-menu {
	background-color: #c4c4c4 !important;
}
.editform {
	background-color: #c4c4c4 !important;
}
.tabs>li.active, .tabs>li.active:hover , .tabs>li:hover{
	background-color: #c4c4c4 !important;
}
.more-data {
  text-align:center !important;
  white-space:nowrap !important;
  cursor: default !important;
  background-color: #566484 !important;
  color: white !important;
}
.more-data:hover{
	background-color: #667494 !important;
}
.more-data .rotator, .more-data .rotator .eksico {
	color: #fff !important;
	fill: #fff !important;
}
#message-thread .incoming {
    background-color: #c4c4c4 !important;
}
#message-thread .outgoing {
    background-color: #D6D6D6 !important;
}
.dropdown .dropdown-menu li:hover {
    background-color: #D6D6D6 !important;
}
.edittextbox.with-helpers {
    background-color: #D6D6D6 !important;
}
#partial-index::-webkit-scrollbar{
	width:8px;
}
#partial-index::-webkit-scrollbar-thumb{
	background-color:#D6D6D6 !important;
	-webkit-box-shadow:inset 1px 1px 0 rgba(0,0,0,0.1),inset 0 -1px 0 rgba(0,0,0,0.07);
	-webkit-border-radius:4px;
	-moz-border-radius:4px;
}
#partial-index::-webkit-scrollbar-button{
	display:inline !important;
}
#sub-navigation {
    padding-top: 3px !important;
}
.showall {
    margin-top: 0 !important;
}
button.primary, input[type=submit].primary, input[type=button].primary {
    border: none !important;
	float:left !important;
	margin-right: 5px !important;
}
.editform .actions #save-draft-button{
	padding-right:15px !important;
}
.pager select{
	border-radius: 4px !important;
}
#aside{
	margin-left: 55px !important;
}
#videos, #who-to-follow{
	display: none !important;
}
#topic-channel-info{
	position: absolute!important;
    top: 120px!important;
    right: 25px!important;
    z-index: 999!important;
  
}
#kanallar{
	display: none !important;
}
#topic-channel-suggestion-menu{
	background-color: #D6D6D6 !important;
}
#topic-channel-info #topic-channel-options>li>.name.passive{
	background-color: #E8E8E8 !important;
}
#topic-channel-info #topic-channels li.channel>a {
	background-color: #E8E8E8 !important;
}
.modal-overlay{
	display: none !important;
}
#aside>div, #aside>section {
    display: none !important;
}
@media handheld, only screen and (max-width: 1265px){
#content-body {
    margin-left: 10% !important;
}
}
}
