css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("where.moe") {
  .day-switch, .s-zone, body, html{
    background: #181818;
  }
  footer, .b-list .w, .search, .calendar .cal-menu{
    background: #263238;
  }
  .name-cn-s{
    color: #bbb;
  }
  .ova-name, .d-web, .donate, .say, .links a{
    color: #888;
  }
  .search .engine, .e-menu{
    background: #3f2a2a;
  }
  .e-menu li.active, .e-menu li:hover{
    background: #5f3a3a;
  }
  .go{
    background: #181818;
  }
  .go:hover{
    background: #080808;
  }
  .go:hover .iconmelon{
    fill: #ccc;
  }
  .load-bar,.day-list li.active, .calendar .cal-menu a.active, .calendar .cal-menu a:hover{
    background: #364248;
  }
  .angle-solid-up{
    border-bottom: 7px solid #263238;
  }
  .donate{
    background: #382626;
  }
  .d-code,.d-web{
    background: #3c2a2a;
  }
  .d-web:hover{
    background: #3f2a2a;
    color: #999
  }
  .say{
    background: #263826;
  }
  .ova{
    background: #263832;
  }
  .head-ova{
    color: #364842;
  }
  ol.b1 > li{
    background: #364842;
  }
  .poi{
    background: #383232;
  }
  .b2 .rate, .links{
    background: #282828;
  }
  .links a{
    background: #333;
  }
  .angle-solid-down{
    border-top: 7px solid #282828;
  }
  .links a:hover{
    background: #444;
  }
  .rate .angle-solid-down{
    border-top: 8px solid #282828;
  }
  .iconmelon{
    fill: #bbb;
  }
}
