css:
@-moz-document domain("eksisozluk.com") {
body.light-theme.theme-disabled > header {
    background: #393939 !important;
}
html.no-touch > body > header {
    background: #393939 !important;
}
body {
    background: #EBEBEB !important;
    color: #262626 !important;
    font-size: 13px !important;
    line-height: 20px !important;
}
body > header {
    border-top: 4px solid #80c14b;
    background-color: #80c14b;
    border-bottom: 1px solid #80c14b;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #000;
}
.btn-group > .btn:hover,
.btn-group > .btn:focus,
.btn-group > .btn:active,
.btn-group > .btn.active {
    z-index: 2;
    border: 1px solid #53a245;
    background-color: #80c14b;
    color: #000;
}
#container {
    padding-top: 34px;
    margin-top: 0px !important;
    width: 97%;
}
#index-section {
    margin-top: -20px !important;
    border-right: 1px solid #bcbcbc;
}
.more-data {
    border: 1px solid #bcbcbc;
    color: #000;
    background: #EBEBEB !important;
}
#feed-info a {
    color: #000 !important;
}
.tabs {
    border-bottom: 1px solid #bcbcbc;
}
.tabs > li.active {
    border: 1px solid #bcbcbc;
    border-bottom-color: #bcbcbc;
}
.tabs > li.active > a {
    color: #000;
    background: #EBEBEB;
}
.tabs > li.active > a:hover {
    color: #000;
}
.tabs > li > a:hover {
    background: #bcbcbc;
}
.showall {
    color: #000 !important;
}
.topic-list.partial,
.topic-list li > a {
    color: #000;
}
.topic-list .seperator,
.topic-list .seperator span {
    color: #000;
    background-color: #bcbcbc;
}
html.no-touch .topic-list li > a.empty-index-item:hover {
    color: #000;
}
html.no-touch .topic-list li > a:hover {
    background-color: #EBEBEB;
}
.topic-list li > a.empty-index-item,
.topic-list.partial > li > a > small {
    color: #000;
    background: #EBEBEB;
}
.topic-list li {
    border-top: 1px solid #bcbcbc !important;
}
.topic-list li > a small {
    font-size: 12px;
    margin-top: -7px !important;
}
.topic-list.partial a {
    background: #EBEBEB !important;
}
html.no-touch body.light-theme.theme-enabled div#container div#main div#content.instapaper_body section#content-body div#topic ul#entry-item-list{
    font-size: 14px;
}
html.no-touch body.light-theme.theme-disabled div#container div#main div#content.instapaper_body section#content-body div#topic ul#entry-item-list{
    font-size: 14px;
}
#entry-item-list footer .eksico {
    fill: #000;
    width: 23px;
    height: 11px;
}
#entry-item-list footer .feedback a {
    color: #000;
    font-size: 12px;
}
#content-section {
    margin-left: 15px !important;
    margin-right: 15px !important;
    width: 98%;
    margin-top: -35px !important;
}
#content-body {
    margin-left: 15px !important;
    margin-right: 15px !important;
    width: 98%;
    margin-top: -35px !important;
}
#aside {
    display: none !important;
}
#search-form #search-textbox {
    font-size: 12px;
    float: none;
}
#search-form button {
    background: #80c14b !important;
    font-size: 12px;
    float: none;
    margin-left: -2px !important;
}
#entry-list > li {
    border-bottom: 1px solid #DEDEDE;
}
#entry-list .content {
    margin-bottom: 15px !important;
    font-size: 14px;
}
.editform {
    background-color: #EBEBEB;
}
textarea {
    padding: 8px 15px;
    border: 1px solid #bcbcbc;
}
input[type="text"],
input[type="email"],
input[type="search"],
input[type="url"],
input[type="datetime"],
input[type="date"],
input[type="time"],
input[type="password"],
select,
textarea {
    border: 1px solid #bcbcbc;
}
button,
input[type="submit"],
input[type="button"] {
    background: #EBEBEB !important;
    color: #000;
    border: 1px solid #bcbcbc;
}
button.primary,
input.primary[type="submit"],
input.primary[type="button"] {
    border: 1px solid #bcbcbc;
    background-color: #EBEBEB;
    color: #000;
}
h2 {
    color: #000;
}
a {
    color: #000;
}
#entry-item-list a,
#draft-content a,
#disambiguations a {
    color: #000;
}
.sub-title-menu > a,
.sub-title-menu > div > a,
.sub-title-menu > div > .dropdown > a,
.dropdown .dropdown-menu li > a {
    color: #000;
}
#entry-list footer .other .dropdown-menu li > a {
    background: #EBEBEB !important;
}
.sub-title-menu .eksico,
#entry-list footer .eksico,
#entry-list footer .feedback a,
#entry-list footer .permalink {
    font-size: 12px;
    color: #898989;
    fill: #898989;
}
#entry-list a,
#draft-content a {
    color: #000;
}
.pager > a {
    border: 1px solid #bcbcbc;
    background: #EBEBEB !important;
}
.pager select {
    height: 24px;
    border: 1px solid #bcbcbc;
    background: #fff !important;
}
.topic-list.partial {
    margin: 10px 0 !important;
}
.topic-list.partial li > a {
    padding: 3px 25px 3px 0 !important;
    font-size: 14px;
}
.ads {
    display: none !important;
}
html > body > header #top-bar {
    background: #393939 !important;
    margin-top: 0px !important;
}
#top-navigation {
    text-align: center;
    margin-top: -6px;
}
#top-navigation > ul > li > a {
    color: #EBEBEB !important;
    display: inline-block;
}
#sub-navigation {
    background: #393939 !important;
    margin-top: 15px !important;
    height: 5px;
}
#sub-navigation > ul {
    height: 21px;
    line-height: 14px;
}
#sub-navigation a {
    color: #EBEBEB !important;
}
#sub-navigation > ul > li > a {
    margin: 0 20px 0;
    color: #333;
    display: inline-block;
    border-bottom: 3px solid transparent;
}
#sub-navigation .dropdown .dropdown-menu {
    background: #393939 !important;
    border: none !important;
    border-radius: 0 !important;
    box-shadow: none !important;
    margin-top: 0 !important;
}
#sub-navigation .dropdown ul.dropdown-menu {
    padding-bottom: 5px !important;
}
#sub-navigation .dropdown .dropdown-menu li:hover {
    background: none !important;
}
#sub-navigation .dropdown .dropdown-menu li > a {
    padding: 0 7px !important;
}
#sub-navigation .dropdown .dropdown-menu li > a:hover {
    background: #80c14b;
}
#sub-navigation > #nav-extra-buttons {
    flex-basis: 15%;
    flex-grow: 0;
    flex-shrink: 0;
    display: flex;
    margin-top: -15px !important;
}
body.light-theme.theme-disabled #top-bar .eksico {
    fill: #80c14b;
}
#entry-list footer .other .eksico {
    fill: #000;
}
#top-bar .eksico {
    fill: #80c14b;
}
.dropdown .dropdown-menu {
    display: none;
    background-color: #EBEBEB;
    border: 1px solid #bcbcbc;
}
.dropdown .dropdown-menu li.separated {
    border-top: 1px solid #bcbcbc;
}
.dropdown .dropdown-menu li > a:hover {
    background: #bcbcbc;
}
.sub-title-menu #in-topic-search-menu > .toggles::after {
    border-top: 4px solid #000;
}
#in-topic-search-options,
#in-topic-search-options form {
    background-color: #ebebeb;
    border: 1px solid #bcbcbc;
}
#in-topic-search-options form {
    background-color: #ebebeb;
    border: 0px;
}
#in-topic-search-options li > a:hover {
    background: #bcbcbc;
}
#in-topic-search-options button .eksico {
    fill: black;
}
.ui-autocomplete {
    background-color: #fff;
    border: 1px solid #bcbcbc;
}
.ui-autocomplete li > a:hover {
    background: #ebebeb;
}
.related-titles {
    background-color: #ebebeb;
    border: 1px solid #bcbcbc;
    color: #000;
}
.favorite-list-popup {
    background-color: #ebebeb;
    border: 1px solid #bcbcbc;
    color: #000;
}
#entry-list footer .feedback a,
#entry-list footer:hover .feedback a:hover {
    color: #000;
}
#entry-item-list footer .permalink {
    color: #000;
}
#site-footer {
    border-top: 1px solid #bcbcbc;
}
}
