css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("view-source:") {

html {background-color:transparent!important;margin:0!important;padding:5px!important;text-align:center!important}

html::before {
-moz-border-radius-topleft:7px!important;
-moz-border-radius-topright:7px!important;
white-space:pre!important;
padding:2px 130px 0 130px!important;
margin:auto!important;
content:"VIEW SOURCE"!important;
background-color:transparent!important;
background-position:0 -30px!important;
color:#fff!important;
font-size:18px!important;
font-weight:bold!important;
}

html::after {padding:6px 15px 6px 15px!important;background:#ccc!important;color:#000!important;content:"\ by t31os"!important;margin:0!important;font-size:9px!important;font-family:Verdana!important;}

#viewsource {
text-align:left!important;
margin:0!important;
padding:10px!important;
font-size:11px!important;
font-family:Verdana!important;
line-height:14px!important;
background-position:0 -80px!important;
background-repeat:repeat-x!important;
background-color:#fff!important;
}

#viewsource, html::before {background-image:url("data:image/jpg;base64,")!important;}

.doctype {color:#fff!important;font-size:15px}

pre[id*="line"], .attribute-name {color:#000!important}

}
