@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("drive.google.com"), domain("calendar.google.com"), domain("myaccount.google.com"), domain("translate.google.com") {
/* 
 * Name:    Google Drive Dark Addon
 * Author:  Abhishek Chakraborty
 */
  
  /* .gb_Ve .gb_4d { /* Top bar bkgd. */
  div#gb > div { /* Top bar bkgd. */
    background-color: #404040;
  }
    
  .a-pa-kg-in { /* Search Drive drop-down bkgd. */
    background-color: #404040;
  }
  
  .a-Bc-cd-m { /* Advanced Search bkgd. */
    background-color: #404040;
    color: #aaaaaa;
  }
  
  .j-A { /* Advanced Search menu drop-down bkgd. */
    background-color: #404040;
  }
  
  .j-D { /* Advanced Search menu drop-down text color */
    color: #aaaaaa;
  }
  
  /* .j-D .j-Xe .j-Xe-da {
    color: #040;
  } */
  
  /* Manage Versions */
  .kb-r {
    background-color: #404040;
  }
  
  .kb-r-M-x {
    color: #cccccc;
  }
  
  .kb-r-M {
    background-color: #404040;
  }
  
  .kb-r-s {
    background-color: #404040;
    color: #aaaaaa;
  }
  
  /* Upload Complete Pop-up pane */
  .a-Oe-bb { /* Pane */
    background-color: #404040;
    border-left-color: #404040;
    border-right-color: #404040;
  }
  
  .an-Oe .Cd-bb.Cd-sf { /* Item text */
    color:  #cccccc;
  }
}
