css:
@-moz-document url("https://www.taringa.net/mi") {
#feed-alert-container {
    margin: 0;
}
#feed-alert-container .alert {
    border: none;
    border-radius: 0;
    color: #fff;
    margin: 10px 0;
    text-shadow: none;
}
#friends-live-activity {
    height: 300px;
    width: auto;
}
#main {
    background-color: rgba(0,0,0,0.20);
    background-image: none;
    border: none;
}
#sidebar .highlight-data {
    width: auto;
    max-width: 255px;
    min-width: 221px;
}
.activity-element{
	padding: 8px 8px 2px 8px;
}
.activity-element a.alter {
    background: #fff;
    border: none;
    padding: 1px;
}
.activity-element a.avatar {
    margin: 0;
}
.activity-element a.avatar img,
.shout-box a.avatar img {
    background: #fff;
    border: none;
    box-shadow: none;
}
.activity-element a.avatar img:hover,
.shout-box a.avatar img:hover .activity-element a.avatar img,
.shout-box a.avatar img {
    border: none;
    box-shadow: none;
}
.activity-element.unread .activity-content {
    border: none;
    border-radius: 0;
    padding: 0 15px;
    width: 543px;
}
.activity-header .s-action-list {
    right: -31px;
    top: -2px;
}
.alert.danger,
.alert.error {
    background-color: #ff5722;
}
.alert.info {
    background-color: #2196f3;
}
.box {
    background: #fff;
    padding: 3px 8px;
}
.box div.title,
.divider {
    background: none;
    border-bottom: 1px solid silver;
    padding: 4px;
}
.button-action-s {
    background: none !important;
    border-left: none;
}
.cont-filterH ul li a {
    height: 30px;
    padding: 14px 20px 0;
}
.cont-filterH ul li a span {
    background: rgb(0, 93, 171);
    border-radius: 15px;
    box-shadow: none;
    text-shadow: none;
}
.cont-filterH ul li a.active {
    background: rgb(232, 82, 82);
    border-radius: 0;
    box-shadow: none;
    text-shadow: none;
}
.dialog {
    display: none;
}
.fixme {
    display: none;
}
.header-main {
    border: none;
}
.header-main .nav-principal li {
    color: #fff;
}
.nav-user {
    padding-top: 7px;
}
.nav-user__s-input {
    transition: all 300ms cubic-bezier(0.18, 0.89, 0.32, 1.28);
}
.nav-user__search .br-2 {
    border-radius: 0;
    height: auto;
}
.shout-box {
    margin: 10px 0 0 0;
}
.shout-footer {
    background: #fff;
    background-image: none;
    border-left: none;
    border-right: none;
    border-bottom: none;
    border-top: 1px solid silver;
    border-radius: 0;
    box-shadow: none;
    float: left;
    margin-left: 0 !important;
    margin-top: 8px;
    width: 645px;
}
.activity-element.unread .activity-content,
.activity-element.unread .shout-footer {
    border: none;
}
.tools {
    background: rgb(241, 241, 241);
}
.tools .mas {
    background: rgb(241, 241, 241);
}
.tools:before {
    border-bottom-color: #868993;
}
.tools#tool-profile {
    border: none;
}
.tools#tool-profile li a {
    background: #fff;
}
.tools li a {
    background: #fff;
}
.tools#tool-profile li a:hover {
    color: #005DAB;
}
.user-actions {
    background: #fff;
    border-radius: 0;
    color: #005DAB;
    padding: 2px;
}
.user-actions .user-action .user-name {
    max-width: 100px;
}
.user-actions .user-action .user-name:hover {
    color: silver;
}
.user-actions .user-action>a {
    color: #005DAB;
    padding: 0 4px;
}
.user-actions img.user-picture {
    border-radius: 50%;
}
.v6 .btn {
    border-radius: 0;
    padding: 12px;
}
div#Feed-controls-mi {
    background: #fff;
    border: none;
    border-radius: 0;
    box-shadow: none;
    box-sizing: border-box;
    margin: 10px 0 10px 0;
    width: auto;
}
div#Feed-controls-mi ul li:hover {
    background: aliceblue;
}
.activity-element {
    background: #fff;
    margin-bottom: 15px;
}
.cont-filterH ul li {
    height: auto;
}
.user-actions .user-action>a[class^="ico"] .count:after {
    display: none;
}
.user-actions .user-action>a[class^="ico"] .count:before {
    display: none;
}
.user-actions .user-action>a[class^="ico"] .count {
    left: 3px;
    bottom: 2px;
    line-height: 5px;
    border-radius: 50%;
    z-index: 100;
    width: auto;
    height: auto;
    padding: 6px;
}
.user-actions .user-action>a[class^="ico"]:hover {
    color: silver;
}
.activity-content {
    border: none;
    border-radius: 0;
    padding: 0 15px !important;
    width: 543px;
    background: none;
}
.small-list.activity-log,
.small-list.quick-reply {
    margin: 0;
    width: auto;
    max-width: 645px;
    border:0;
    border-left: 3px solid #E34C4C;
}
.quick-reply {
    background: none;
}
.quick-reply textarea {
    width: 478px !important;
}
.shout-live-box {
    left: 525px;
    border-radius: 0;
}
.activity-element a.avatar img:hover, .shout-box a.avatar img:hover{
	border:none;
    box-shadow:none;
}
}
