css:
@-moz-document domain("newsblur.com") {
/* ========= */
/* = Index = */
/* ========= */

/* Global
 * Scrollbars
 * Splash Pane
 * Pop-up Menus
 * Task bars (top & bottom)
 * Feed Pane
 * Stories Pane
 * Selected Story Pane
 * NewsBlur Blog
 * NewsBlur Forum
 */




/* ========== */
/* = Global = */
/* ========== */

html, body {
	background-color: #191b1c !important;
    color: #ccc !important;
}

.NB-static-alert {
	color: #ccc !important;
}

a:-webkit-any-link {
	color: #83b4e0 !important;
}

.NB-overlay {
	background: rgba(16,16,28,0.3) !important;
}

/* ============== */
/* = Scrollbars = */
/* ============== */

::-webkit-scrollbar {
	width: 12px !important;
}

::-webkit-scrollbar-thumb {
	background-color: rgb(24,29,33) !important;
}

::-webkit-scrollbar-track {
	background-color: rgb(52,59,61) !important;
}

.ui-layout-resizer-west {
	background-color: rgb(52,59,61) !important;
	border-left: 1px solid rgba(0,0,0,0) !important;
}




/* =============== */
/* = Splash Pane = */
/* =============== */

/*
a.NB-splash-link:hover,
.NB-module-features .NB-module-feature .NB-module-feature-description a:hover {
  color: #a85b40 !important;
}
*/

/* Background color */
#NB-splash {
    background-color: #191b1c !important;
}

/* Text color for "Premium Account", "New Features", "Community Feedback", "Past day", "Past day by hour" & "Recommended Site" body*/
.NB-module-content-header,
.NB-module-recommended .NB-recommended-description,
.NB-module-features .NB-module-feature .NB-module-feature-description {
	color: #ccc !important;
}

/* Divider color */
.NB-module.NB-module-features .NB-module-content-header,
.NB-module-recommended .NB-recommended-description,
.NB-module-recommended .NB-recommended-title {
	border-bottom: 1px solid #777 !important;
}

/* "Recomended Site" title color */
.NB-recommended-title {
	color: #eee !important;
}

/* Link color */
a.NB-splash-link,
.NB-module-features .NB-module-feature .NB-module-feature-description a {
	color: #83b4e0 !important;
}

/* Background color of "Upgrade" box (only visible for free users */
.NB-module-premium .NB-module-item-intro {
    background-image: none !important;
    background-color: #303739 !important;
	border: none !important;
    box-shadow: none !important;
}

/* Background color of
	 "Premium Account", "Manage", "Past Day" & "Past Day by Hour*/
.NB-module-stats-counts {
	background-color: #303739 !important;
}

/* "Logout", "Try" & "Add" button colors */
.NB-recommended-try,
.NB-recommended-add,
.NB-modal-submit-green .NB-modal-submit-button,
.NB-modal-submit-grey .NB-modal-submit-button,
.NB-menu-manage-logout {
	background-color: #505050 !important;
	background-image: none !important;
	color: #d5d4db !important;
	text-shadow: none !important;
}

/* Red buttons */
.NB-modal-submit-button {
	border: none !important;
}

/* Disabled Buttons */
.NB-modal-submit-green.NB-disabled,
.NB-modal-submit-button.NB-disabled {
	color: #999 !important;
    background-color: #505050 !important;
}

/* Title colors of
	 "Premium Account", "Manage", "Past Day" & "Past Day by Hour* */
.NB-menu-manage-title,
.NB-module-stats-count-number {
	color: #ccc !important;
	text-shadow: 1px 1px 1px rgba(0,0,0,0.5) !important;
}

/* Graph color */
.NB-graph-value .NB-graph-bar {
	background-color: #83b4e0 !important;
}

/* Graph hover text color */
.NB-graph-value .NB-graph-label {
    font-weight: bold !important;
	text-shadow: none !important;
}

/* Search input field color */
.NB-module-search-input input {
	color: #ccc !important;
	background-color: #505050 !important;
    border: solid 1px #505050 !important;
}

/* Bottom splash links color */
.NB-splash-info .NB-splash-links a {
	color: #83b4e0 !important;
	text-shadow: none !important;
}

/* "All Site Stories" color of titles */
.NB-storytitles-title {
	color: #ccc !important;
}

/* "All Site Stories" background color */
.NB-story-title {
	color: #ccc !important;
	background-color: #191b1c !important;
	border-top: 1px solid #232526 !important;
	border-bottom: 1px solid #101111 !important;
	border-right: 1px solid #101111 !important;
}




/* ================ */
/* = Pop-up Menus = */
/* ================ */

/* Manage menu on splash page & cogwheel pop-up */
.NB-menu-manage-container,
.NB-menu-manage .NB-menu-manage-site-info, /* Preferences title background */
.NB-menu-item, /* Icon background */
.NB-menu-manage-title /* Title text background */ {
	background-color: #303739 !important;
    border-radius: 4px !important;
	box-shadow: none !important;
}

/* Subtitle color for manage menu on cogwheel pop-up */
.NB-menu-manage-subtitle {
	color: gray !important;
}

/* Title colors and Folder titles when moving a feed to another folder */
.NB-popover-section-title,
.NB-folder-option-title {
	color: #ccc !important;
}

/* Dividers */
.NB-popover-section,
.NB-menu-manage li.NB-menu-separator {
	border-bottom: 1px solid #90928b !important;
}

/* Dividers: No bottom divider on last element */
.NB-popover-section:last-child,
.NB-menu-manage li.NB-menu-separator-inverse {
	border-bottom: none !important;
}

.popover {
	box-shadow: none !important;
}

/* Arrow color, then clicking the options of a feed or story */
.NB-menu-manage-arrow {
	background-image: none;
	background-color: #303739 !important;
}


/* Pop-up menu arrows */
.popover .arrow,
.popover .arrow:after,
.popover.left .arrow,
.popover.left .arrow:after,
.popover.right .arrow ,
.popover.right .arrow:after,
.popover.top .arrow,
.popover.top .arrow:after,
.popover.top-left .arrow,
.popover.top-left .arrow:after,
.popover.top-right .arrow,
.popover.top-right .arrow:after,
.popover.bottom .arrow,
.popover.bottom .arrow:after,
.popover.bottom-left .arrow,
.popover.bottom-left .arrow:after,
.popover.bottom-right .arrow,
.popover.bottom-right .arrow:after {
	display: none !important;
}

/* Add new site border color */
.NB-popover {
	background-color: #303739 !important;
	border-top: 1px solid #90928b !important;
	border-left: 1px solid #90928b !important;
	border-right: 1px solid #90928b !important;
	border-bottom: 1px solid #90928b !important;
	padding: 1px !important;
}

/* Add new site color */
.NB-add-form {
	color: #ccc !important;
	background-color: #303739 !important;
	border: none !important;
	padding: 0px !important;
}

/* Add new site input field and drop down menu */
.NB-input,
.NB-add input[type=text].ui-autocomplete-loading,
.NB-add-form .NB-folders{
	color: #ccc !important;
	background: none !important;
	background-color: #505050 !important;
	border: solid 1px #505050 !important;
}

/* Input field suggestions window (1 of 6) */
.NB-add-form .ui-autocomplete.ui-widget-content {
/*  position: static !important; */
	border-radius: 4px !important;
	border: 1px solid #90928b !important;
	background-color: #505050 !important;
	background: none !important;
	color: #ccc !important;
}

/* Input field suggestions window (2 of 6) */
.NB-add-form .ui-menu .ui-menu-item a {
	display: inline-block !important;
	width: 345px !important;
	height: 40px !important;
	border-radius: 4px !important;
    border-right: none;
	background: #303739 !important;
	color: #ccc !important;
}

/* Input field suggestions window (3 of 6) */
.NB-add-form .ui-menu .ui-menu-item a:hover {
/*  border: 1px solid #90928b !important; */
    border-radius: 4px !important;
    border-right: none !important;
    display: inline-block !important;
	background: #252D30 !important;
	color: #ccc !important;
}

/* Input field suggestions window (4 of 6) */
.NB-add-form .ui-autocomplete li a .NB-add-autocomplete-title  {
	color: #ccc !important;
}

/* Input field suggestions window (5 of 6) */
.NB-add-form .ui-autocomplete li a .NB-add-autocomplete-address {
	color: #83b4e0 !important;
}

/* Input field suggestions window (6 of 6) */
.NB-add-form .ui-autocomplete.ui-widget-content {
	background: #303739 !important;
}

/* Unread/Oldest text color */
.NB-feedbar-options-stat .NB-stat {
	color: #ccc !important;
}

/* Style Font family active */
.segmented-control-vertical li.NB-active, .segmented-control-vertical li.NB-active:active, .segmented-control-vertical-active li {
	color: #ccc !important;
	background-color: #252D30 !important;
	background-image: none !important;
}

/* Style Font family non-active */
.segmented-control-vertical li {
	color: #ccc !important;
	text-shadow: none !important;
	background-color: #303739 !important;
	background-image: none !important;
}

/* Background color */
#simplemodal-container {
	background-color: #252D30 !important;
    box-shadow: none !important;
    border: 1px solid #90928b !important;
}

/* Title color */
.NB-modal .NB-modal-title,
.NB-modal .NB-modal-subtitle,
.NB-modal .NB-feed-title,
.NB-modal .NB-modal-feed-heading,
.NB-modal-trainer .NB-trainer-points li b{
	color: #ccc !important;
	text-shadow: none !important;
}

/* Header boxes */
fieldset legend {
	color: #ccc !important;
	background-color: #303739 !important;
	border: 1px solid #777 !important;
}

/* Divider color */
.NB-profile-badge,
.NB-feed-notification,
fieldset {
	border-color: #777 !important;
}

/* Selected tab */
.NB-modal .NB-modal-tab.NB-active {
	color: #ccc !important;
	text-shadow: none !important;
	background-color: #252D30 !important;
	border-bottom: 1px solid #252D30 !important;
}

/* Non-selected tab */
.NB-modal .NB-modal-tab {
	color: #ccc !important;
	text-shadow: none !important;
	background-color: #303739 !important;
	border-bottom: 1px solid #777 !important;
	border-top: 1px solid #777 !important;
	border-left: 1px solid #777 !important;
}

/* Last tab */
.NB-modal .NB-modal-tab:last-child {
	border-right: 1px solid #777 !important;
}

/* Center window */
.NB-modal .NB-tab {
	color: #ccc !important;
	border-top: 1px solid #777 !important;
}

/* section "blobs" */
.NB-preference,
.NB-profile-badge table {
	background-color: #303739 !important;
}

/* Text color for labels (Full, split, list, etc.) in preferences */
.NB-view-settings.NB-preference-options label,
.NB-modal-preferences .NB-preference-password label {
	color: #ccc !important;
}

/* Drop down menus */
select {
	color: #ccc !important;
	background-color: #505050 !important;
	border: solid 1px #505050 !important;
}

/* CSS input fields */
.NB-modal-profile-editor .NB-profile-blurblog-css,
.NB-modal-account .NB-account-custom-css,
.NB-modal-account .NB-account-custom-javascript {
    color: #ccc !important;
	background-color: #505050 !important;
    border: solid 1px #505050 !important;
}

/*Account input fields */
.NB-modal-account .NB-preference-username input,
.NB-modal-account .NB-preference-email input,
.NB-modal-account .NB-preference-password input {
	color: #ccc !important;
	background: none !important;
	background-color: #505050 !important;
	border: solid 1px #505050 !important;
	box-shadow: none !important;
}


/* Goodies & Extras: Section "blobs" */
.NB-goodies-group,
.NB-modal-goodies .NB-goodies-title {
    color: #ccc !important;
    text-shadow: none !important;
	background-color: #303739 !important;
}

/* Goodies & Extras: Section subtitle text*/
.NB-modal-goodies .NB-goodies-subtitle {
    color: gray !important;
}

/* Goodies & Extras: Custom URLs input field */
.NB-modal-goodies .NB-goodies-custom-input {
	color: #ccc !important;
	background-color: #505050 !important;
	border: solid 1px #505050 !important;
}

/* Tips & Tricks: Text color */
.NB-modal-tutorial {
	color: #ccc !important;
}

/* Import or upload sites: Text color */
.NB-modal-page-text,
.NB-modal-intro .NB-page-2 h4,
.NB-modal-intro .NB-page-3,
.NB-modal-intro .NB-page-4,
.NB-modal-intro .NB-intro-import-starred-message,
.NB-modal-intro .NB-intro-module,
.NB-modal-intro .NB-intro-uptodate-follow,
.NB-modal-intro .NB-intro-services-stats-count-number {
	color: #ccc !important;
    text-shadow: none !important;
}

/* Import or upload sites: Container color */
.NB-modal-intro .NB-intro-module,
.NB-modal-intro .NB-intro-uptodate-follow{
	background-color: #303739 !important;
    border: 1px solid #999 !important;
}

/* Import or upload sites: "Choose categories" selection color */
.NB-modal-intro .NB-intro-module .NB-modal-submit-button {
	background-color: #505050 !important;
	background-image: none !important;
	color: #d5d4db !important;
	text-shadow: none !important;
}

/* Email Newsletters: Email field */
.NB-modal-newsletters .NB-newsletters-email {
	color: #ccc !important;
	background-color: #505050 !important;
	border: solid 1px #505050 !important;
}

/* Email Newsletters: Body text color */
.NB-newsletters-group {
	color: #ccc !important;
}

/* Keyboard shortcuts: Section "blobs" */
.NB-keyboard-group,
.NB-keyboard-shortcut-explanation {
    color: #ccc !important;
    text-shadow: none !important;
	background-color: #303739 !important;
}


/* Organize sites: Text color */
.NB-organizer-action-title,
.NB-organizer-sidebar-title,
.NB-feed-organizer-subscribers,
.NB-feed-organizer-laststory,
.NB-feed-organizer-monthlycount,
.NB-feed-organizer-opens {
	color: #ccc !important;
}

/* Organize sites: Sorting categories text color */
.NB-modal-organizer .NB-organizer-action,
.NB-modal-organizer .NB-organizer-action.NB-active,
.NB-modal-organizer .NB-organizer-action.NB-active:hover {
	color: #83b4e0 !important;
}

/* Organize sites: Container color */
.NB-modal-organizer .NB-organizer-sidebar-container {
	background-color: #303739 !important;
}

/* Mute sites: Text color */
.NB-feedchooser-info-counts {
	text-shadow: none !important;
}

/* Intelligence Trainer: Container color */
.NB-modal .NB-modal-subtitle,
.NB-modal-submit-bottom,
.NB-modal .NB-fieldset-fields {
    background-color: #252D30 !important;
    border-top: none !important;
}

/* Intelligence Trainer: "Close" button color */
.NB-modal-trainer .NB-modal-submit .NB-modal-submit-grey {
	background-color: #505050 !important;
	background-image: none !important;
	color: #d5d4db !important;
	text-shadow: none !important;
}

/* Site Settings: Text color */
.NB-preference-label,
.NB-modal-exception .NB-exception-label {
	color: #ccc !important;
}

/* Site Statistics: Text color */
.NB-modal-statistics .NB-statistics-stat .NB-statistics-label ,
.NB-statistics-count,
.NB-modal-statistics .NB-statistics-classifiers {
	color: #ccc !important;
    text-shadow: none !important;
}

/* Site Statistics: Dividers */
.NB-modal-statistics .NB-statistics-facet-title,
.NB-modal-statistics .NB-statistics-facet {
	border-bottom: none!important;
}

/* Interactions / Activities text color and thickness */
.NB-interactions-popover.NB-active-interactions .NB-tab-interactions,
.NB-interactions-popover.NB-active-activities .NB-tab-activities {
	color: #ccc !important;
	font-weight: bold !important;
}

/* Interactions / Activities bottom border color */
.NB-interactions-popover .NB-interactions-header {
	border-bottom: 1px solid rgba(204, 204, 204, 0.5) !important;
}

/* Hover color */
.NB-interaction:hover:not(.NB-disabled) {
	background-color: #252D30 !important;
	border-top: 1px solid transparent !important;
	border-bottom: 1px solid transparent !important;
}

/* Hover color of date "3 days ago" */
.NB-interaction:hover:not(.NB-disabled) .NB-interaction-date {
	color: #5e828b !important;
}

/* Click color (no color at all) */
.NB-interaction:active:not(.NB-disabled) {
	border-top: 1px solid transparent !important;
	border-bottom: 1px solid transparent !important;
	background-color: transparent !important;
	background: none !important;
}

/* New replies color */
.NB-interaction.NB-highlighted {
	background-color: #252D30 !important;
	border-top: 1px solid transparent !important;
	border-bottom: 1px solid transparent !important;
}

/* Reply color */
.NB-interaction {
 	background-color: #303739 !important;
	border-bottom: 1px solid transparent !important;
}

/* Username color */
.NB-splash-info .NB-splash-links a, .NB-static a, .NB-splash-link {
	color: #83B4E0 !important;
}

/* "replied to your reply" text color */
.NB-interaction-title {
	color: #ccc !important;
}




/* ============================ */
/* = Task bars (top & bottom) = */
/* ============================ */

.NB-feeds-header-user,
.NB-feedbar,
.NB-taskbar-container,
#story_titles .NB-feedbar,
.NB-splash-info.NB-splash-bottom,
.left-south {
	color: #ddd !important;
	text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
	background: #191b1c !important;
	background-color: #191b1c !important;
	background-image: none !important;
	border-color: #303739 !important;
}

/* Buttons on bottom & top bar */
.NB-modal h5,
.NB-module h5,
.NB-module-header,
.NB-module-recommended-date,
.NB-taskbar-options,
.NB-feedbar-options,
ul.segmented-control,
ul.segmented-control li {
	background: none !important;
	background-color: #303739 !important;
	color: #ccc !important;
	text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
	/*border: 1px solid #90928b !important;*/
}

/* Error text on bottom bar */
#story_taskbar .NB-river-progress .NB-river-progress-text,
#story_taskbar .NB-feed-error .NB-feed-error-text {
	background-color: #191b1c !important;
	color: #ccc !important;
	text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
}

/* "Unread/Oldest" & "Style" border color */
.NB-feedbar .NB-feedbar-options,
#story_taskbar .NB-taskbar-options {
	border: 1px solid rgba(255, 255, 255, .2) !important;
}

/* Story Titles Hidden Count */
.NB-feedbar .NB-story-title-indicator {
	color: #ccc !important;
	border: 1px solid rgba(255, 255, 255, .2) !important;
	background-color: #303739 !important;
}

.NB-feedbar .NB-story-title-indicator:hover {
	background-color: rgba(204, 204, 204, .3) !important;
}

/* "Mark read" icon*/
.NB-feedbar .NB-feedbar-mark-feed-read .NB-icon {
	background: transparent url(data:image/png;base64,)
no-repeat center center !important;
	background-size: 18px !important;
	width: 100% !important;
	height: 100% !important;
}

/* Color when hovering over "plus" icon */
.NB-feedbar .NB-feedbar-mark-feed-read-expand:hover {
	background-color: rgba(204, 204, 204, 0.3) !important;
}


