//"Plus" icon

.NB-feedbar .NB-feedbar-mark-feed-read-expand {
	position: absolute !important;
	right: 20px !important;
	z-index: 0 !important;
	background: transparent url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABgAAAAYCAYAAADgdz34AAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAALiIAAC4iAari3ZIAAAAYdEVYdFNvZnR3YXJlAHBhaW50Lm5ldCA0LjAuOWwzfk4AAABMSURBVEhLYyAWnDlz5j8adoBKUQeMWkAQjFpAEIxgC7BopCoetYAgBlngQCRG11wAxNjUoWBoVBMGQMXoFoxmNFQwagFBMFItYGAAAFSuu1vdkb1XAAAAAElFTkSuQmCC") no-repeat 4px center !important;
	background-size: 6px !important;
	width: 19px !important;
}
*/

/* "Mark read" + expanded mode background color */
.NB-feedbar .NB-feedbar-mark-feed-read,
.NB-feedbar .NB-feedbar-mark-feed-read-expand,
.NB-feedbar .NB-feedbar-mark-feed-read-time {
	border: 1px solid rgba(255, 255, 255, .2) !important;
  	background: rgba(48, 55, 57, 1) !important;
}

/* Expanded "Mark read" text color (14D, 7D, 3D, 1D)*/
.NB-feedbar .NB-feedbar-mark-feed-read-time {
	color: #ccc !important;
}

/* Search input field */
.NB-search-input {
	border: 1px solid rgba(255, 255, 255, .2) !important;
 	background: none !important;
	background-color: #303739 !important;
	color: #ccc !important;
	text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
	box-shadow: none !important;
}

/*
.NB-callout {
	color: #333 !important;
	text-shadow: none !important;
}
*/

/* Selected tabs on bottom task bar */
.segmented-control li.NB-active, .segmented-control li.NB-active:active, .segmented-control-active li {
	background: none !important;
	background-color: #252D30 !important;
	color: #ccc !important;
}




/* ============= */
/* = Feed Pane = */
/* ============= */

.left-pane,
.NB-feedlist {
	background-color: #303739 !important;
}

.NB-feedlist .feed {
	border-top: 1px solid #303739 !important;
	border-bottom: 1px solid #303739 !important;
	padding-right: 2px !important;
	background-color: #303739 !important;
}

/* Selected Feed */
.NB-feedlist .feed.selected, .NB-feedlist .feed.NB-selected, .NB-feeds-header.NB-selected, .NB-feedlist .folder.NB-selected>.folder_title {
	background: none !important;
	background-color: #40492F !important;
	border-top: 1px solid #515A3F !important;
	border-bottom: 1px solid #282E1C !important;
}

/* Shared Stories*/
.NB-feedlists .NB-socialfeeds {
	border-bottom: 0 !important;
}

/*
.NB-feedlists .NB-socialfeeds .feed {
	background-color: #303739 !important;
	border-top-color: #303739 !important;
	border-bottom-color: #303739 !important;
}
*/

/* Feed title text */
.NB-feeds-header .NB-feeds-header-title,
.NB-feedlists .NB-socialfeeds .feed .feed_title,
.NB-feedlist .feed_title {
	color: #bbb !important;
	text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
}

/* Feed folder text */
.NB-feedlist .folder_title,
.NB-feedlist .folder.NB-selected>.folder_title {
	color: #777 !important;
	text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
}

/* Feed text for unread feeds*/
.unread_view_neutral .unread_neutral {
	font-weight: normal !important;
}

/* "All Site Stories" text*/
.NB-feeds-header {
	background: none !important;
