.category-name {
	color: #83b4e0 !important;
}

.selected-name .category-name {
	color: #FFFFFF !important;
}

/* Sorting titles ("Category", "Replies", "Activity") */
.topic-list .sortable:hover {
    color: #ccc !important;
	background-color: #0088cc  !important;
}

/* Thread excerpt */
.topic-excerpt {
 color: #ccc !important;
}

/* Topic fade-in */
.topic-body {
    animation: none !important;
}

/* Topic bar at the bottom of OP */
.topic-map {
    border: none !important;
}

/* Topic bar at the bottom of OP */
.topic-map section {
	background-color: #191b1c !important;
	border-top: 1px solid #191b1c !important;
    border-bottom: 1px solid #191b1c !important;
    border-left: 1px solid #191b1c !important;
    border-right: 1px solid #191b1c !important;
}

/* Expand/Collapse button in Topic bar */
.topic-map .buttons .btn {
	background-color: #191b1c !important;
    border-left: 1px solid #191b1c !important;
    border-bottom: none !important;
}

/* Quote box */
aside.quote .title,
blockquote {
    border-left: 5px solid #ccc !important;
    background-color: #191b1c !important;
}

/* Link box border */
aside.onebox {
    border-left: 5px solid #ccc !important;
    border-right: none !important;
    border-top: none !important;
    border-bottom: none !important;
}

/* Mention box */
a.mention, a.mention-group {
	background: none !important;
}

