css:
html.no-touch>body {
  padding-top:61px !important;
}
/* i really want this to be global */
body {
  background-color:#ebebeb !important;
  font-family:Verdana,sans-serif !important;
  font-size:75% !important;
  font-weight:normal !important;
  color:#000 !important;
  line-height:1.618 !important;
}

input, select, textarea {
  font-family:Verdana,sans-serif !important;
  font-size:12px !important;
}

.ui-autocomplete {
  background-color:#ebebeb !important;
}

body.dark-theme {
  background-color:#2d2d2d !important;
  font-family:Verdana,sans-serif !important;
  font-size:75% !important;
  font-weight:normal !important;
  color:#91989c !important;
  line-height:1.618 !important;
}

.dark-theme .ui-autocomplete {
  background-color:#2d2d2d !important;
}

a {
  color: #0027b8 !important;
}

.dark-theme a {
  color: #d89630 !important;
}

body>header {
  border-top:0px !important;
  height:65px !important;
}

html>body>header #top-bar {
  height:65px !important;
}

body.light-theme.theme-disabled>header {
  background-color:#333 !important;
  color:#fff !important;
}

#sub-navigation {
  height:25px !important;
}

#sub-navigation>ul {
  line-height:25px !important;
}

#sub-navigation>ul>li {
  display:inline-block !important;
  width:auto !important;
  line-height:25px !important;
}

#sub-navigation>ul>li>a {
  color:#d4d4d4 !important;
  font-weight:normal !important;
  margin:0 8px 0px !important;
  border-bottom:0px !important;
  font-size:11px !important;
  line-height:25px !important;
}

#sub-navigation #quick-index-nav {
  display:inline-block !important;
  height:25px !important;
}

#options-dropdown ul.dropdown-menu li>a {
  color: black !important;
}

.dark-theme #options-dropdown ul.dropdown-menu li>a {
  color: #d89630 !important;
}

#top-navigation>ul {
  height:40px !important;
  line-height:40px !important;
}

#top-navigation li a {
  padding:0 !important;
  color:#d4d4d4 !important;
}

#top-navigation li a.new-update {
  background-color: #3da723 !important;
  font-weight:normal !important;
  color:#ebebeb !important;
  margin-left:15px !important;
  padding-left:10px !important;
  padding-right:10px !important;
  -webkit-box-shadow: 0 0 19px #3da723 !important;
  box-shadow: 0 0 19px #3da723 !important;
  border-radius:2px !important;
  -webkit-border-radius:2px !important;
}

#top-navigation li a.new-update svg {
  display:none !important;
}

li#options-dropdown a svg, li.dropdown a.dropdown-toggle svg {
  fill:#d4d4d4 !important;
}

.dropdown .dropdown-menu {
  background-color:#ebebeb !important;
  color:#91989c !important;
}

.dark-theme .dropdown .dropdown-menu {
  color:#ebebeb !important;
  background-color:#2d2d2d !important;
}

.dropdown .dropdown-menu li>a{ font-weight:normal !important; }

#logo, #search-form, #top-navigation {
  height:40px !important;
}

#search-form {
  height:25px !important;
  line-height:25px !important;
  margin: 7px 0 0 57.5px !important;
}

#search-form #search-textbox, #search-form button[type=submit] {
  border: 0px !important;
  height:25px !important;
  line-height:25px !important;
  border-radius:0px !important;
}

div#logo {
  width:210px !important;
  height:29px !important;
}
#logo a {
  display:block !important;
  margin:0 !important;
  padding:0 !important;
  height:40px !important;
  line-height:40px !important;
  background-position:1px 0 !important;
  background-size:129px 40px !important;
  background-repeat:no-repeat !important;
  text-indent: -119988px !important;
  background-image: url('data:image/png;base64,') !important;
}






#partial-index {
  top:0px !important;
}

#partial-index h2 {
  font-weight:normal !important;
  font-size:1.7em !important;
}

#index-section {
  top:75px !important;
  border-right:0px !important;
}

.topic-list li:first-child {
  border-top:0 !important;
}
.topic-list li {
  border-top:1px solid #d7d7d7 !important;
}

.topic-list li>a small {
  top:7px !important;
  font-size:0.75em !important;
  color:#666 !important;
}

.topic-list.partial>li>a {
  padding:4px 0 !important;
  padding-right:25px !important;
  font-size:1em !important;
}

.tabs>li.active {
  border-bottom-color:#ebebeb !important;
}

#container {
  padding-top:0px !important;
}

ul#entry-list {
  font-size:100% !important;
}

#entry-list>li:first-child {
  padding-top:0 !important;
}

#entry-list>li {
  border-bottom:1px solid #d2d2d2 !important;
}

#entry-list footer .feedback a {
  color:#666 !important;
}

#entry-list footer .favorite-count {
  font-size:.9em !important;
}

#entry-list footer .permalink {
  font-size:.6em;
  color:#666 !important;
}

#entry-list footer .entry-date {
  font-size:0.7em !important;
  opacity:0.5 !important;
}

#entry-list footer .entry-author {
  font-size:.9em !important;
}

#topic>h1 {
  font-size:25px !important;
  font-weight:normal !important;
}

h1, h2, h3, h4, h5, h6 {
  font-family:Calibri,Helvetica,Verdana,sans-serif !important;
  font-weight:normal !important;
}

fieldset.vertical {
  background-color:#ebebeb !important;
}

.dark-theme fieldset.vertical {
  background-color:#2d2d2d !important;
}

#content-body {
  width:695px !important;
  margin-left:17.5px !important;
  margin-right:17.5px !important;
}

@media handheld, only screen and (max-width: 1265px) {
  #content-body {
    width:550px !important;
    margin-left:15px !important;
    margin-right:15px !important;
  }
}

@media handheld, only screen and (max-width: 1090px) {
  #content-body {
    width:auto !important;
    margin-left:10px !important;
    margin-right:0 !important;
    float:none !important;
  }
}

@media handheld, only screen and (max-width: 850px) {
  #content-body {
    width:100% !important;
    max-width:100% !important;
    margin:0 !important;
  }
}



html.no-touch #partial-index::-webkit-scrollbar {
  width:8px !important;
}

html.no-touch #partial-index::-webkit-scrollbar-button {
  width:0 !important;
  height:0 !important;
  display:none !important;
}

html.no-touch #partial-index::-webkit-scrollbar-corner {
  background-color:transparent !important;
}

html.no-touch #partial-index::-webkit-scrollbar-thumb {
  background-color: #dedede !important;
  border-radius:4px !important;
}

html.no-touch .topic-list li>a:hover {
  background-color:#d8d8d8 !important;
}

html.no-touch body.dark-theme .topic-list li>a:hover {
  background-color:#404040 !important;
}




#entry-list .content {
  max-height:100% !important;
}

span.read-more-link-wrapper {
  display:none !important;
}


@media handheld, only screen and (max-width: 850px) {
  div#logo {
    width:100px !important;
  }

  #search-form {
    width:40% !important;
    margin-left:10px !important;
  }

  .pager-container {
    display:inline-block !important;
  }
}

ul.topic-list a.empty-index-item {
  opacity:0.3 !important;
}



/**
* entry number
*/
#entry-list>li {
    position: relative;
    padding: 4px 0 50px 0 !important;
    border-bottom: 1px solid #D2D2D2;
}

#entry-list>li:first-child{
    border-top: 1px solid #D2D2D2 !important;
}

#entry-list > li:before {
    font-size: .8em;
    position: absolute;
    content: attr(value);
    top: -16px;
    left: 0;
    display: block;
    width: 100%;
    color: #666;
}

.pager select{
  margin-top: -2px !important;
}
