css:
/*
	@name          VNDB Complete
	@description	 VNDB style merging "Better VNDB.org"'s code and "VNDB Dark and Flat"'s code. The style also adds new features absent from both these skins for better navigation.
	@author        One Shade
	@version       1.0.2
*/

@-moz-document domain("vndb.org") {
	
	/*Style part taken from VNDB Dark and Flat*/
	* {
		font-family: "Open Sans", "Segoe UI", "Calibri", sans-serif !important;
	}
	
	img {
		-webkit-transition: 0.25s ease;
		-moz-transition: 0.25s ease;
		-o-transition: 0.25s ease;
		transition: 0.25s ease;
	}
	
	#vntags span {
		width: calc(100%/4 - 20px);
	}
	
	#vntags {
		margin-left: 0px !important;
		margin-right: 0px !important;
		padding: 0 !important;
		padding-right: 20px !important;
		text-align: left !important;
	}
	
	#vntags span {
		display: inline-block;
		text-align: left;
		margin: 0;
		margin-left: 5px;
		-webkit-transition: 0.25s ease;
		-moz-transition: 0.25s ease;
		-o-transition: 0.25s ease;
		transition: 0.25s ease;
	}
	
	#vntags span a {
		font-size: 13px !important;
	}
	
	#vntags span.cat_cont {
		box-sizing: border-box;
		border-left: 2px solid #00ff00;
		padding-left: 3px;
	}
	
	#vntags span.cat_tech {
		box-sizing: border-box;
		border-left: 2px solid #0000ff;
		padding-left: 3px;
	}
	
	#vntags span.cat_ero {
		box-sizing: border-box;
		border-left: 2px solid #ff0000;
		padding-left: 3px;
	}
	
	#vntags span.tagspl1 {
		box-sizing: border-box;
		border-right: 5px solid #800000;
	}
	
	#vntags span.tagspl2 {
		box-sizing: border-box;
		border-right: 5px solid #ff0000;
	}
	
	#vntags span:hover {
		border-left-width: 10px;
		border-right-width: 10px;
	}
	
	#tagops a[href='#cont'], #tagops a[href='#tech'], #tagops a[href='#ero'] {
		border-top: 2px solid #333;
	}
	
	#tagops a.tsel[href='#cont'] {
		border-top: 2px solid #00ff00;
	}
	
	#tagops a.tsel[href='#tech'] {
		border-top: 2px solid #0000ff;
	}
	
	#tagops a.tsel[href='#ero'] {
		border-top: 2px solid #ff0000;
	}
	
	#tagops a:nth-of-type(5) {
		border-top: 2px solid #800000;
	}
	
	#tagops a:nth-of-type(6) {
		border-top: 2px solid #ff0000;
	}
	
	/*Style part taken from Better VNDB.org*/
	.taglvl {
		font-size: 3px !important;
	}
	
	div.vndetails td.anime b, #vntags b, .tagvnlist .tc1 i, .browse.taglinks .setfil, table.tgl .tc_allvote i, div.taglvl0, svg .edge text {
		font-size: 10px !important;
	}
	
	div.thread i.lastmod, div.vnimg i, .taglvl0 {
		font-size: 12px !important;
	}
	
	li, td, #maincontent h2 b, .menubox h2 a, #menulist, #menulist h2, #fil_div h3, #staff, .charsum, #screenshots, .votestats, #footer, .mainbox p {
		font-size: 13px !important;
	}
	
	input.text, input.submit, select, textarea, .mainbox.threelayout h2, .docs h4, .docs dt b, #iv_view a {
		font-size: 14px !important;
	}
	
	.mainbox h1, .mainbox h2 {
		font-size: 15px !important;
	}
	
	.mainbox h2, .mainbox.threelayout h1, .docs h3 {
		font-size: 16px !important;
	}
	
	h1.boxtitle, h1.boxtitle a {
		font-size: 19px !important;
	}
	
	.mainbox h1 {
		font-size: 21px !important;
	}
	
	#debug h2 {
		font-size: 22px !important;
	}
	
	#header h1, #header h1 a {
		font-size: 26px !important;
	}
	
	#header h1 a {
		color: #FFF !important;
		text-shadow: 1px 0px 20px #FFF;
		text-transform: capitalize;
	}
	
	#header h1 a:hover {
		/*color: #FFFFB0 !important;*/
		/*text-shadow: 1px 0px 20px #FFFFB0;*/
		text-shadow: 1px 0px 25px #FFF;
	}
	
	#menulist {
		top: 60px !important;
	}
	
	#maincontent {
		top: 39px !important;
	}
	
	#header {
		top: 10px !important;
		left: 100px !important;
	}
	
	.votegraph td div {
		height: 16px !important;
	}
	
	.votestats {
		width: 680px !important;
	}
	
	#maincontent > div.mainbox.releases > table > tbody > tr > td.tc4 > a:after,
	#maincontent > div.mainbox.browse.vnbrowse > table > tbody > tr > td.tc1 > a:after,
	#maincontent > form > div > table > tbody > tr > td.tc1 > a:after,
	#maincontent > form > div > table > tbody > tr > td.tc3_5 > a:after,
	#maincontent > div.mainbox.browse.charb > table > tbody > tr > td.tc2 > a:after,
	#maincontent > div > div.vndetails > table > tbody > tr > td > a:not([rel^='nofollow']):not([href^='http://en.wikipedia.org/']):not([href^='http://novelnews.net/']):after {
		content: " (" attr(title)")";
		color: #FFF;
	}
	
	table td, table > tbody > tr > td > acronym.icons {
		vertical-align: middle !important;
	}
	
	#maincontent > div.mainbox.browse.vnbrowse > table > tbody > tr > td.tc2 {
		width: 150px !important;
	}
	
	#maincontent > div.mainbox.browse.vnbrowse > table > tbody > tr > td.tc3 {
		width: 165px !important;
	}
	
	#maincontent > div.mainbox.browse.vnbrowse > table > tbody > tr > td.tc4 {
		width: 82px !important;
	}
	
	#maincontent > div.mainbox.browse.vnbrowse > table > tbody > tr > td.tc5 {
		width: 92px !important;
	}
	
	/*Code for screenshot hover next/previous and click outside image to close*/
	#ivimg:before {
		position: absolute;
		content: '';
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
		z-index: 15;
	}
	
	#ivclose:before {
		cursor: default;
		position: fixed;
		content: '';
		top: -10px;
		left: 0;
		width: 100%;
		height: 100%;
		z-index: 10;
	}
	
	#ivclose {
		opacity: 0;
		z-index: 5;
	}
	
	#iv_view {
		box-shadow: 0 0 0 3000px rgba(0, 0, 0, 0.5);
		overflow: hidden;
		z-index: 5;
		padding-top: 0px !important;
		padding-bottom: 0px !important;
	}
	
	#iv_view img {
		display: block;
		position: absolute;
		bottom: 10px;
	}
	
	#ivnext:hover:after {
		opacity: 100;
		width: 60px;
	}
	
	#ivprev:hover:after {
		opacity: 100;
		width: 60px;
	}
	
	#ivnext:after {
		opacity: 0;
		color: white;
		content: "‚ùØ";
		font-size: 50px;
		text-shadow: 0px 0px 10px black;
		position: absolute;
		display: block;
		right: 0px;
		width: 50px;
		top: 45%;
		z-index: 15;
		-webkit-transition: 0.25s ease;
		-moz-transition: 0.25s ease;
		-o-transition: 0.25s ease;
		transition: 0.25s ease;
	}
	
	#ivprev:after {
		opacity: 0;
		color: white;
		content: "‚ùÆ";
		font-size: 50px;
		text-shadow: 0px 0px 10px black;
		position: absolute;
		display: block;
		left: 0px;
		width: 50px;
		top: 45%;
		z-index: 15;
		-webkit-transition: 0.25s ease;
		-moz-transition: 0.25s ease;
		-o-transition: 0.25s ease;
		transition: 0.25s ease;
	}
	
	#ivnext {
		color: transparent;
		position: absolute;
		display: block;
		right: 0px;
		height: 100%;
		width: 35%;
		top: 0px;
		z-index: 15;
	}
	
	#ivprev {
		color: transparent;
		position: absolute;
		display: block;
		left: 0px;
		height: 100%;
		width: 35%;
		top: 0px;
		z-index: 15;
	}
	
	/*Minor interface change code (rounded tabs & boxes + footer no underline)*/
	.maintabs:not(.notfirst), .browsetabs {
		position: relative;
		display: block;
		right: -2px;
		width: 99.7%;
	}
	
	.notfirst {
		position: relative;
		display: block;
		left: 2px;
		width: 99.7%;
	}
	
	.maintabs li a {
		border-radius: 5px 5px 0 0;
	}
	
	.bottom li a {
		border-radius: 0 0 3px 3px;
	}
	
	.summarize {
		border-radius: 3px 3px 0px 0px;
	}
	
	.summarize_more {
		border-radius: 0px 0px 3px 3px;
		position: relative;
		display: block;
		top: -1px;
	}
	
	.mainbox, .menubox, #iv_view, .threelayout td {
		border-radius: 3px 3px 3px 3px;
	}
	
	#footer a {
		text-decoration: none;
	}
	
	/*Custom code for title under game thumbnails on homepage*/
	@-moz-document url("https://vndb.org/") {
		
		table td, table > tbody > tr > td > acronym.icons {
		  vertical-align: top !important;
	  }
		
		div.mainbox h1, p.description {
			text-align:left;
		}
		
		div.mainbox {
			text-align:center;
		}
		
		p.screenshots {
			display: inline;
		}
		
		p.screenshots img {
			padding-bottom:15px;
		}
		
		p.screenshots a {
			position: relative;
		}
		
		p.screenshots a:after {
			content: attr(title);
			position: absolute;
			top: 0;
			width: 100%;
			left: 0;
			overflow: hidden;
			white-space: nowrap;
			text-overflow: ellipsis;
		}
	}
}
