@-moz-document url("about:Home"), url("about:home") {

#sync, #brandLogo, #snippetContainer, #searchEngineLogo, #restorePreviousSessionSeparator
{ display: none !important; }

/* bottom icons */
.launchButton::before
{ display: none !important; }

html
{ background: #77ADCE url("http://image.noelshack.com/fichiers/2016/09/1456743252-a-hero-s-work-by-helixel-d7pnv25.png") no-repeat center fixed !important; 
