box-shadow: -2px 4px 14px 2px rgba(0, 106, 255, 1) !important; }

#searchText, #searchSubmit
{ -moz-appearance: none !important; 
font-size: 23px !important;
color: #83D8DE !important;
text-shadow: 1px 2px 8px #97947F !important;
font-weight: bold !important;
font-family: Black Night, sans-serif !important;
padding: 0px !important;
outline: 0 none !important;
border-radius: 4px !important;
border: 1px solid !important;
border-left-color: #1C1916 !important;
border-top-color: #1C1916 !important; 
border-bottom-color: #77ADCE !important;
border-right-color: #77ADCE !important;
box-shadow: 0px 4px 8px rgba(0, 106, 255, 1) !important; }

#searchText, #searchSubmit
{ background: radial-gradient(at top, #83D8DE, rgba(73,83,87, .4), transparent) !important; }

#searchText:hover, #searchText:focus
{ background: radial-gradient(at top, #77ADCE, rgba(73,83,87, .2), rgba(110,125,102, .2)) !important; }

#searchSubmit
{ margin-left: 2px !important;
padding: 0px 5px !important;
font-weight: normal !important;
color: #83D8DE !important;
text-shadow: 1px 0px 0px #000, 2px 2px 2px rgba(86,97,102, .5) !important;
font-family: Papyrus /*Enviro *//*Goudy Old Style*/ /*Viner Hand ITC*/ !important; }

#searchSubmit:hover
{ text-shadow: 1px 0px 0px #000, 1px 2px 4px #77ADCE !important;
background: radial-gradient(at top, #77ADCE, rgba(162,163,146, .3), transparent) !important; }

/* ========== links bar */

#launcher, #restorePreviousSession
{ background: transparent !important;
border: none !important;
box-shadow: none !important; } 

#launcher
{ position: fixed !important;
bottom: 40% !important;
margin-left: 6px !important;
display: block !important; 
text-align: center !important; 
white-space: nowrap !important; }

.launchButton
{ background: transparent !important;
border: none !important;
box-shadow: none !important; }

#launcher button
{ -moz-appearance: none !important;
padding: 0 6px !important;
font-family: Black Night  /*Enviro, *//*Goudy Old Style*/ /*Papyrus*/, sans-serif !important;
font-size: 22px !important;
/* ========== buttons color v */
color: #83D8DE !important;
text-shadow: 1px 2px 1px #000, 1px 1px 4px #000 !important;
