css:
@-moz-document regexp('https?://geizhals\\.(at|de|eu)/.*') {
  * {
    box-shadow: none !important;
    box-sizing: border-box !important;
    outline: none !important;
    font-family: 'Lato', sans-serif;
    border-radius: 0 !important;
    text-shadow: none !important;
    text-decoration: none !important;
    transition: opacity .3s ease-in-out, letter-spacing .3s ease-in-out, color .3s ease-in-out, background-color .3s ease-in-out, transform .3s ease-in-out;
  }
  body#ghbody {
    font-size: 14px;
    background-color: #fff;
    overflow-x: hidden;
  }
  #gh_hdr,
  #hdr_right {
    background: #005096 none !important;
  }
  #hdr_right {
    padding-top: 33px;
  }
  #gh_content_wrapper {
    background-color: #fff;
  }
  #gh_center_search {
    padding: 25px 0;
  }
  .gh_srch_wrp {
    height: 42px !important;
    padding: 5px 10px;
    position: relative;
  }
  .gh_srch_wrp > input {
    height: 42px;
    line-height: 42px;
    padding: 0 5px;
  }
  div#gh_srch_ctrls {
    position: absolute;
    right: 0;
    top: 0;
    height: 42px;
    width: 160px;
    margin: 0;
    background-color: #ddd;
  }
  span.gh_scat_wrp {
    border: none;
    position: absolute;
    top: 9px;
    left: 7px;
    width: 120px;
  }
  select.gh_searchkat {
    position: absolute;
    top: 3px;
    font-size: 12px;
  }
  #gh_suchen_bt_i {
    background: none;
    height: 42px;
    width: 35px;
  }
  #gh_srch_ctrls:before {
    content: '';
    display: block;
    height: 12px;
    width: 12px;
    border: 2px solid #555;
    border-radius: 50%;
    position: absolute;
    right: 10px;
    top: 12px;
    pointer-events: none;
  }
  #gh_srch_ctrls:after {
    content: '';
    display: block;
    height: 2px;
    width: 9px;
    background: #555;
    border-radius: 50%;
    position: absolute;
    right: 18px;
    top: 23px;
    transform: rotate(-45deg);
    pointer-events: none;
  }
  #gh_hdr_toplogo {
    position: absolute;
    left: 0;
    top: 46px;
  }
  #gh_hdr_toplogo a {
    display: block;
  }
  #gh_hdr_toplogo a img {
    opacity: 0;
  }
  #gh_hdr_toplogo:after {
    content: 'GEIZHALS';
    display: inline-block;
    font-weight: 300;
    letter-spacing: 2px;
    line-height: 42px;
    font-size: 20px;
    padding: 25px;
    position: absolute;
    left: 0;
    top: -15px;
    pointer-events: none;
  }
  div#gh_hdr_toplinks {
    position: absolute;
    right: 0;
    top: 0;
    font-size: 0;
    width: 100%;
    background-color: #223;
    text-align: right;
    padding: 8px 0;
  }
  div#gh_hdr_toplinks img {
    display: none;
  }
  a.gh_toplinks {
    font-size: 12px;
    margin-right: 15px;
    opacity: .42;
  }
  div#gh_hdr_toplinks a:not(.gh_toplinks):before {
    display: inline-block;
    font-size: 12px;
    margin-right: 10px;
    color: #fff;
    opacity: .42;
    content: 'settings';
  }
  div#gh_hdr_toplinks a:not(.gh_toplinks):hover:before {
    opacity: .875;
  }
  div#gh_hdr_toplinks a:not(.gh_toplinks) img {
    display: none;
  }
  #gh_hdr_tabs {
    max-width: none;
  }
  a.gh_toplinks:hover {
    opacity: .875;
  }
  li.gh_tabs {
    background: #223 none;
  }
  a.gh_tabs {
    border-color: #555;
    padding: 15px 0;
    text-transform: uppercase;
    background: none !important;
  }
  li.gh_tabs:hover {
    background-color: #000;
    letter-spacing: 2px;
  }
  div#gh_statline {
    display: none;
  }
  li.mkat {
    border: 1px solid #eee;
  }
  li.mkat:hover {
    background-color: #eee;
  }
  .mkatimg {
    padding: 10px;
  }
  .mkatimg img {
    width: 100%;
    height: auto;
  }
  li.mkat > .ovhid {
    padding: 0;
  }
  li.mkat > .ovhid h2 {
    padding: 5px 0;
    text-transform: uppercase;
    font-weight: 300;
  }
  li.mkat > .ovhid h2 a {
    color: #333;
  }
  li.mkat > .ovhid p.x {
    font-size: 12px;
    padding: 0 0 8px 0;
  }
  li.mkat > .ovhid p.x a {
    color: #888;
  }
  li.mkat > .ovhid p.x a:hover {
    color: #005096;
  }
  .msbfix_inner {
    margin-right: 25px;
  }
  div#gh_leftnav {
    width: 185px;
    padding: 0;
    border-right: 1px solid #ddd;
  }
  .right_fix,
  div#gh_blah {
    width: 320px;
    position: absolute;
    background-color: #fff;
    right: 0;
    padding: 10px;
    border: 1px solid #ddd;
    transform: translateX(100%);
    z-index: 10;
  }
  .ghnavi:before {
    display: none;
  }
  div#gh_leftnav > .plinks,
  div#gh_leftnav > iframe {
    display: none;
  }
  .gh_stat_nav {
    border-top: 0 none;
    opacity: .25;
  }
  li.ghnavi {
    border-bottom: 1px solid rgba(255, 255, 255, 0.125);
    margin: 0;
  }
  li.ghnavi a {
    display: block;
    padding: 5px 10px;
  }
  li.ghnavi a:hover {
    background-color: #e9eef7;
    color: #333;
  }
  .right_fix:before,
  div#gh_blah:before {
    content: '<<';
    display: block;
    width: 25px;
    height: 25px;
    background-color: #fff;
    border: 1px solid #ddd;
    border-right: 0;
    position: absolute;
    top: -1px;
    left: -26px;
    color: #ccc;
    line-height: 23px;
    text-align: center;
  }
  .right_fix:hover,
  div#gh_blah:hover {
    width: 320px;
    transform: translateX(0);
  }
  div#gh_content_wrapper {
    padding: 15px 35px 0 35px;
  }
  .gh_listtitle {
    background: none;
    font-weight: 700;
    font-size: 25px;
    text-transform: uppercase;
    margin: 0 0 10px;
    padding: 0 0 10px;
    border: 0 none;
  }
  .box10h {
    background-color: #efefef;
    text-transform: uppercase;
  }
  .gh_listtitle, div.arthdr {
    background: transparent;
    padding: 10px 0 15px 0;
    margin-bottom: 0;
    position: relative;
    border: none;
  }
  .gh_listtitle:after,
  div.arthdr:after {
    content: '';
    display: block;
    width: 10%;
    height: 4px;
    background-color: #C8CED5;
    position: absolute;
    bottom: -2px;
  }
  div#monav {
    background: transparent;
    padding: 0;
  }
  span.monav {
    background: #efefef;
    border-color: #efefef !important;
  }
  input, select, textarea {
    background: #fff;
    border: 1px solid;
    color: #223;
  }
  input[type=submit], button {
    background: #222233;
    color: #fff;
    cursor: pointer;
    padding: 5px 10px;
  }
  button {
    background: #005096;
    border: 1px solid;
  }
  .productlist__breadcrumb {
    background: none;
    padding-left: 45px;
  }
  .productlist__name, .gh_gradient0, #gh_proddesc {
    background: inherit;
  }
  #gh_proddesc {
    border: none;
    padding: 0;
  }
  .productlist__product:nth-child(2n), #xf_table.expanded .xf_tr:nth-child(2n+1), .offer:nth-child(2n) {
    background: #f4f4f4;
  }
}
