  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
body{font-family: "Open Sans", sans-serif !important;} :focus {outline: none !important;} /* Background */ body{background:#636363;} /* Couleurs des polices */ a,.DataList a.Title,.forumPosts a.topic, .accordion h3.title {color:#2B2B2B} p,td,div,label,.pagebar a, .newsWrapper p, .results tbody td a{color:rgba(0,0,0,1)} /* Les tableaux */ .results *{border:none !important;} .results tbody tr:nth-child(even){background:rgba(0,0,0,.05);} .results tbody tr:hover{background:rgba(0,0,0,.5);} /* Images fantômes */ img[src=non] {display:none !important;} /* Boutons */ .btn, .redbtn { -moz-appearance: none !important; color: #FFFFFF !important; cursor: pointer !important; font-size: 13px; font-weight: 700; min-width: 80px; display: block !important; text-align: center; padding: 7px 10px; margin: 4px; background: #404040 !important; box-shadow: none !important; border: 0 !important; border-radius: 3px !important; transition: background 0.3s, box-shadow 0.3s !important;} .btn:hover, .redbtn:hover {background: #2B2B2B !important;} .btn:active, .redbtn:active {background: #404040 !important;box-shadow: inset 1px 1px 3px rgba(0,0,0,0.5) !important;} .altbtn{background: #2B2B2B !important;box-shadow: none !important; border: 0 !important;} .altbtn:hover{color:black;} /********************* a classer ****************/ /* 2 - Content *****************************************************/ .wrapper{min-width:800px !important;} .contentWrapper{min-width:none !important;max-width:none !important;} .accordion, .accordion>div, .accordion>h3{border-color:rgba(0,0,0,.2) !important} hr{background:rgba(0,0,0,.2);} .accordion h3{background-color:rgba(0,0,0,.1) !important} .accordion>div{background-color:rgba(0,0,0,.05) !important} .content {margin-left: 200px !important;margin-right: 0 !important;padding:0 !important;max-width:none !important;min-width:none !important;} .block>div{padding:0 !important;} .block h2 {background: #404040 !important;border: none !important;border-radius: 0 !important;box-shadow: none !important;margin: 0 !important;padding: 14px !important;} .block h2 span {background:none; border: none !important;border-radius: 0 !important;color: #FFFFFF !important;font-weight:300;font-size:25px} .torrentDetails.block h2 {padding:7px !important;} .torrentDetails.block h2 span {font-size: 16px;} /* Désactivation des animations inutiles */ .categories-icons{transition:none !important;} /* Commentaires */ .comment{border:0 !important;margin:0 !important;} #comment-form .comment{background:rgba(0,0,0,.1);border-color:rgba(0,0,0,.5);} #comment-form .comment tr:first-child{font-size:12px !important;} #comment-form .comment tr:first-child a:before{font-size:12px !important;content:"règles" !important;} #comment-form .comment tr:first-child a{font-size:0px !important;color: #404040 !important;} /*Boite*/ #info{position:fixed !important;top:20px !important;left:calc(50% - 260px)!important;background:white;box-shadow:0 0 20px rgba(0,0,0,0.5)!important;} body>img[src="http://www.t411.al/themes/blue/css/overlay/white.png"]{display: none!important;} /*Bandeau d'info*/ #messages {top:0px !important;} div.nopad{padding:10px !important} /***************************** Desactiver les pubs **********************************/ .results tbody a[target="_blank"], .torrentDetails > .align-center, .content > .align-center, .block.description > article > div > center, .block.description > article > center, .wrapper .contentWrapper #left, .wrapper .contentWrapper #right, .banner728, .content .ads, .block .bottomads, .details>center, .details>table>tbody tr:last-child, .details>center, .details>table>tbody tr:nth-last-child(2), .details>table>tbody tr:nth-last-child(7), .vtt{display:none;} /********************************************************************************/ /********************** Header (Menus et Login Box) *************************/ /********************************************************************************/ .loginWrapper { display: none !important; overflow:visible !important; position: fixed !important; height:0px !important; min-height: 0px; } .active .loginWrapper {display:block !important; } /* Menu général */ .headerPlace.active + .wrapper{filter: blur(20px);-webkit-filter: blur(20px); transition: filter 1s, -webkit-filter 1s;} .headerPlace .userBox { position:fixed; top:calc(50% - 230px); left:calc(50% - 320px + 100px); background: #fff; height: 390px !important; width: 500px !important; padding: 40px 60px 30px 80px !important; box-shadow:0 0 10px rgba(0,0,0,0.5) !important; z-index:20 !important;} .userBox .column {width: auto !important;} .userBox .column li { background: #404040 !important; height: 45px !important; line-height: 45px !important; margin: 13px !important; text-align: center !important; transition: background 0.3s !important; width: 210px !important; text-shadow:0 0 5px rgba(0,0,0,0.5) !important} .userBox .column li a { display: block !important; height: 100% !important; text-decoration: none !important; width: 100% !important; color:#FFFFFF !important;} .userBox .column li:hover {background: #2B2B2B !important;} .userBox .column:nth-of-type(3) {margin-left: 120px !important;} .userBox .avatar-big {display:none !important;} /* Fin du Menu général */ /******************** A - Boite de connexion *******************************/ /* A1 - Boite de connexion - Partie haute*/ .loginBox{ position:fixed; overflow:hidden !important; top:calc(50% - 195px) !important; margin-left: calc(50% - 290px + 100px) !important; padding: 10px !important; padding-top: 30px !important; width: 560px !important; height: 350px !important; background: white !important; border-radius: 5px !important; box-shadow: 0 0 0px 2000px rgba(0,0,0,.5),0 0 10px black !important;} #loginform{ width:330px !important; height:124px !important; margin-left:90px !important; text-align: right !important; padding-top:40px !important; padding-bottom:40px !important; } .userInput, .passInput{ -moz-appearance: none !important; font-size: 16px !important; padding: 5px 10px !important; margin: 0 !important; height: 40px !important; width: 180px !important; margin-bottom: 10px !important; color: #555 !important; font-family: 'Open Sans', sans-serif !important; background: #EDEDED !important; border: 1px solid #D6D6D6 !important; border-radius: 3px !important; box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.07) !important;} .userInput:focus, .passInput:focus{ -moz-appearance: none !important; box-shadow: rgba(14, 78, 134, 0.5) 0 0 8px !important; border: 1px solid #bbb !important;} .username, .password{ margin-right: 15px !important; font-family: " Open Sans ", sans-serif !important; font-size: 14px !important; font-weight: normal !important; color: #444 !important; } .loginbtn { -moz-appearance: none !important; width:200px !important; height:40px !important; margin: 20px 40px 10px !important; padding: 12px !important; border: none !important; border-radius: 4px !important; font-size:13px !important; color: #FFFFFF !important; background: #404040 !important; transition:background 0.3s, padding 0.3s !important;} .loginbtn:hover, .loginbtn:focus {background: #2B2B2B !important;} .loginbtn:focus {padding-left: 150px !important;background: #2B2B2B !important;} .rememberMe{margin-left: 140px !important;margin-top:4px !important;} .remMe{margin-top:0 !important;} /* A2 - Boite de connexion - Partie basse*/ .register{ position:relative !important; width:580px !important; margin-top:40px !important; margin-right:-150px !important; height:70px !important; text-align: center !important; background: #eaeaea !important; border-top: 1px solid #e5e5e5 !important; padding:0 !important; } .register li{ float:none !important; margin-top:10px !important; border-left: none !important;} .register li a{ font-family: " Open Sans ", sans-serif !important; font-size: 14px !important; font-weight: normal !important; display:inline-block !important; padding:5px !important; } .register li:first-of-type:before{ content:'Vous n\'avez pas encore de compte ? \00a0'; font-family: " Open Sans ", sans-serif !important; font-size: 14px !important; font-weight: normal !important;} /* A - FIN - Boite de connexion */ /**************************** B - Menu latéral *****************************/ .headerPlace { height: 100% !important; margin: 0px !important; min-width: inherit !important; position: fixed !important; width: 200px !important; z-index: 100 !important;} .headWrapper { height: 100% !important; padding-top: 0px !important; position: relative !important; width: 200px !important; box-shadow: rgba(0,0,0,0.8) 0 0 5px !important; background: #2B2B2B !important;} /* B1 - Menu latéral - Partie Haute*/ .head {height: auto !important;padding-top: 0px !important;width: auto !important;} .logo{position: relative !important;height:120px !important;background-position:50% 50% !important;} .toolbar {float: inherit !important;height: auto !important;position: relative !important;right: 0 !important;top: 0 !important;} .searchBar {float: inherit !important;height: auto !important;width: auto !important;} .searchBtn {display: none !important;} .searchInput { border-radius: 0px !important; height: 25px !important; margin: 0 !important; transition: width 0.5s, box-shadow 0.5s !important; width: 189px !important;} .searchInput:focus { width: 280px !important; box-shadow: 2px 1px 6px black !important; border:0 !important;} .searchHistory { background: #404040 !important; clear: both !important; display: block !important; height: 5px !important; margin-top: 0px !important; width: 200px !important;} .loginBar { background: none !important; clear: both !important; float: inherit !important; height: auto !important; padding: 3px !important; text-align: center !important;} .loginBar a.logout { background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') -1209px 3px no-repeat !important; padding:12px !important; padding-top:15px !important; font-size:0px !important; transition: opacity 0.4s !important; opacity:0.6 !important;} .loginBar a.logout:hover {opacity:1 !important;} .loginBar a.reg {margin-left: 0px !important;margin-right: 4px !important;} .loginBar a.mail{ background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') -1573px -1px !important; transition: opacity 0.4s !important; opacity:0.6 !important; text-decoration:none !important;} .loginBar a.mail:hover{opacity:1 !important;} .loginBar span:nth-child(7){font-size:0;} .loginBar span:nth-child(7) strong{font-weight:100; font-size:40px;margin-top:10px;} .loginBar span { display: inline-block !important; float: inherit !important; margin: 0 !important; margin-left: 2px !important; margin-right: 2px !important; color:#FFFFFF !important;} .loginBar span.sep {display: none !important;} .loginBar span.slideToo { background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABgAAAAYCAYAAADgdz34AAAAmUlEQVRIS2NkoDFg/P//vwPQjvlArEBlux4AzUsEWQBiyFPZcJhxD0AWfADy+GlkwUeQBQlAwyfQwJKPQDMLGGnkcrixoxYQDGHaBxE0FfUDnSJA0DmkKQAlf3BGo2U+oHlGewjyQQA0o1G7uHgINDeB9pFMWryRrnrUBwTDDFZc0yqjFdI6o32gtQXgjAaq9BfQoF6mT0YDAD3vPSK+YKNlAAAAAElFTkSuQmCC') center bottom no-repeat!important; clear: both !important; color: #FFFFFF !important; display: block !important; font-size: 0px !important; min-height:30px; margin-bottom: 20px !important; padding: 0px !important; padding-top: 20px !important; text-align: center !important;} .active .loginBar span.slideToo:after { transition: background 0.3s !important; top:0px !important; background: rgba(0,0,0,0.5) !important;} .loginBar span.slideToo:after { transition: background 0.3s !important; content:'' !important; display: block !important; position:fixed !important; top:5000px !important; left:200px !important; height:100% !important; width:100% !important; z-index:-1 !important; background: rgba(0,0,0,0) !important;} .loginBar span:nth-of-type(2) {clear: both !important;} .loginBarSide {display: none !important;} /* B12 - Menu latéral - Partie basse*/ #lavamenu {margin: 0 !important;} #lavamenu li { border: none !important; border-radius: 0 !important; height: 38px !important; margin: 0px !important; padding: 0px 0px !important; text-shadow: 0 0 3px black !important; width: 200px !important;} #lavamenu li a {margin: 0px !important;padding: 10px 0px !important;color: #FFFFFF !important;} #lavamenu li ul { background: #2B2B2B !important; border: 0 !important; border-radius: 0 10px 10px 0px !important; left: 200px !important; margin: 0px !important; overflow: hidden !important; padding: 0px !important; position: relative !important; top: -114px !important; z-index: -1 !important; box-shadow: inset 10px 0px 10px -10px rgba(0,0,0,0.8) !important;} #lavamenu li ul li:hover {background: #404040 !important;box-shadow: inset 10px 0px 10px -10px rgba(0,0,0,0.8) !important;} #lavamenu li:nth-of-type(7) ul {top: -38px !important;} #lavamenu li:nth-of-type(8) ul {top: -260px !important;} #lavamenu .rss{padding: 10px 0px 10px 20px !important;background-position:7% 50% !important;} .backLava {background: #404040 !important;left:0 !important;} #lavamenu>li:nth-last-child(3){display:none !important;}  /* B - FIN - Menu latéral */ .ui-autocomplete { z-index: 101 !important; } /********************************************************************************/ /*************************** Pages spécifiques ******************************/ /********************************************************************************/ /*Boutons URL raccourcie*/ h2 .shortlink{ display:block !important; position:absolute !important; font-size:0px !important; height:40px !important; width:50px !important; top:-7px !important; right: -6px !important; padding:0 !important; margin:0 !important;} h2 .shortlink:hover{background:#2B2B2B !important;} h2 .shortlink:before{ display:block !important; position:absolute !important; content:"" !important; background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') -1177px 5px no-repeat !important; height:30px !important; width:30px !important; top:4px !important; left:11px !important;} /*Boite URL raccourcie*/ #shortlink 
  ner .shorthref{ -moz-appearance: none !important; box-shadow: inset 0 0 6px rgba(0,0,0,0.4) !important; border:none !important; padding:5px 10px !important; width: 355px !important;} #shortlink 
  ner hr{display:none !important;} /*Telecharger plus tard*/ .share>.alignleft a{ height:42px; line-height:42px; position:fixed; bottom:0px; left:242px; display:block; background: #404040; color:#FFFFFF; padding: 0px 20px; transition: all 0.3s; font-size: 0; font-weight: 700; border: 0; text-decoration:none; z-index:11;} .share>.alignleft a:before{font-size: 13px;content:"Télécharger plus tard";} .share>.alignleft a:hover {background: #2B2B2B;} .share>.alignleft a:active {background: #404040;box-shadow: inset 1px 1px 3px rgba(0,0,0,0.5);} /*Boutons suivant et précédents*/ .navigation{ -moz-appearance: none !important; padding:0 !important; background: #404040 !important; margin:0 !important; width:calc(100% - 200px) !important; height:42px !important; position:fixed !important; left:200px; bottom:0 !important; margin-left:0 !important; z-index:10 !important; overflow:visible !important; border-top: 1px solid #2B2B2B !important;} .navigation .alignleft a, .navigation .alignright a{font-size:0 !important;color:black !important;display:block !important;position:relative !important;} .navigation .alignleft a:after, .navigation .alignright a:before{font-size:14px !important;color:black !important;background:red !important;padding:21px !important;display:block !important;} .navigation .alignleft a:after{ content:" " !important; background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') #404040 -393px 12px no-repeat !important; box-shadow: inset 10px 0px 10px -10px rgba(0,0,0,0.8) !important;} .navigation .alignleft a:hover:after{background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') #2B2B2B -393px 12px no-repeat !important;} .navigation .alignright a:before{content:" " !important;background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') #404040 -422px 12px no-repeat !important;} .navigation .alignright a:hover:before{background: url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') #2B2B2B -422px 12px no-repeat !important;} /*Editer et Supprimer*/ .block.navigation{ width: 100px !important; height: 40px !important; position: absolute !important; right:50px !important; top: 0px !important; margin-left: 0px !important; border:0 !important; left:auto !important;} .block.navigation a{ display:block !important; padding:0px !important; margin:0px !important; border:none !important; background:none !important; border-radius: 0 !important; box-shadow:none !important; font-size:0px !important; height:40px !important; width:50px !important; min-width: 0px !important;} @media screen and (max-width: 1279px) { .block.navigation,.block.navigation a{height:38px !important;} } .block.navigation a:first-child:before{ display:block !important; position:absolute !important; background:url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') -665px 5px no-repeat !important; content:" " !important; width:30px !important; height:30px !important; top:5px !important; left:9px !important;} .block.navigation a:nth-child(2):before{ display:block !important; position:absolute !important; background:url('https://cdnssl.mediafire.com/images/icons/ico30/ico30_reversed-v8.png') -1295px 5px no-repeat !important; content:" " !important; width:30px !important; height:30px !important; top:5px !important; left:58px !important;} .block.navigation a:hover{background: #2B2B2B -665px 5px no-repeat !important;} .navigation, .share, .votes, .ads {border-bottom: 0 !important;} /*Note, Télécharger, Demander seed, Dire merci, Signaler et Rappeler vote*/ #stars.aligncenter{display:block;padding: 3px 20px 5px;margin:0px;float:right;} .details tr:nth-last-child(4){ display:block !important; position:fixed !important; bottom:0px !important; right:41px !important; background: #404040 !important; z-index:11 !important;} .details tr:nth-last-child(4) td{padding: 0 !important;border: 0 !important;} .details tr:nth-last-child(4) td>a{ -moz-appearance: