important; transition: width 0.3s .5s, padding 0.3s .5s!important;} /* Desciption du torrent */ .block.description article{text-align:center!important;padding:10px!important;} .block.description article .align-center{padding: 0!important;} .block.torrentDetails .block.description img{max-height:400px;max-width:600px;width:auto!important;} /*********** Mon compte ******************/ .private{background:rgba(0,0,0,.05);border:none;padding:5px !important;} /* Page Mes abonnements */ .subscriptions .subscriptions-head .title{box-shadow: 10px -10px 10px -10px rgba(0,0,0,.7) inset;opacity:0.7;background:#777;padding:15px 0;color:white;} .subscriptions .subscriptions-head .title:first-child{box-shadow: -10px -10px 10px -10px rgba(0,0,0,.8) inset;} .subscriptions .subscriptions-head .title.active{box-shadow: none;opacity:1;} /* Chat */ #messages, #bar{border-bottom:1px rgba(0,0,0,.5) solid;} #messages .avatar{border-radius:100px !important;} #messages .avatar img{border-radius:100px !important;} #messages>div{margin:4px 0 !important;} #messages .message {background:rgba(0,0,0,0.1) !important;border:none;} #messages .odd .message {background: rgba(0,0,0,.2) !important;} /**************************************** Forum t411 **********************************************/ /** Menu supérieur du forum**/ #Frame #Head{border:0;border-radius:0;background:none;padding:0;} #Frame #Head .Menu{background:#404040;padding:0 0 0 15px;margin:0;} .Vanilla #Frame #Head .Menu{width:calc(100% - 203px);} .Vanilla.Post #Frame #Head .Menu {width:auto;} #Frame #Head .Menu #Menu *{background:none;color:#FFFFFF;text-decoration:none !important;margin: 0;} #Frame #Head .Menu #Menu>li ul{padding:10px 0;} #Frame #Head .Menu #Menu>li>a{padding:6px 20px;} #Frame #Head .Menu #Menu>li:hover, #Frame #Head .Menu #Menu>li ul{background:#2B2B2B;} #Frame #Head .Menu #Menu>li li a:hover{background:#404040;} #Frame #Head .Menu #Menu>li ul{width:800px;-webkit-columns:2 300px;-moz-columns:2 300px;columns:2 300px;} #