css:
@namespace url(http://www.w3.org/1999/xhtml);

@document url-prefix("https://beta.reddit.com/r/Windows10"){
  #header #header-bottom-right{
    background-color: rgba(0,0,0,0.2) !important;
    padding-top: 11px !important;
    padding-left: 43px !important;
    height: auto !important;
    width: auto !important;
    position: absolute !important;
    bottom: 30px !important;
    border-radius: 0px !important;
    box-shadow: none !important;
  }
  .tabmenu{
    width: 100% !important;
  }
  #header-bottom-right .user, #header-bottom-right #modmail, #header #header-bottom-right #mail.nohavemail, #header #header-bottom-right .choice, #header #header-bottom-right .logout a{
    outline:none !important;
    border-color: rgba(0,115,166,0.3) !important;
    border-width: 3px !important;
    border-style: solid !important;
    top: auto !important;
  }
  #header-bottom-right #modmail{
    right: 335px !important;
    
  }
  #header-bottom-right .user:hover, #header-bottom-right #modmail:hover, #header #header-bottom-right #mail.nohavemail:hover, #header #header-bottom-right .choice:hover, #header #header-bottom-right .logout a:hover{
   border-color: white !important;
   outline: none !important;
    background-color: #1992FF !important;
    transition: background-color 0.15s ease-in-out !important;
}
}
