css:
@-moz-document url-prefix("http://www.byfandom.org/") {
body,
.ow_canvas,
.jhtmlarea.ow_smallmargin,
.ow_bg_color,
.ow_box.ow_stdmargin.clearfix,
.ow_box_cap,
.ow_box.ow_stdmargin.clearfix.profile-BLOGS_CMP_UserBlogWidget,
.ow_box.ow_stdmargin.ow_break_word {
    /* This colors the background of the page */
    
    background-color: #2E2E2E !important;
}
body.htmlarea_styles {
    font-family: 'Lucida Grande', Verdana, Tahoma;
    color: white !important;
    font-size: 20px;
    line-height: 20px;
}
input[type=text], input[type=password], textarea, select, div.jhtmlarea, .ow_photo_upload_description, .ow_photo_preview_edit .CodeMirror {
    background-color: #fff;
    border: 1px solid #ccd6de;
    color: black;
    font-family: "Lucida Grande", "Verdana";
    font-size: 18px;
    padding: 4px;
}

/*This is for the edit profile button*/
  a.ow_lbutton, html body span.ow_lbutton {
    font-size: 14;
    line-height: 3em;
}

a.ow_lbutton, span.ow_lbutton {
    background: white;
    border: 1px solid transparent;
    padding: 1px 3px;
    line-height: 13px;
    text-transform: uppercase;
    text-shadow: none;
    display: inline-block;
    font-family: 'core_sans_e55_medium', "Trebuchet MS", "Helvetica CY", sans-serif;
    vertical-align: text-bottom;
    color: purple !important;
}
.ow_console_item a {
    color: purple !important;
    text-decoration: none;
}

h1 {
    color: white;
}

/*This gets rid of the header image*/
  .ow_header_pic {
    background-image: none !important;
    height: 0px !important;
}
body,
html {
    /* This colors the color and size of regular text site-wide */
    
    color: #C1BFC1 !important;
    font-size: 20px !important;
    line-height: 20px !important;
    word-wrap: break-word !important;
}
a {
    /* This colors the color of link text site-wide */
    
    color: #C66CC7 !important;
    text-decoration: none;
}
.ow_newsfeed_body,
.ow_dnd_widget.group-FORUM_CMP_LatestTopicsWidget,
.ow_alt2,
.ow_alt1,
.feed_item_sharing_section.ow_box,
.ow_newsfeed_comments,
td,
th {
    /* This colors the background of most container boxes on the newsfeed, blogs, and forums */
    
    background-color: black !important;
}
.ow_tr_delimiter td {
    /* This prevents the background color of the forum boxes from over running their container */
    
    background-color: transparent !important;
}
.ow_tooltip_body,
.ow_tooltip {
    /* This colors the activity notification boxes at the top of the site */
    
    background-color: purple !important;
}
.ow_table_3 {
    width: 100% !important;
    padding-left: 0px;
    text-align: center;
    font-size: 100%;
    box-sizing: content-box;
    word-wrap: break-word !important;
    Background-color: black !important;
	Display: block !important;
}
.ow_table_3 td.ow_label {
    border: none;
    color: silver !important;
    text-align: right;
    width: 100% !important;
    
}
th.ow_section span {
   color: silver;
}
.ow_canvas {
    width: 1200px !important;
}
.ow_super_narrow {
  width: 25%;
}

/*This is for Chat modification*/
.ow_chat_block_wrap,.ow_chat_in_block,.ow_chat_search input,.ow_chat_list ul {
    color: white !important;
     box-sizing: content-box;
    background-color: white !important
}
.ow_count_block span.ow_count_txt {
    color: white !important;
}
.ow_chat_list {
    position: relative !important;
    overflow: hidden;
    background-color: dimgrey !important;
    height: 320px !important;
}
.ow_chat_block .ow_bot_panel {
    position: relative;
    background-color: white !important;
    height: 25px;
}
.ow_chat_dialog.ow_active .ow_author_block a.ow_chat_item_author_wrap {
    background-color: black
}
.ow_chat_dialog_active.ow_chat_dialog .ow_author_block {
    background-color: black
}
body .ow_chat_block_main .ow_chat_search input[type="text"].invitation, body .ow_chat_block_main .ow_chat_search input[type="password"].invitation {
    background-color: dimgrey !important;
    color: white !important
}
.ow_chat_block .ow_top_panel .ow_count_block, .ow_chat_block .ow_bot_panel .ow_count_block {
    padding: 5px 0;
    margin: 0;
    text-align: center;
    font-size: 20px !important;
    color: purple;
    font-weight: normal;
    visibility: visible;
}
.ow_chat_dialog .ow_dialog_items_wrap .clearfix {
    width: 100%;
    background-color: #828894 !important;
}
body .ow_chat_dialog_wrap .ow_dialog_in_item p {
    color: white;
}
span.ow_chat_in_item_author, span.ow_chat_in_item_text {
    color: white
}
.ow_chat_block .ow_dialog_item.even .ow_dialog_in_item, .ow_chat_block .ow_dialog_item.odd .ow_dialog_in_item {
    border-radius: 15px;
    -moz-border-radius: 15px;
    -webkit-border-radius: 15px;
    background: purple;
    border-color: purple;
    font-size: 16px;
}
.ow_chat_block_wrap, .ow_chat_in_block, .ow_chat_search input, .ow_chat_list ul {
    color: white !important;
    box-sizing: content-box;
    background-color: dimgrey !important;
    font-size: 14px !important;
}

.ow_console_item a {
    color: white !important;
    text-decoration: none;
    background: #1C1C1C;
    overflow: hidden;
}
/*this should effect the new notifications highlight color*/
.ow_console_invt_no_img .ow_console_invt_txt {
    width: 100%;
    background: #1C1C1C;
    padding: 5px;
}
.ow_console_list li.ow_console_new_message {
    background: purple;
    border-color: #5cd9bd;
}
.ow_console_messages_btns .ow_console_messages_viewall a, .ow_console_messages_btns .ow_console_messages_send a {
    display: inline-block;
    width: 100%;
    line-height: 38px;
    text-align: center;
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    -wenkit-box-sizing: border-box;
    background: black;
    color: #a2acb3;
    text-transform: uppercase;
}
.ow_console_list li.ow_console_new_message {
    background: black;
    border-color: transparent;
}
body.htmlarea_styles {
    font-family: 'Lucida Grande', Verdana, Tahoma;
    color: white;
    font-size: 18px;
    line-height: 18px;
}
.ow_box_cap h3 {
    padding-top: 0px;
    color: white
}
/*Avatar background color for profile page*/
.ow_avatar_change {
    position: absolute;
    right: 0;
    top: 0;
    left: 0;
    height: 204px;
    background-color: inherit !important;
}
}
