css:
@namespace url(http://www.w3.org/1999/xhtml);

@document url-prefix("https://www.reddit.com/r/xboxone") {
  .side .titlebox table{
    -webkit-box-sizing: border-box!important;
  -moz-box-sizing: border-box !important;
  box-sizing: border-box !important;
    border-style: none !important;
    border-color: transparent !important;
    background-color: transparent !important;
     box-shadow: 1px 1px 5px #999 !important;
    border-spacing: 0px !important;
    background-image: none !important;
  }
  .side .titlebox table th, .side .titlebox table tr{
    border-style: none !important;
    border-color: transparent !important;
    border-bottom-color: transparent !important;
    box-shadow: none !important;
    cursor: default !important;
    border-width: 0px !important;
    border-left-color: transparent !important;
    border-top-color: transparent !important;
    border-right-color: transparent !important;
    border-spacing: 0px !important;
    background-image:  none !important;
  }
  .side .titlebox table th{
    background-color: #107c10 !important;
  }
  .side .titlebox table td{
    border-left-style: none !important;
    border-left-color: transparent !important;
    border-right-style: none !important;
    border-right-color: transparent !important;
    background-color: #f1f1f1 !important;
    border-bottom-color: #3a3a3a !important;
    cursor: default !important;
    box-shadow: none !important;
    border-left-width: 0px !important;
    border-right-width: 0px !important;
    border-top-style: none !important;
    border-top-color: transparent !important;
    border-top-width: 0px !important;
    border-spacing: 0px !important;
    background-image:  none !important;
  }
  .side .md hr{
     background-color: #3a3a3a !important;
     border-style: none !important;
     border-color: transparent !important;
     border-width: 0px !important;
     box-shadow: none !important;
    border-spacing: 0px !important;
     cursor: default !important;
    background-image: none !important;
  }
  .side .titlebox .md h3{
    cursor: default !important;
    border-style: none !important;
    border-color: transparent !important;
    border-spacing: 0px !important;
    border-width: 0px !important;
  }
  .side p a[href$="xbox-live-status"]{
    background-color: transparent !important;
    background-image:  none !important;
  }
  .side .md a{
    color: #5dc21e !important;
  }
  .side .md a[href$="https://twitter.com/RedditXboxOne"], .side .md a[href$="http://www.reddit.com/r/xboxone/w/gameswithgold#wiki_xbox_one"], .side .md a[href$="https://www.reddit.com/r/xboxone/wiki/irc"], .side .md a[href$="http://www.reddit.com/r/xboxone/w/games"], .side .md a[href$="/r/xboxone"], .side .md a[href$="/r/XboxThemes"], .side .md a[href$="https://xbox.uservoice.com/"], .side .md a[href$="https://www.reddit.com/r/xboxone/wiki/ama"]{
    color: #afafaf !important;
  }
  .side .md h4:nth-of-type(1){
    cursor: default !important;
  }
  #header #header-bottom-left .tabmenu{
    background-color:#107c10 !important;
    color: #f1f1f1 !important;
    border-color: transparent !important;
    border-style: none !important;
    border-width: 0px !important;
    border-spacing: 0px !important;
    outline-style: none !important;
  }
  #header .tabmenu li a:hover, #header .tabmenu li a:active, #header .tabmenu li.selected a, #header .tabmenu li a:focus{
    background-color: black !important;
    border-color: transparent !important;
    border-width: 0px !important;
    text-decoration-color: transparent !important;
    border-style: none !important;
    outline-style: none !important;
  }
  #header #header-bottom-left{
    border-bottom-color: transparent !important;
    background-color: black !important;
    color: #f1f1f1 !important;
    border-left-color: transparent !important;
    border-right-color: transparent !important;
    border-top-color: transparent !important;
    outline-style: none !important;
  }
  body{
    font-family: "Segoe UI", Verdana, Arial, Helvetica, "Batch", sans-serif !important;
    outline-style: none !important;
  }
}
