css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

#style-list {padding:0!important; margin:0!important;}

#style-list>li {
border: 2px solid #82a5da !important;
-moz-border-radius:4px;
margin:5px 7%!important;
padding:0 0 0 15px!important;
font-size:12px!important;
text-align:left!important;
vertical-align:middle!important;
background-color: #E8E6D9!important;
/* opacity:0.7;*/
}

#style-list>li:hover{border: 2px solid #fff !important;background-color: #82a5da !important;}
#style-list>li:hover>a{color:#fff!important}
#style-list>li>a{text-decoration:none!important;}


/*To remove numbering of styles - uncomment this*/
/*
#style-list{-moz-column-count: 1 !important;text-align:center;}
#style-list>li {display:inline-block!important;width:30%!important;margin:5px 0!important;}
*/

/*For button-lovers - uncomment this*/
/*
#style-list>li{-moz-appearance: button !important;}
#style-list>li:hover>a{color:#426395!important;}
*/
}

@-moz-document 
url-prefix(http://userstyles.org/users/) 
, url-prefix("https://userstyles.org/users/") {
#style-list>li>a {display:table-cell!important;padding:3px 0!important}
#style-list>li>a:first-child{width:90%!important;}
#style-list>li>a.edit-link{padding:4px 10px!important;}
}
