css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("askubuntu.com") {
  /* ============ Default Reset ============ */
  
  * { background-color: transparent !important; } /* */
  * { border-color: #00ACAF !important; color: #777 !important; box-shadow: none !important; text-shadow: none !important; } /* */
  
  input[type="submit"] { background: #000C17 !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  input[type="submit"]:hover { background-color: #000C87 !important; }
  
  /* Page content */
  body { background: #000C17 none !important; color: #777 !important; }
  a:link, a:visited { color: #00ACCC !important; }
  .button, a.button { background: transparent none !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  .button:hover, a.button:hover { background-color: #000C87 !important; }
  
  /* ======== topbar (mothership?) ======== */
  #custom-header .nav-global { background: #000C47 none !important; }
  #custom-header .nav-global ul li a:link, #custom-header .nav-global ul li a:visited { color: #777 !important; }
  .nav-global-wrapper li ul { background-color: #000C17 !important; }
  .nav-global-wrapper li ul a:hover { background-color: #000C47 !important; }
  #custom-header .nav-global ul li a:link, #custom-header .nav-global ul li a:visited { border-top-color: transparent !important; color: #00ACAF !important; }
  #custom-header .nav-global ul li:hover { background-color: #000C87 !important; }
  
  /* ======== 2nd topbar ======== */
  .topbar .topbar-wrapper .icon-site-switcher-on { background-position: 9px -20px !important; }
  
  .topbar { background-color: #000C27 !important; width: 96% !important; }
  .topbar .topbar-icon-on, .topbar .topbar-icon-on:hover { background-color: #000C47 !important; }
  .topbar .icon-help.topbar-icon-on { color: #00ACAF !important; }
  .topbar-icon:hover { background-color: #000C47 !important; }
  .topbar-menu-links a:hover { background-color: #000C47 !important; }
  /* search box */
  .topbar .topbar-links .search-container input[type="text"] { background: #000C17 url("http://i.imgur.com/DzRqSe5.png") no-repeat left !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  .topbar-dialog { background-color: #000C17 !important; }
  
  /* header */
  #header { background: #000C37 none !important; width: 96% !important; }
  .youarehere { background-color: #000C57 !important; }
  .nav li:hover { background-color: #000C87 !important; } /* */
  
  /* ======== hero box ======== */
  #herobox-mini { background: #000C47 none !important; border: 1px solid #00ACAF !important; }
  #herobox-mini #hero-content { background: transparent none !important; border: 3px solid transparent !important; }
  #tell-me-more { background: transparent none !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  #tell-me-more:hover { background-color: #000C87 !important; }
  
  #herobox #hero-content { background: #000C87 none !important; }
  #herobox #close a { background-color: #000C47 !important; color: #00ACAF !important; }
  
  /* ======== Sidebar ======== */
  .question-hyperlink:hover, #hot-network-questions li:hover { background-color: #000C67 !important; }
  
  
  /* ======== Content ======== */
  #content { background: #000C27 none !important; width: 95% !important; }
  
  #mainbar { width: 80% !important; }
  .question-summary { width: 100% !important; }
  .question-summary .summary { width: 86% !important; }
  .narrow .summary { width: 87% !important; }
  .stats .unanswered strong { height: 22px !important; }
  
  /* headers */
  .subheader { border-bottom-color: #00ACAF !important; }
  .subheader h1 { color: #00ACAF !important; }
  #tabs a:hover, .tabs a:hover { background-color: #000C87 !important; border-bottom-color: #00ACAF !important; }
  #tabs a, .tabs a { padding: 12px !important; }
  /* tags */
  .post-tag { background: #000C47 none !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  .post-tag:hover { background-color: #000C87 !important; border: 1px solid #00ACAF !important; }
  .post-tag::before { background: #00ACAF !important; }
  #tag-menu { background-color: #000C17 !important; }
  /* new activity */
  .new-post-activity, #new-answer-activity { background: #000C47 !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  .new-post-activity:hover, #new-answer-activity:hover { background-color: #000C87 !important; }
  /* Q/A */
  .question-summary { border-bottom-color: #00ACAF !important; } /* separator */
  .question-summary:hover { background-color: #000C47 !important; }
  .question-hyperlink, .answer-hypherlink { color: #00ACAF !important; } /* main q/a titles */
  .unanswered .mini-counts span { color: #777 !important; } /* unanswered talley */
  .answered-accepted .mini-counts { background: #000C87 none !important; } /* answered talley bg */
  /* author link */
  .started a:hover:not(.started-link), .started .mod-flair:hover { color: #00ACCF !important; }
  /* badges */
  .badge-hierarchy:hover { background-color: #000C47 !important; }
  a.badge:hover { background-color: #000C87 !important; }
  /* users */
  .user-info:hover { background-color: #000C47 !important; }
  #tabs-interval a:hover { background-color: #000C87 !important; }
  
  /* ======== Question / Comments / Answers / Respond ======== */
  /* question */
  #questions { width: 100% !important; }
  .question .postcell { width: 100% !important; }
  .question:hover { background-color: #000C47 !important; }
  .post-text { width: 100% !important; }
  code { background-color: transparent !important; }
  pre { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  /* .owner { background-color: #000C17 !important; border: 1px solid #00ACAF !important; } /* taken care of down below with - ".post-signature .user-info" */
  
  /* syntax highlighting */
  .pln { color: #777 !important; } /* plain */

  .str, .tag { color: #C00000 !important; }
  .typ { color: #2B91AF !important; }
  .pun { color: orange !important; }
  .lit { color: cyan !important; }
  .kwd { color: yellow !important; }
  .com { color: darkgreen !important; }
  .atn { color: red !important; }
  .atv { color: #09F !important; }
  /* =================== */
  
  /* comments */
  .comments { width: 100% !important; }
  .comment:hover { background-color: #000C67 !important; }
  .comments-link { border: 1px solid transparent !important; padding: 4px !important; }
  .comments-link:hover { background-color: #000C17 !important; color: #00ACAF !important; padding: 4px !important; border: 1px solid #00ACAF !important; }
  
  /* answers */
  #answers { width: 100% !important; }
  .answer { width: 100% !important; }
  td.answercell { width: 100% !important; }
  .answer:hover { background-color: #000C47 !important; }
  .post-signature .user-info { border: 1px solid #00ACAF !important; background: #000C17 none !important; }
  .post-signature .user-info:hover { background-color: #000C47 !important; }
  
  /* respond */
  .post-editor, .wmd-button-bar, .wmd-input { width: 100% !important; }
  .wmd-button-bar { margin: 0px !important; padding: 0px !important; background-color: #000C17 !important; }
  .wmd-button-row li:hover { background-color: #000C47 !important; }
  /* .grippie { width: 100% !important; background: transparent url("http://askubuntu.com/img/sprites.svg?v=a7723f5f7e59"), none, url("http://askubuntu.com/img/sprites.svg?v=a7723f5f7e59") no-repeat !important; } /* */
  .grippie { width: 100% !important; background: transparent url("http://askubuntu.com/img/sprites.svg?v=a7723f5f7e59") no-repeat center !important; }
  #mdhelp { right: 1px !important; }
  
  #post-form div.answer-help-background { background-color: #000C47 !important; }
  
  /* ======== footer ======== */
  #footer .footerwrap { background: #000C27 none !important; }
}
