css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("help.ubuntu.com"), domain("wiki.ubuntu.com"), domain("manpages.ubuntu.com") {
  body { background: #000C17 none !important; color: #777 !important; width: 95% !important; }
  #container { width: 98% !important; }
  #container-inner { width: 90% !important; }
  #cwt-content { width: 100% !important; }
  
  table { background-color: transparent !important; }
  h3.tableheader, #content th { background: #000C37 !important; }
  div.trails { background-color: #000C47 !important; }
  font[color="black"] { color: #00ACAF !important; }
  font[color="lightgrey"] { color: #777 !important; }
  
  select, input { background-color: #000C17 !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  input[type="submit"] { border: 1px solid transparent !important; background-color: transparent !important; }
  #search-box #titlesearch { margin: -2px 0 0 8px !important; }
  #search-box { background-color: transparent !important; float: right !important; width: 220px !important; margin-left: 0px !important; }
  
  a:link, a:visited { color: #00ACCF !important; }
  p a { border-bottom-color: #00ACCF !important; }
  td[style*="background-color:"] { background-color: transparent !important; }
  hr { background-color: #00ACAF !important; }
  
  #mothership a { color: #777 !important; }
  #main-menu li { border-right: 1px solid #00ACAF !important; }
  #main-menu li:first-child { border-left: 1px solid #00ACAF !important; }
  #header #orangeHeader, #header, #main-menu, #main-menu li, div#subheader div, #subheader, div.subheader-menu li { background: #000C47 none !important; }
  #subheader ul li a:hover, #subheader ul li span.active { background: #000C87 none !important; }
  
  #search-box-input { background: transparent none !important; border: 1px solid #00ACAF !important; }
  
  #wikinav { border: 1px solid #00ACAF !important; box-shadow: none !important; background: #000C47 !important; }
  #wikinav hr { background-color: transparent !important; }
  
  table tr:hover { background-color: #000C87 !important; }
  table > tbody > tr:first-child { background-color: #000C47 !important; }
  #cwt-content td, #cwt-content th { border-color: #00ACAF !important; }
  
  /* h1 { text-decoration: underline !important; } /* */
  h1, h2, h3 { color: #00ACAF !important; }
  h1:hover, h2:hover, h3:hover { background-color: #000C67 !important; }
  
  #container, #container-inner, #cwt-content, #main, #wrapper, .post, #content { background: #000C27 !important; }
  
  #footer { background: #000C27 none !important; }
  
  /* topic content */
  span.disabled { color: #00ACAF !important; font-weight: bold !important;  }
  #second-nav li:first-child { background: transparent none !important; }
  #second-nav { background: transparent none !important; }
  
  strong.highlight { background-color: brown !important; }
  
  #content > div > table[style*="float:right"] { background-color: transparent !important; width: 30% !important;  } /* hopefully this is always a TOC table */
  
  pre { background-color: transparent !important; border: 1px solid #000C87 !important; color: orange !important; }
  pre:hover, p:hover { background-color: #000C87 !important; }
  
  /* help pages */
  a.linkdiv:hover { background-color: #000C47 !important; }
  .note { background-color: #000C47 !important; border: 1px solid #00ACAF !important; }
  a.linkdiv > span.desc, div.links .desc { color: #777 !important; }
  
  .links.nextlinks a { background-color: #000C47 !important; }
  a.nextlinks-next::after { border-left-color: #000C47 !important; }
  a.nextlinks-prev::after { border-right-color: #000C47 !important; }
  
  .links.nextlinks a.nextlinks-next:hover { background-color: #000C87 !important; }
  .links.nextlinks a.nextlinks-next:hover::after { border-left-color: #000C87 !important; }
  
  .links.nextlinks a.nextlinks-prev:hover { background-color: #000C87 !important; }
  .links.nextlinks a.nextlinks-prev:hover::after { border-right-color: #000C87 !important; }
  
  ul.mouseovers a:hover { background-color: #000C87 !important; }
  span.cmd { background-color: #000C47 !important; color: #00ACAF !important; }
  kbd.key-Fn { color: black !important; }
  
  /* #problems.sect { background-color: #000C47 !important; transition: background-color 2s linear 0s !important; } /* */
}

