css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stackoverflow.com") {
  /* ============ Default Reset ============ */
  
  * { background-color: transparent !important; } /* */
  
  * { border-color: #00ACAF !important; color: #777 !important; box-shadow: none !important; text-shadow: none !important; } /* */
  a:link { color: #00ACAF !important; } /* */
  a:visited { color: #00ACCF !important; } /* */
  
  /* ============ Site Settings ============ */
  
  html, body { background-color: #000C17 !important; }
  #mainbar { width: 82% !important; } /* main column content */
  .question-summary, .narrow .summary, .post-text, .wmd-preview, .comments, #answers, #answers-header, .answer { width: 100% !important; }
  #content, #header, .topbar .topbar-wrapper { width: 98% !important; padding: 1% !important; }
  
  /* .question-summary .summary { display: inline !important; } /* */
  
  #hlogo a { background-image: url("http://i.imgur.com/CtY6A9o.png") !important; background-size: contain !important; } /* */
  
  .topbar { height: 0px !important; }
  
  .network-items { position: absolute !important; top: 0px !important; z-index: 2 !important; }
  .modal-content li:hover { background-color: #000C87 !important; }
  /* .current-site .site-link:hover { background-color: #000C47 !important; } /* */
  /* .other-sites li:hover { background-color: #000C47 !important; } /* */
  
  .topbar .topbar-wrapper .icon-site-switcher-on { background-position: 9px -20px !important; }
  .topbar-links { right: 22px !important; z-index: 2 !important; } /* */
  .topbar-icon:hover { background-color: #000C47 !important; }
  .topbar .topbar-icon-on, .topbar .topbar-icon-on:hover { background-color: #000C87 !important; }
  .topbar .icon-help.topbar-icon-on { color: #00ACAF !important; } /* help color when selected */
  .topbar-menu-links a:hover { background-color: #000C87 !important; }
  
  .topbar-dialog { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  
  #herobox-mini #hero-content { background: #000C47 none !important; }
  
  #header { height: 36px !important; }
  
  /* jobs */
  .inputOptionsPanel > div { background-color: #000C17 !important; }
  .btn-outlined:hover, .btn-secondary:hover, button:hover { background-color: #000C67 !important; }
  .jobs .-item:hover { background-color: #000C47 !important; }
  /* badges */
  .badge-hierarchy:hover { background-color: #000C47 !important; }
  a.badge:hover { background-color: #000C87 !important; }
  /* users */
  .user-info:hover { background-color: #000C47 !important; }
  #tabs-interval a:hover { background-color: #000C87 !important; }
  
  .nav li:hover { background-color: #000C87 !important; } /* */
  
  
  /* buttons */
  a.button:hover, .button:hover { background-color: #000C87 !important; }
  
  /* tags */
  .post-tag:hover { background-color: #000CC7 !important; }
  
  /* ============ Side Bar ============ */
  .module:hover { background-color: #000C27 !important; }
  /* .related .spacer:hover, #hot-network-questions li:hover { background-color: #000C47 !important; } /* */
  .module h4, .module h4 a:link { color: red !important; }
  
  /* ============ Pretty Print ============ */
  
  .str, .tag { color: #C00000 !important; }
  .typ { color: #2B91AF !important; }
  .pun { color: orange !important; }
  .lit { color: cyan !important; }
  .kwd { color: yellow !important; }
  .com { color: darkgreen !important; }
  .atn { color: red !important; }
  .atv { color: #09F !important; }
  
  /* ============ Login Page ============ */
  
  div#or { background-color: #000C17 !important; }
 /* img[src="//cdn.sstatic.net/img/se-logo.png"] { padding-left: 150px !important; padding-top: 35px !important; background: transparent url("file://C:/Users/UserData/WebStyles/DarkBlue/StackOverflow/StackExchangeNetwork.png") no-repeat !important; height: 0px !important; width: 0px !important; background-size: contain !important; } /* */
  
  #submit-button { border: 1px solid #00ACAF !important; color: #00ACAF !important; } /* login button */
  #submit-button:hover, .major-provider:hover, .preferred-login:hover { background-color: #000C87 !important; }
  
  /* ============ Browse Questions ============ */
  
  .question-summary:hover { background-color: #000C47 !important; } /* row highlight */
  
  #tabs a:hover { background-color: #000C87 !important; } /* tab highlight hover */
  #tabs .youarehere { background-color: #000C47 !important; }
  
  #tag-menu { background-color: #000C17 !important; } /* tag popup on hover */
  #tag-menu:hover { background-color: #000C47 !important; }
  .tm-links a, .tm-sub-links a { padding: 3px !important; }
  .tm-links a:hover, .tm-sub-links a:hover { padding: 3px !important; background-color: #000C87 !important; }
  
  /* ============ Question / Answer ============ */
  
  #careers3, .newsletter-anon { display: none !important; } /* unwanted elements */
  
  .prettyprint { background-color: #000C27 !important; }
  .comment:hover { background-color: #000C47 !important; }
  .answer:hover, .question:hover { background-color: #001C27 !important; }
  
  .post-editor, .wmd-button-bar, .wmd-input { width: 100% !important; }
  .wmd-button-bar { margin: 0px !important; padding: 0px !important; background-color: #000C17 !important; }
  .wmd-button-row li:hover { background-color: #000C47 !important; }
  .grippie { width: 100% !important; background: transparent url("http://stackoverflow.com/img/sprites.svg?v=a7723f5f7e59") no-repeat !important; } /* */
  
  #post-form div.answer-help-background { background-color: #000C47 !important; }
}
