css:
@-moz-document domain("linux-usb.org") {
body {
    max-width: 55em;
    margin: 0 auto 0 auto;
}

body > p,
body > ul,
body > ol
{
    max-width: 40em;
}

body > ul > li
body > ol > li
{
    max-width: 38em;
}

body > table,
body > p > table
{
    margin-top: 1em;
    width: 90%;
}
}
