css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(zendesk.com) 
{

    body {
        background-color: #474747!important;
    }
    
    #page {
      background:#626262 none repeat scroll 0% !important;
    }

    .tabbed_container .tabs_content, .sammy_tabbed_container .tabs_content {
        background-color: #626262 !important;
    }
    
    .tabbed_container .tab_links a.current, .sammy_tabbed_container .tab_links a.current {
        background-color: #626262 !important;
    }
    
    .section.ticket .notice{
        background: #6da84a !important;
    }
    
#branding_header {
background-color:#000!important
}
.zd-tag-item {
    background-color: #444!important;
    color: #FFF !important;
}
.zendesk-editor--rich-text-comment, .zendesk-editor--plaintext-box .zendesk-editor--plain-text-comment {
    color:#DDD !important;
    }
    
    .filter_tickets tbody tr.selected {
      background-color: #303030!important; }
    
     .bulk_edit_modal .modal-footer,
    .bulk_edit_modal .modal-header,
    .pane.right label,.modal h3,
    .bulk_edit_modal .modal-body .pane.right{
        background-color: #333 !important;
        color: white!important;
    }
    
    .dropdown-menu {
        background-color:#444 !important;
    }
    
    
#main_navigation {
background-color:#3B3B3B!important;
border-right:1px solid #444!important;
border-top:1px solid #444!important
}
   
    .event:not(.is-public) .comment {
        background-color: #A8A8A8 !important;
        border: 1px solid #ADADAD!important;
        
    }
    
    
    .rich_text .comment_input:not(.is-public) div[contenteditable]{
       background-color: #646464 !important; 
        border: 1px solid #ADADAD!important;
    }
    
    .comment_input:not(.is-public) .zendesk-editor--toolbar {
        background-color: #6e6e6e !important;
    }

    .notification.v2{
        color:white!important;
    }
    
    .comment_input .btn {
        color: #DADADA !important;
    }
    
    .zd-selectmenu-base-content{
        color: #BFBFBF !important;
    }
    
    .activities .description strong {
        color: #D8D8D8 !important;
    }
    
    
    
    .filter_tickets .regular:hover td{
        background-color: #7a9c5f !important;
    }
    
.main_panes header, .workspace header {
    background-color: #484848 !important;
    border-bottom: 1px #747474 solid !important;
    }
    
.activities .description {
    margin-left: 35px;
    line-height: 16px;
    color: #95BE22 !important;
    background: #444;
}  
    
.activities .item {
background-color:#333!important;
border:1px solid #333!important
}
.right header h1, section.incidents .right header h1 {
    font-size: 18px;
    color: #FEFEFE!important;
    font-weight: bold;
    padding-top: 27px;
    padding-bottom: 5px;
    margin-right: 160px;
    white-space: nowrap;
}
    .left header h1, section.incidents .left header h1 {
    font-size: 12px;
    color: #FEFEFE!important;
    font-weight: bold;
    border-bottom: 1px solid #ddd;
    padding: 24px 0 5px 0;
    margin: 0 30px;
}
.comment_input textarea {
background-color:#333!important;
color:#ddd!important
}
.form_field > input, .form_field textarea, .form_field .token_list {
    background-image: linear-gradient(#333333, #444444) !important;

}
.token_list {
    border: 1px solid #333333 !important;
    color: #FFF!important;
   }

.form_field label {
    color: #95BE22 !important;

}.main_panes > header {
    background-color: #333333 !important;
    border-bottom: 1px solid #333333 !important;

}
    .btn {
    border: 0px solid #ddd!important;
    border-radius: 4px;
    box-shadow: none;
    color: #555;
    cursor: pointer;
    display: inline-block;
    font-size: 12px;
    line-height: 18px;
    margin-bottom: 0;
    padding: 5px 10px;
    text-align: center;
    text-shadow: none !important;
    vertical-align: middle;
    white-space: nowrap;
}
.main_panes > .pane.left {
    background-color: #333333 !important;
}
.add_comment_attachment_buttons {
    color: #FF7F00 !important;
}
.search .search_bar {
    border-bottom: #333 !important;
    background-color: #333 !important;
    border-top: 2px solid #333 !important;

}
section.filters > .pane.left ul.filters li.selected, section.user_filters > .pane.left ul.filters li.selected {
    background-color: #333333 !important;
    border-bottom: 1px solid #333333 !important;
    color: #fff !important;
}
section.filters > .pane.left ul.filters li.selected a .count, section.user_filters > .pane.left ul.filters li.selected a .count {
    background-color: #008000;

}


section.filters > .pane.left ul.filters li a .count, section.user_filters > .pane.left ul.filters li a .count {
    background-color: #008000 !important;
    box-shadow:none !important;
    color: #FFFFFF !important;
}
.main_panes > header .pane nav.btn-group .btn:not(.active) {
    background-image: linear-gradient(rgb(55, 55, 55), rgb(33, 33, 33)) !important;
}
a {
    color: #FFFFFF !important;

}



.main_panes > .pane.left {
    background-color: #333333 !important;
}
.main_panes > .pane {
    border-top: 2px solid #333333 !important;

}
.object_options .dropdown-toggle {
    cursor: pointer;
    font-size: 11px;
    font-weight: bold;
    padding-bottom: 2px;
    color: #FFF!important;
    opacity: 0.7;
}


#alert {
    background-color: #333333 !important;

}

.main_panes > header {
    background-color: #333333 !important;
    border-bottom: 1px solid #333333 !important;
    }
.ticket .property_box .delim {
    border-bottom: 0px dotted #DDD!important;
    margin-bottom: 4px;
    padding-bottom: 10px;
}
.ticket .apps header {
    height: auto;
    outline: none;
    border-top: none;
    background-color: #3E3E3E!important;
}
    
    .event.is-new {
        background-color: #838383 !important;
        border-left-color: #666 !important;
    }
    
        .tab .popover.bottom .popover-inner .links-section,
    .tab .popover.bottom .popover-inner .recent-tickets-section,
    .tab .popover.bottom .popover-inner .popover-title,
    .tab .popover.bottom .popover-inner{
        background-color: #343434!important;
        color:#66d156 !important;
    }
    
    
    .pane.left ul.filters li:hover:not(.selected),
        .filter_tickets .regular:hover td{
        background-color: #7a9c5f !important;
    }

.filter_tickets tbody tr.grouping td span.grouper, filter_content tbody tr.grouping td span.grouper {
    color: #008000 !important;}
a:hover {
    color: #9fec8c !important;

}
.ticket .apps, .user .apps {
    background-color: #333333 !important;
    border-left: 1px solid #333333 !important;
    }

.zd-tag-menu-root {
    background-image: linear-gradient(#323232, #161616)!important;
    border: 1px solid RGBA(0, 0, 0, 0)!important;
    border-radius: 2px;
    box-shadow: inset 0 0 1px 0 #DDDDDD;
    box-sizing: border-box;
    cursor: pointer;
    margin: 0;
    min-height: 32px;
    outline: none;
}

.zd-tag-menu-root {
    min-height: 36px;
    background-color: #282828!important;
}
    .form_field .zd-menu-list-holder {
    display: block;
    background-image: linear-gradient(#282828, #282828)!important;
}
.details.property_box .value .token_list,.editable.textarea {
border:1px solid #444!important
}

.property_box {
    background: none repeat scroll 0 0 #282828 !important;
    border: 1px solid #333333 !important;
    }
.event {
border-top:1px dotted #333!important
}
.collisionnotification-updated {
    background-color: #444444 !important;
    border-color: #333333 !important;
}
    
.filter_tickets tbody tr.grouping td,filter_content tbody tr.grouping td {
border-bottom:1px solid #444!important;
border-top:1px solid #444!important;
}
.filter_tickets th, .filter_tickets td {
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    height: 40px;
    white-space: nowrap;
    text-align: left;
    padding: 0px 2px 0px 12px;
    vertical-align: middle;
    color: #fff !important;
}   
.filter_tickets tbody tr.grouping,filter_content tbody tr.grouping,.well,.zd-selectmenu-root,.zd-searchmenu-root,.zd-combo-selectmenu-root {
background-color:#333!important
}

.filter_tickets td,filter_content td {
border-bottom:1px dotted #333!important
}

    .table tbody tr:hover td, .table tbody tr:hover th {
  background-color: #DDD!important;; }


.filter_tickets th,filter_content th {
border-bottom:1px solid #333!important
}

.filter_tickets thead tr,.filter_content thead tr {
background-color:#444!important;
background-image:linear-gradient(#333333,#444444)!important;
color:#DDD!important
}

.filter_tickets,filter_content,.main_panes > .pane.left {
background-color:#444!important
}

.footer,footer {
background:linear-gradient(#444444,#333333) repeat scroll 0 0 #111!important;
border-top:1px solid #555!important
}

.form_field > input,.form_field textarea,.form_field .token_list {
background-image:linear-gradient(#333333,#444444)!important
}

.main_panes > .pane {
border-top:2px solid #444!important
}

.main_panes > .pane.right {
background-color:#444!important;
border-left:1px solid #333!important
}

.main_panes > header .pane .btn-group .btn {
background-color:#444!important;
border-color:#333!important;
color:#313131!important;
text-shadow:none!important
}



.main_panes > header h1 {
color:#DDD!important
}

.main_panes > header,.indicators {
background-color:#444!important;
border-bottom:1px solid #333!important
}

.main_panes.dashboard > .pane.right,.indicators li:not(.hidden) + li {
border-left:1px solid #333!important
}

.mast {
background:none repeat scroll 0 0 #282828!important;
border-bottom:1px solid #333!important
}

.section.ticket .conversation_mode a {
background:none repeat scroll 0 0 #333!important;
border-bottom:1px solid #333!important;
border-right:1px solid #333!important
}

.section.ticket,.editable.textarea:hover:not(.disabled) {
background:none repeat scroll 0 0 #282828!important
}

.split_pane.filters > header .pane.left,.split_pane.dashboard > header .pane.left,.split_pane.user_filters > header .pane.left {
border-right:1px solid #333!important
}

.sub_nav a.active {
background:none repeat scroll 0 0 #333!important;
border-bottom:1px solid #333!important;
color:#DDD!important
}

.ticket .apps .box,.user .apps .box,.section.ticket .conversation_mode,.session.ticket {
background:none repeat scroll 0 0 #333!important
}

.ticket .apps,.user .apps {
box-shadow:4px 4px 0 #333 inset!important
}

.token_list {
border:1px solid #222
}

.zd-menu-item {
background-color:#444!important;
color:#DDD!important
}

.zd-menu-root,.zd-searchmenu-base,.indicators ul {
background-color:#444!important;
border:1px solid #333!important
}
 
.zd-selectmenu-base {
background-color:#444!important;
background-image:linear-gradient(#333,#444)!important;
border:1px solid #333!important;
color:#ddd!important
}
.rich_text .comment-actions .btn.active {
    border-bottom-color: #323232;
    color: #fff!important;
    font-weight: 600;
}

body,select,input,textarea {
color:#ddd!important
}

button:focus,select:focus,textarea:focus,input:focus:not(.highlightable),.editable:focus,.editable input[type=text]:focus,.focused,.token_list.ui-state-focus,.ui-selectmenu:focus,.zd-state-focus .zd-selectmenu-base,.zd-state-focus .zd-searchmenu-base {
border-color:#222!important
}

h4.list-heading {
background-color:#333!important;
border-bottom:1px solid #333!important
}

input[type=text],input[type=password],input[type=number] input[type=url],input[type=email],textarea {
border:1px solid #444!important;
box-shadow:0 0 1px 0 #444 inset!important
}
}
