css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('esv.org'), url-prefix('http://www.esv.org/'), url-prefix('https://www.esv.org/') {

html, body {
	background: #fefefe;
	color: #444;
}
#main {
	max-width: 33em;
	margin: 0 auto;
	width: 33em;
}
#reading-day-nav, #todays-readings, .top-link {
	display: none;
}
body, .devotion, .devotion div.esv-text, .devotion div.chunk.esv-text, .passage p {
	font-family: "Hoefler Text", Constantia, "Lucida Bright", Lucidabright, "Lucida Serif", Lucida, "DejaVu Serif", "Bitstream Vera Serif", "Liberation Serif", Georgia, serif;
	line-height: 1.5em;
}
.excerpted-from, .footnote, .footnotes, .noprint {
	border: none;
	font-size: 90%;
	margin: 0;
}
a {
	color: #e22;
	font-size: 80%;
}
li a {
	color: #e22;
	font-size: 100%;
}
h2 {
	font-variant: small-caps;
	font-weight: normal;
	letter-spacing: 0.05em;
}
h2:first-child {
	margin: 3em 0 2em;
	text-align: center;
}
h2:first-child:after, .devotional-readings:before {
	color: #e22;
	content: "†";
	display: block;
	margin-top: 1em;
	text-align: center;
}
h3 {
	font-weight: normal;
	font-style: italic;
}
p {
	margin-bottom: 0;
}
p + p {
	margin-top: 0;
}
p + p, .block-indent + p {
	text-indent: 2em;
}
p.line-group, .block-indent + p.same-paragraph {
	text-indent: 0;
	margin-top: 1em;
	margin-bottom: 0;
}
.block-indent {
	margin-left: 3em;
}
.speaker {
	font-style: italic;
	margin-left: -3em;
}
.footnote a, .footnotes {
	font-style: italic;
	text-decoration: none !important;
}
.footnotes:before {
	color: #e22;
	content: "¶";
	display: block;
	font-size: 120%;
	font-style: normal;
	margin: 1em auto;
	text-align: center;
}
.footnotes h3 {
	font-size: 120%;
}
.excerpted-from {
	margin-top: 3em;
	margin-bottom: 2em;
	text-indent: 0;
}
.excerpted-from:before {
	color: #e22;
	content: "❦";
	display: block;
	font-size: 150%;
	font-style: normal;
	margin: 1em auto;
	text-align: center;
}
.columns {
	position: relative;
}
section.title {
	background: #fafafa;
}
.devotional-controls,
article.bible .column-content .devotional-controls,
article.devotional .column-content .devotional-controls {
	border: 0;
}
section.devotional-content {
	float: left;
	padding-right: .5em;
	min-width: 15.5em;
}
section.devotional-content br {
	display: none;
}
footer article.left {
	margin-top: 5px;
}
footer article.right {
	margin-top: 2px;
}
footer article.right span:last-child {
	display: block;
}
.readers-mode #bible-content h3, .readers-mode #bible-content .chapter-num {
	display: initial;
}
.bible-column header a, .alt-column header a, .alt-sub-header a {
	font-size: 1.25rem;
}
.bible-column h3 {
	font-style: italic;
	font-weight: 200;
	font-family: "Hoefler Text",Constantia,"Lucida Bright",Lucidabright,"Lucida Serif",Lucida,"DejaVu Serif","Bitstream Vera Serif","Liberation Serif","Sentinel A","Sentinel B",Georgia,Times,serif;
}
.bible-column div p {
	font-family: "Hoefler Text",Constantia,"Lucida Bright",Lucidabright,"Lucida Serif",Lucida,"DejaVu Serif","Bitstream Vera Serif","Liberation Serif","Sentinel A","Sentinel B",Georgia,Times,serif;
	max-width: 62rem;
}
.readers-mode #bible-content .chapter-num {
	font-family: "Sentinel A","Sentinel B",Georgia,Times,serif;
	font-weight: lighter;
}

/* Optional */
nav.option,
article.bible .column-content .devotional-controls .track-progress,
article.devotional .column-content .devotional-controls .track-progress,
section.day.right,
article.bible:only-of-type .title .move-column,
article.devotional:only-of-type .title .move-column,
.title a.open-settings,
.user,
footer article.right span,
img[width="1"],
img[height="1"],
.chapter-num,
.verse-num,
.noprint,
.surround {
	display: none;
}
.surrounding-chapters:last-of-type {
	padding: 0;
	visibility: hidden;
}
}
