css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("http(s)?://.*\\.facebook\\.com/(?!plugins/|Fplugins|plugins/like).*"){
  /* Big sprites */
  ._iuz {
    background-image: url(http://i.imgur.com/spqGLaQ.png) !important;
  }
  
  /* Small sprites */
  ._2p7a, ._2p79 {
    background-image: url(http://i.imgur.com/QoWYN70.png) !important;
  }
  
  ._sa_ ._48-k:before {
    background-image: url(http://i.imgur.com/Nsk1xz3.png) !important;
  }
  
  ._4sm1 {
    color: transparent;
  }
  
  ._4sm1:after {
    color: #FFF;
    content: "Smug";
    visibility: visible;
    display: block;
    margin-top: -1.5em;
  }
  
  .UFILikeLink span {
    color: transparent;
  }
  
  .UFILikeLink span:after {
    content: "Smug";
    color: #9197a3;
    margin-left: -25px;
  }
  
}
