css:
@-moz-document domain("wiki.archlinux.org") {
/* Based on: https://userstyles.org/styles/92488/arch-linux-wiki-readability
 * Changed by user ahcox to remove all color settings so it integrates
 * with independent color-based styles.
 * License: http://creativecommons.org/licenses/by/4.0/
 */

* { font-family: "Linux Biolinum O", Georgia, serif; font-size: 1.0rem;}
    h1 span{ margin-top: 40px; font-weight: normal; font-variant: small-caps; font-size: 2em; line-height: 130%; letter-spacing: -0.2pt; word-spacing: -0.2pt; border-bottom: 3px solid #667799;}
    h2 span{ margin-top: 30px;  font-size: 1.9em; line-height: 140%; letter-spacing: -0.3pt; word-spacing: -0.2pt; font-weight: normal; }
    h3 span{ margin-top: 20px; font-weight: normal; font-size: 1.6em; letter-spacing: -0.2pt; word-spacing: -0.1pt;}
    h4 span{ margin-top: 10px;  font-size: 1.4em; font-weight: normal; letter-spacing: -0.2pt; word-spacing: -0.1pt;}
    h5 span{ margin-top: 0px;  font-weight: normal; font-size: 1.2em;}
    b { font-weight:500;}
    div#content { width: 960px; margin: 3.75em 0px 0px 13.2em;}
    div#column-content { margin: -1px 0px 0.6em -17.2em;}
    ul { text-align: left; padding: 3px;}
    #p-search, .pBody { padding: 0.5em 0.4em 0.4em; font-size: 12px; width: 190px;}
    #p-cactions { margin: 0px 0px 0px 27px;  }
    pre {font-size: 85%; width: 930px;}
}
