css:
@-moz-document url-prefix("http://www.ohmydollz.com/forum/") {
  #contenu {
        background: url("http://www.zupimages.net/up/16/09/aybf.png") no-repeat fixed 50% 0 rgba(0, 0, 0, 0);
        height: 100%;
        margin: 0;
        padding: 3px 0;
        width: 100%;
    }
    #head_forum {
        background-image: url("http://www.zupimages.net/up/16/09/om6c.png");
        background-repeat: no-repeat;
        height: 300px;
        position: relative;
        width: 950px;
    }

#head_links {
    bottom: 118px;
    left: 577px;
    position: absolute;
}

#head_links a {
    color: #CEB7A5;
    font-size: 16px;
    text-decoration: none;
}

#head_links a:hover {
    color: #82715F;
}

    #brdmenu {
        background-image: url("http://www.zupimages.net/up/16/09/gjwl.png");
        background-repeat: no-repeat;
        height: 30px;
    }
    #footer_box {
        background-image: url("http://www.zupimages.net/up/16/09/xg10.png");
        background-repeat: no-repeat;
        height: 100px;
        width: 950px;
    }
    #brdwelcome {
        background-image: url("http://www.zupimages.net/up/16/09/5pa1.png");
        background-repeat: no-repeat;
        color: #A79284;
        font-weight: bold;
        height: 60px;
        padding: 10px;
        width: 950px;
    }
    .pun H2, #brdmenu {
        background-image: url("http://www.zupimages.net/up/16/09/gjwl.png");
        background-repeat: no-repeat;
    }
    .pun {
        color: #837062;
    }
    DIV.box, .pun BLOCKQUOTE, DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven {
        background-color: #ebe0d5;
    }

.pun BLOCKQUOTE {
    border: 1px solid #A99688;
}

.box {
    border: 1px solid #BFAA9B;
}
    .pun A:link, .pun A:visited {
        color: #f59aa1;
    }
    .pun A:hover {
        color: #DCDDD2;
    }
    DIV.block DIV.inbox, DIV.blockmenu DIV.inbox {
        padding: 0 6px;
        color: #9a826e;
    }
    .pun TH {
        background-image: url("http://www.zupimages.net/up/16/09/e0nj.png");
        background-repeat: no-repeat;
        color: #E1CEC7;
        font-size: 13px;
        height: 100%;
        text-align: center;
    }
.inbox tr {
    border: 1px solid #F6F2EE;
}

    pun .tcr .byuser {
        color: #837062;
    }
    TD.tc2, TD.tc3, TD.tcmod, #postpreview, #viewprofile DD, DIV.forminfo, #adminconsole FIELDSET TD, DIV.blockmenu DIV.box, #adstats DD {
        background-color: #EBE0D5;
    }
    TD DIV.tclcon {
        color: #716258;
        float: left;
        margin-left: 10px;
        width: 475px;
    }
    .pun .tcr .byuser {
        color: #837062;
    }
.pun .tcr {
    border-right: 1px solid #BFAA9B;
}
    .pun {
        color: #837062;
    }
    DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {
        background-color: #E9E0D4;
    }
    DIV.postright, DIV.postfootright {
        background-color: #E4D4C9;
    }
    DIV.blockpost DIV.box {
        background-color: #E4D4C9;
    }
    DIV.postright, DIV.postfootright {
        border-color: #E4D4C9;
        border-left: 18em solid #E9E0D4;
    }
DIV.postleft DT, DIV.postmsg H4, TD.tcl H3, DIV.forminfo H3, P.postlink, DIV.linkst LI, DIV.linksb LI, DIV.postlinksb LI, .blockmenu LI, #brdtitle H1, .pun SPAN.warntext, .pun P.warntext {
    font-weight: bold;
    color: #867569;
}
    .pun A:link, .pun A:visited {
        color: #A79284;
    }
    DIV.icon {
        background-image: url("http://www.zupimages.net/up/16/09/woqv.png") !important;
    }
    DIV.inew {
        background-image: url("http://www.zupimages.net/up/16/09/bvr0.png") !important;
    }

DIV.blockpost H2 SPAN.conr {
    color: #52453C;
    text-align: right;
}

.pun H2 A:link, .pun H2 A:visited {
    color: #6B5C51;
}

DIV.linkst .conl, DIV.linksb .conl, DIV.postlinksb .conl {
    width: 18em;
    color: #8E6D6A;
}
  
}
